 C I=I,O=O
/*

CLEAR.text:
//D061001X JOB (00000000,06009,10),CLEAR,MSGCLASS=Q,NOTIFY=D061001
//*
//*  REFER: D061001.OUTLIST(CLEAR)
//*  DOC  : THE "CLEAR" COMMAND IS USED BY THE OUTLIST APPL'S CLIST.
//*
//CLEAR    EXEC PGM=IFOX00,PARM='OBJ,TERM'
//SYSLIB   DD DSN=SYS1.MACLIB,DISP=SHR
//SYSUT1   DD DSN=&&SYSUT1,UNIT=SYSDA,SPACE=(1700,(600,100))
//SYSUT2   DD DSN=&&SYSUT2,UNIT=SYSDA,SPACE=(1700,(300,50))
//SYSUT3   DD DSN=&&SYSUT3,UNIT=SYSDA,SPACE=(1700,(300,50))
//SYSTERM  DD SYSOUT=*
//SYSPUNCH DD DUMMY
//SYSGO    DD DSN=&&OBJSET,UNIT=SYSDA,SPACE=(80,(200,50)),
//             DISP=(MOD,PASS)
//SYSIN    DD *
*
* THIS CODE CLEARS A 3270-TYPE SCREEN
*
CLEAR    START 0
         USING *,12
         SAVE  (14,12)
         LR    12,15
         LR    15,13
         LA    13,SAVEAREA
         ST    13,8(15)
         ST    15,4(13)
         TPUT  ERASE,14,FULLSCR
         L     13,4(13)
         RETURN (14,12),RC=0
SAVEAREA DC   18F'0'
ERASE    DC    X'C1115D7E1140403C4040001DC813'
         END   CLEAR
/*
