//        MSGLEVEL=(1,1),MSGCLASS=T TYPRUN=HOLD
//*
//******************************************************************//
//*   PDSLOAD  EXECUTION  -  LOAD PDS FROM IEBUPDTE SYSIN CARD D.S.*//
//*                                        DATE ====> 01/14/86     *//
//******************************************************************//
//*
//S01UPDTE EXEC PGM=PDSLOAD,PARM='UPDTE(./)'
//STEPLIB   DD  DISP=SHR,DSN=TST.TSO.CMDLIB
//SYSPRINT  DD  SYSOUT=*
//SYSUT1    DD  DSN=SYMFILE,DISP=SHR,UNIT=TAPE,
//          VOL=(,RETAIN,SER=SYM011),LABEL=(05,SL,EXPDT=98000),
//          DCB=(RECFM=FB,BLKSIZE=12000,LRECL=80)
//SYSUT2    DD  DSN=TST2SSG.SYM011.F05,DISP=(NEW,CATLG,DELETE),
//          UNIT=3380,VOL=SER=WORK03,
//          SPACE=(TRK,(30,30,91),RLSE),
//          DCB=(RECFM=FB,LRECL=80,BLKSIZE=3120,DSORG=PO)

TAPEL$.text:
//* ********************************************************
//* *  INSTALL THE 'TAPEL'    PROGRAM                      *
//* ********************************************************
//TAPEL    EXEC ASMHCL,
//         PARM.ASM='OBJECT,NODECK,TERM,ALIGN,LIST,BATCH',
//         PARM.LKED='LIST,MAP'
//SYSPRINT DD DUMMY
//SYSLIB   DD DSN=SYS1.MACLIB,DISP=SHR
//         DD DSN=SSJDM.MVS.SHARE.SOURCE,DISP=SHR
//SYSIN    DD DSN=SSJDM.MVS.SHARE.SOURCE(TAPEL),DISP=SHR
//         DD DSN=SSJDM.MVS.SHARE.SOURCE(MSGWRITE),DISP=SHR
//         DD DSN=SSJDM.MVS.SHARE.SOURCE(TLPRINT),DISP=SHR
//SYSTERM  DD SYSOUT=*
//LKED.SYSPRINT DD SYSOUT=*
//LKED.SYSLMOD DD DSN=SYS1.SADSC.CMDLIB,UNIT=,DISP=SHR,SPACE=
//LKED.SYSIN DD *
 SETCODE AC(1)
 NAME TAPEL(R)
/*

TAPEMAPS.text:
//TST2FP2M JOB ,'TECH.SUPP-SAM.GOLOB',CLASS=M,NOTIFY=TST2SSG,
//        MSGLEVEL=(1,1),MSGCLASS=T TYPRUN=HOLD
//*
//***********************************************************
//*   THIS JCL EXECUTES DAVE COLE'S TAPEMAP PROGRAM.        *
//*      CBT TAPE - FILE 299.   OUTPUT IS COPIED TO TWO     *
//*      MEMBERS OF THE TAPEMAP PDS, WHERE IT IS KEPT       *
//*      FOR FUTURE REFERENCE, TO FIND SOURCE MODULES,      *
//*      AND TO CALCULATED FOOTAGES ON THE TAPES, ETC.      *
//*                                                         *
//*   SINCE WE HAVE A PROC, MULTIPLE REELS MAY BE READ IN.  *
//*   THE DSCOPY PROGRAM MAY BE FOUND WITH MY SMP PACKAGE   *
//*      ON FILE 428 OF THE CBT TAPE, IF YOU CAN'T FIND     *
//*      IT IN ITS PROPER PLACE ON THE TAPE.  YOU CAN       *
//*      SUBSTITUTE AN IEBGENER STEP INSTEAD IF YOU         *
//*      WANT TO.  IT'S JUST A SEQUENTIAL COPY.             *
//***********************************************************
//TAPEMAP PROC VOL=XXXXXX,M='M',DRIVE=481
//TAKEMAP EXEC PGM=TAPEMAP
//STEPLIB  DD  DSN=TST.TSO.CMDLIB,DISP=SHR
//SYSPRINT DD  DSN=&&TMP,DISP=(NEW,PASS,DELETE),UNIT=SYSDA,
//         SPACE=(CYL,(3,3)),DCB=(RECFM=FBA,LRECL=133,BLKSIZE=3059)
//SYSPRNT2 DD  DSN=&&TMP1,DISP=(NEW,PASS,DELETE),UNIT=SYSDA,
//         SPACE=(CYL,(3,3)),DCB=(RECFM=FBA,LRECL=133,BLKSIZE=3059)
//SYSUT1   DD  UNIT=&DRIVE,VOL=(PRIVATE,,,,SER=(&VOL)),
//             DISP=SHR,LABEL=(,BLP,EXPDT=98000)
//COPYMAP EXEC PGM=DSCOPY
//SYSPRINT DD  SYSOUT=*
//IN1      DD  DISP=(OLD,DELETE),DSN=&&TMP
//IN2      DD  DISP=(OLD,DELETE),DSN=&&TMP1
//OUT1     DD  DISP=SHR,DSN=TST.SYMTAPE.MAPS(&VOL)
//OUT2     DD  DISP=SHR,DSN=TST.SYMTAPE.MAPS(&VOL&M)
//  PEND
//MAP001  EXEC TAPEMAP,VOL=SYM022

TLPRINT$.text:
//*------------------------------------------------------------------*
//*        INSTALLATION FOR 'TLPRINT'  SUBROUTINE                    *
//*------------------------------------------------------------------*
//ASMHCL   EXEC ASMHCL,PARM.ASM='BATCH',SYSOUT=H
//SYSIN    DD DSN=ASMI99U.MI110.SUBSRC(TLPRINT),DISP=SHR
//LKED.SYSLMOD DD DSN=SYS2.LINKLIB,DISP=SHR
//SYSIN    DD *
 SETCODE AC(1)
 NAME TLPRINT(R)
/*
