    ENDZONEEDIT.
    UCLIN .
      REP TZONE(C&TRGTVOL)
        RELATED(CSMPDL1) .
    ENDUCL  .
  SET BDY(SMPDL1) .
    UCLIN .
      REP DZONE(SMPDL1)
        RELATED(&TRGTVOL) .
    ENDUCL  .
  SET BDY(CSMPDL1) .
    UCLIN .
      REP DZONE(CSMPDL1)
        RELATED(C&TRGTVOL) .
