  SET BDY(&TRGTVOL) .
    ZONECOPY (&SRCVOL) INTO(&TRGTVOL) .
    ZONEEDIT DDDEF.
      CHANGE VOLUME(&SRCVOL,&TRGTVOL).
    ENDZONEEDIT.
    UCLIN .
      REP TZONE(&TRGTVOL)
