  SET BDY(C&TRGTVOL) .
    ZONECOPY (C&SRCVOL) INTO(C&TRGTVOL) .
    ZONEEDIT DDDEF.
      CHANGE VOLUME(&SRCVOL,&TRGTVOL).
    ENDZONEEDIT.
    UCLIN .
      REP TZONE(C&TRGTVOL)
        RELATED(CZDLB01) .
    ENDUCL  .
  SET BDY(ZDLB01) .
    UCLIN .
      REP DZONE(ZDLB01)
        RELATED(&TRGTVOL) .
    ENDUCL  .
  SET BDY(CZDLB01) .
    UCLIN .
      REP DZONE(CZDLB01)
        RELATED(C&TRGTVOL) .
    ENDUCL  .
