//LLAENQ   EXEC LLAENQ,LLA=WI,LIB='W$#.LINKLIB'         ** THE "LIB"**
//*
//MONAENQ3 EXEC  PGM=IEBGENER,COND=(2,NE,ONLPAR)
//SYSPRINT DD    SYSOUT=*
