./ ADD NAME=ALLOCDB  0102-20297-20338-1548-00057-00057-00000-Z531415 00
//Z531415D JOB 01,RPF,CLASS=A,MSGCLASS=X,REGION=0K,NOTIFY=Z531415
//*********************************************************************
//*
//*                       Install RPF
//*                       ==============
//*
//* DESC: Install RPF Profile cluster, this also is a part of $INSTRPF
//*
//*********************************************************************
//CLEANUP EXEC PGM=IDCAMS
//REPROIN  DD  *
99999999    SEED RECORD FOR THE RPF Profile cluster
//SYSPRINT DD  SYSOUT=*
//SYSIN  DD *
 PARM GRAPHICS(CHAIN(SN))
 /***********************************************************/
 /*                                                         */
 /* Note:  you do not need this define alias if you do not  */
 /*        want to use a private user catalog               */
 /*                                                         */
 /* Note:  you need to update the relate parameter to       */
 /*        point to the catalog that you want to use        */
 /*                                                         */
 /***********************************************************/
   DELETE RPF.V1R8M6.SRPFPROF CLUSTER
   SET LASTCC = 0
   SET MAXCC  = 0
 /***********************************************************/
 /*                                                         */
 /* Note:  You will have to modify the volume names         */
 /*        and the dataset high level qualifiers            */
 /*        to reflect your system environment               */
 /*                                                         */
 /* Note:  Do __not__ modify the low level qualifier, as    */
 /*        they will be needed as is if and when RPF        */
 /*        will be distributed in smp format                */
 /*                                                         */
 /***********************************************************/
  DEFINE CLUSTER ( NAME(RPF.V1R8M6.SRPFPROF) -
                   VOL(PUB000) -
                   FREESPACE(20 10) -
                   RECORDSIZE(1750 1750) -
                   INDEXED -
                   IMBED -
                   UNIQUE  -
                   KEYS(8 0) -
                   CYLINDERS(1 1) -
                 ) -
            DATA ( NAME(RPF.V1R8M6.SRPFPROF.DATA) -
                   SHR(3 3) -
                 ) -
           INDEX ( NAME(RPF.V1R8M6.SRPFPROF.INDEX) -
                   SHR(3 3) -
                 )
  IF LASTCC = 0 THEN -
     REPRO INFILE(REPROIN) -
           OUTDATASET(RPF.V1R8M6.SRPFPROF)
