RACFCLSB.text:
)ATTR DEFAULT(%+_)
   $  TYPE(OUTPUT)  INTENS(LOW)  JUST(LEFT) COLOR(TURQ)
   _  TYPE(INPUT)   INTENS(HIGH) JUST(LEFT) CAPS(ON)     HILITE(USCORE)
   {  TYPE(TEXT)    INTENS(LOW)  SKIP(ON)   COLOR(BLUE)
   +  TYPE(TEXT)    INTENS(LOW)  SKIP(ON)   COLOR(GREEN)
)BODY EXPAND(\\)
+\-\(%RACFADM - General Resources+)\-\
%Command ===>_ZCMD                                              +Scroll==>_SAMT+
%
+PROFILE:_PROFILE                                         +$TYPE
+CLASS:  _RCLASS    +
+UACC:   $UACC    { (Default access)     +OWNER:  $OWNER   { (Owner of profile)
+WARNING:$WARN    { (YES/NO)             +AUDIT:  $AUDIT
+DATA:   $DATA
+                                           +
+SELECT: %A+Add,%R+Remove
+
%S    MEMBER
%-------------------------------------------------------------------------------
)MODEL
_Z+  $Z           +
)INIT
  .ZVARS   = '(OPTB ID)'
  .CURSOR  = OPTB
  .HELP    = #RACMENU
  &ZCMD    = ' '
  IF (&SAMT = ' ')
      &SAMT = &ZSCED
)REINIT
  .CURSOR = OPTB
)PROC
  &SROW = &Z
  IF (&ZCMD = &Z)
     IF (&ZTDSELS = 0)
        &SROW = .CSRROW
        IF (&SROW ¬= 0)
           &OPTB = A
  VER (&OPTB,LIST,A,R)
)END
/*--------------------------------------------------------------------*/
/*                             Comments                               */
/*--------------------------------------------------------------------*/
/*                                                                    */
/*  1) In the )PROC section there is code (&SROW) to determine if     */
/*     the user tabbed down to a row and pressed ENTER to select      */
/*     an entry                                                       */
/*                                                                    */
/*--------------------------------------------------------------------*/

RACFCLSD.text:
)ATTR DEFAULT(%+_)
   _  TYPE(INPUT)   INTENS(HIGH) JUST(LEFT) CAPS(ON)     HILITE(USCORE)
   $  TYPE(OUTPUT)  INTENS(LOW)  JUST(LEFT) COLOR(TURQ)
   #  TYPE(OUTPUT)  INTENS(LOW)  JUST(LEFT) COLOR(GREEN)
   +  TYPE(TEXT)    INTENS(LOW)  SKIP(ON)   COLOR(GREEN)
   ~  TYPE(PS)
)BODY EXPAND(\\)
+\-\(%RACFADM - General Resources+)\-\
%Command ===>_ZCMD                                              +Scroll==>_SAMT+
%
+USER:   $USER    +
+SELECT: %S+Show,%L+List
%
%S  ~Class
%-------------------------------------------------------------------------------
)MODEL CLEAR(OPTA)
_Z+ $Z                                                                         +
)INIT
  .ZVARS   = '(OPTA CLASS)'
  .HELP    = #RACCLS8
  IF (&SAMT = ' ')
      &SAMT = &ZSCED
  &ZCMD = ' '
)PROC
  &SROW = &Z
  IF (&ZCMD = &Z)
     IF (&ZTDSELS = 0)
        &SROW = .CSRROW
        IF (&SROW ¬= 0)
           &OPTA = S
  VER (&OPTA,LIST,S,L)
)PNTS
  FIELD(ZPS00001) VAR(ZCMD) VAL('SORT CLASS')
)END
/*--------------------------------------------------------------------*/
/*                             Comments                               */
/*--------------------------------------------------------------------*/
/*                                                                    */
/*  1) In the )PROC section there is code (&SROW) to determine if     */
/*     the user tabbed down to a row and pressed ENTER to select      */
