RACFCLSF.text:
)ATTR DEFAULT(%+_)
   $  TYPE(OUTPUT)  INTENS(LOW)  JUST(LEFT) COLOR(TURQ)
   #  TYPE(OUTPUT)  INTENS(LOW)  JUST(LEFT) COLOR(GREEN)
   @  TYPE(OUTPUT)  INTENS(HIGH) SKIP(ON)
   _  TYPE(INPUT)   INTENS(HIGH) JUST(LEFT) CAPS(ON)     HILITE(USCORE)
   {  TYPE(TEXT)    INTENS(LOW)  SKIP(ON)   COLOR(BLUE)
   +  TYPE(TEXT)    INTENS(LOW)  SKIP(ON)   COLOR(GREEN)
)BODY EXPAND(\\)
+\-\(%RACFADM - Authorization+)\-\
%Command ===>_ZCMD                                              +Scroll==>_SAMT+
%
+PROFILE:$PROFILE
+CLASS:  $RCLASS   +
+UACC:   $UACC    { (Default access)     +OWNER: $OWNER   { (Owner of profile)
+WARNING:$WARN    { (YES/NO)             +AUDIT: $AUDIT
+DATA:   $DATA                                                               @Z
+                                           +
%Group/ID     Access
%-------------------------------------------------------------------------------
)MODEL
$Z           #Z
)INIT
  .ZVARS = '(SCIND ID ACC)'
  .HELP  = #RACMENU
  &ZCMD  = ' '
  IF (&SAMT = ' ')
      &SAMT = &ZSCED
)PROC
)FIELD
  FIELD(DATA) IND(SCIND,'<>') LEN(255)
)END
/*--------------------------------------------------------------------*/
/*                             Comments                               */
/*--------------------------------------------------------------------*/
/*                                                                    */
/*  1) The 'DATA' field is a scrollable field                         */
/*                                                                    */
/*--------------------------------------------------------------------*/

RACFCLSR.text:
)ATTR DEFAULT(%+_)
   _  TYPE(INPUT)   INTENS(HIGH) JUST(LEFT) CAPS(ON)     HILITE(USCORE)
   $  TYPE(OUTPUT)  INTENS(LOW)  JUST(LEFT) COLOR(TURQ)
   #  TYPE(OUTPUT)  INTENS(LOW)  JUST(LEFT) COLOR(GREEN)
   +  TYPE(TEXT)    INTENS(LOW)  SKIP(ON)   COLOR(GREEN)
   ^  AREA(DYNAMIC) EXTEND(OFF)
   ]  TYPE(DATAOUT) INTENS(LOW)  COLOR(GREEN)
   [  TYPE(DATAOUT) INTENS(HIGH)
   ~  TYPE(PS)
)BODY EXPAND(\\)
+\-\(%RACFADM - General Resources+)\-\
%Command ===>_ZCMD                                              +Scroll==>_SAMT+
%
+SELECT: ^SELCMDS                                                              ^
%
%S  ~Class
%-------------------------------------------------------------------------------
)MODEL CLEAR(OPTA)
_Z+ $Z        #Z                +
)INIT
  .ZVARS   = '(OPTA CLASS ACTION)'
  .HELP    = #RACCLSR
  &ZCMD    = ' '
  IF (&SAMT = ' ')
      &SAMT = &ZSCED
)PROC
  &SROW = &Z
  IF (&ZCMD = &Z)
     IF (&ZTDSELS = 0)
        &SROW = .CSRROW
        IF (&SROW ¬= 0)
           &OPTA = S
  IF (&SETMADMN = YES)
     VER (&OPTA,LIST,S,L,R)
  ELSE
     VER (&OPTA,LIST,S,L)
)PNTS
  FIELD(ZPS00001) VAR(ZCMD) VAL('SORT CLASS')
)END
