@$AVRS.text:
/* rexx */
/*trace i*/
/*********************************************************************/
/* This exec invokes SEA's $AVRS dialog.  ZTRAIL is used to hold the */
/* $AVRS database identifier, so be sure to pass it when you invoke  */
/* this exec.                                                        */
/*********************************************************************/
parse arg ztrail
address ispexec "LIBDEF ISPLLIB DATASET ID('$avrsprf.loadlib') STACK"
address ispexec "LIBDEF ISPMLIB DATASET ID('$avrsprf.ispmlib') STACK"
address ispexec "LIBDEF ISPPLIB DATASET ID('$avrsprf.ispplib') STACK"
address ispexec "LIBDEF ISPTLIB DATASET ID('$avrsprf.isptlib') STACK"
address ispexec "SELECT PGM(ISP$AVRS) PARM($."ztrail") NEWAPPL($AVR)",
                "PASSLIB SCRNAME($AVRS)"
address ispexec "LIBDEF ISPLLIB"
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPPLIB"
address ispexec "LIBDEF ISPTLIB"

@ACF2.text:
/* rexx */
/*trace i*/
/*********************************************************************/
/* This exec invokes CA's ACF2 dialog.                               */
/*********************************************************************/
parse arg ztrail
address tso "ALTLIB ACT APPL(CLIST) DA('CAI.CAICLIB')"
address ispexec "LIBDEF ISPLLIB DATASET ID('CAI.CAIISPL') STACK"
address ispexec "LIBDEF ISPMLIB DATASET ID('CAI.CAIISPM') STACK"
address ispexec "LIBDEF ISPPLIB DATASET ID('CAI.CAIISPP') STACK"
address ispexec "LIBDEF ISPSLIB DATASET ID('CAI.CAIISPS') STACK"
address ispexec "LIBDEF ISPTLIB DATASET ID('CAI.CAIISPT') STACK"
/*********************************************************************/
/* The following code is wrapped around the SELECT PANEL to prevent  */
/* dialog termination if ztrail contains an invalid option.  Instead */
/* of the ISPF dialog termination panel, the "Invalid option" error  */
/* message will be displayed.  This is only necessary for            */
/* non-primary option menus (menus defined with &ZPRIM=NO in the     */
/* )INIT section of the panel) using the IBM default ZSEL syntax.    */
/*********************************************************************/
address ispexec "CONTROL ERRORS RETURN"
do forever
   address ispexec "SELECT PANEL(ACFOPTS) OPT("ztrail") SCRNAME(ACF2)"
   if rc <= 4 then
      leave
   else
      do
         address ispexec "SETMSG MSG("zerrmsg")"
         if zerrmsg <> 'ISPD221' then
            leave
      end
   ztrail = ''
end
address ispexec "LIBDEF ISPLLIB"
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPPLIB"
address ispexec "LIBDEF ISPSLIB"
address ispexec "LIBDEF ISPTLIB"
address tso "ALTLIB DEACT APPL(CLIST)"

@AMDSADD.text:
/* rexx */
/*********************************************************************/
/* This exec invokes IBM's StandAlone Dump Dataset init dialog.      */
/*********************************************************************/
address tso "ALTLIB ACT APPL(CLIST) DA('SYS1.SAMPLIB')"
address ispexec "SELECT CMD(%AMDSADDD) SCRNAME(AMDSADDD)"
address tso "ALTLIB DEACT APPL(CLIST)"

@ASM2.text:
/* rexx */
/* trace i */
/*********************************************************************/
/* This exec invokes CA's ASM2 dialog.  It is provided courtesy of   */
/* Eddie Collins at Brookshire Grocery Co.                           */
/*********************************************************************/
address ispexec "LIBDEF ISPMLIB DATASET ID('asm2pref.CAIISPM') STACK"
address ispexec "LIBDEF ISPPLIB DATASET ID('asm2pref.CAIISPP') STACK"
address tso "CALL 'asm2pref.CAILIB(ASM2SPF)'"
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPPLIB"

@ASU.text:
/* rexx */
/* trace i */
/*********************************************************************/
/* This exec invokes IBM's DCE Application Support dialog.  Since it */
/* requires a KEYLIST, save the PFSHOW value, turn PFSHOW ON, then   */
/* restore the PFSHOW value before leaving.                          */
/*********************************************************************/
parse arg ztrail
address ispexec "VGET (ZPFSHOW)"
pfshowsv = zpfshow
address ispexec "SELECT PGM(ISPOPF) PARM(PFK,ON)"
address tso "ALTLIB ACT APPL(CLIST) DA('ASU.SASUEXEC')"
address ispexec "LIBDEF ISPMLIB DATASET ID('ASU.SASUMENU') STACK"
address ispexec "LIBDEF ISPPLIB DATASET ID('ASU.SASUPENU') STACK"
address ispexec "LIBDEF ISPTLIB DATASET ID('ASU.SASUTLIB') STACK"
address ispexec "SELECT PGM(ASUV) NEWAPPL(ASUV) PASSLIB NOCHECK",
                "SCRNAME(DCEASUPP)"
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPPLIB"
address ispexec "LIBDEF ISPTLIB"
address tso "ALTLIB DEACT APPL(CLIST)"
address ispexec "SELECT PGM(ISPOPF) PARM(PFK,"pfshowsv")"

@BDT.text:
/* rexx */
/* trace i */
/*********************************************************************/
/* This exec invokes IBM's BDT dialog.  This exec uses the profile   */
/* dataset for saved tables, but you may use any dataset you wish.   */
/*********************************************************************/
parse arg ztrail
address tso "ALTLIB ACT APPL(CLIST) DA('SYS1.SBDTCLI0')"
address ispexec "LIBDEF ISPMLIB DATASET ID('SYS1.SBDTMSG') STACK"
address ispexec "LIBDEF ISPPLIB DATASET ID('SYS1.SBDTPNL0') STACK"
address ispexec "LIBDEF ISPTABL DATASET ID('"userid()".ISPF.ISPPROF')",
                "STACK"
address ispexec "LIBDEF ISPTLIB DATASET ID('"userid()".ISPF.ISPPROF')",
                "STACK"
address ispexec "SELECT CMD(%BDTC01) SCRNAME(BDT)"
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPPLIB"
address ispexec "LIBDEF ISPTABL"
address ispexec "LIBDEF ISPTLIB"
address tso "ALTLIB DEACT APPL(CLIST)"

