@IPCSTC2.text:
/* rexx */
/*trace i*/
/*********************************************************************/
/* This exec invokes IBM's IPCS for TCP/IP.  It is provided courtesy */
/* of Phil Knight at AIG.  Turn on PFSHOW and keylist support since  */
/* the TCP/IP exec requires it (comment out the keylist support if   */
/* PQ74283 is applied).  This exec should be invoked from IPCS       */
/* option 6 after invoking IPCS with the @IPCSTC1 exec.  Code is     */
/* added to check the z/OS release, since TCP/IP repackaged into     */
/* SYS1.SBLS* datasets for z/OS V1R4 and higher.                     */
/*********************************************************************/
parse arg ztrail
address ispexec "VGET (ZPFSHOW)"
pfshowsv = zpfshow
address ispexec "SELECT PGM(ISPOPF) PARM(PFK,ON)"
/*zkluse = 'Y'    Uncomment these lines if PQ74283 is not applied
address ispexec "VPUT (ZKLUSE) PROFILE"*/
if mvsvar('sysmvs') >= 'SP7.0.4' then
   address tso "ALTLIB ACT APPL(CLIST) DA('SYS1.SBLSCLI0')"
else
   address tso "ALTLIB ACT APPL(CLIST) DA('TCPIP.SEZAINST',",
                                         "'SYS1.SBLSCLI0')"
address ispexec "SELECT CMD(%EZBTCPEX)"
address tso "ALTLIB DEACT APPL(CLIST)"
address ispexec "SELECT PGM(ISPOPF) PARM(PFK,"pfshowsv")"

@ISM.text:
/* rexx */
/* trace i */
/*********************************************************************/
/* This exec invokes CA's ISM dialog.  It is provided courtesy of    */
/* Werner Kuehnel at IMD GmbH.                                       */
/*********************************************************************/
parse arg ztrail
address tso "ALLOC FI(TRENDS) DA('ismpref.VAIDHIST') SHR"
address ispexec "LIBDEF ISPPLIB DATASET ID('ismpref.ISPPLIB') STACK"
address ispexec "LIBDEF ISPMLIB DATASET ID('ismpref.ISPMLIB') STACK"
address ispexec "LIBDEF ISPSLIB DATASET ID('ismpref.ISPSLIB') STACK"
address ispexec "LIBDEF ISPTLIB DATASET ID('ismpref.ISPTLIB') STACK"
/*********************************************************************/
/* The following code is wrapped around the SELECT PANEL to prevent  */
/* dialog termination if ztrail contains an invalid option.  Instead */
/* of the ISPF dialog termination panel, the "Invalid option" error  */
/* message will be displayed.  This is only necessary for            */
/* non-primary option menus (menus defined with &ZPRIM=NO in the     */
/* )INIT section of the panel) using the IBM default ZSEL syntax.    */
/*********************************************************************/
address ispexec "CONTROL ERRORS RETURN"
do forever
   address ispexec "SELECT PANEL(ISMPANL) OPT("ztrail") SCRNAME(ISM)"
   if rc <= 4 then
      leave
   else
      do
         address ispexec "SETMSG MSG("zerrmsg")"
         if zerrmsg <> 'ISPD221' then
            leave
      end
   ztrail = ''
end
address ispexec "LIBDEF ISPPLIB"
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPSLIB"
address ispexec "LIBDEF ISPTLIB"
address tso "FREE FI(TRENDS)"

@ISMF.text:
/* rexx */
/* trace i */
/*********************************************************************/
/* This exec invokes IBM's ISMF dialog.  It assumes that             */
/* SYS1.DGTLLIB and SYS1.DFQLLIB are available via LINKLIST.  If     */
/* not, add these loadlibs to the LINKLIST, or change this exec to   */
/* use a Dynamic STEPLIB product.  In order to support saved lists,  */
/* the user's ISPPROF dataset is concatenated to the ISPTLIB and     */
/* and ISPTABL datasets.  Change this dataset name to match your     */
/* site standards.  You may also choose a dataset other than ISPPROF */
/* if you wish.  If you do not run RMM, you can remove the           */
/* SYS1.SEDG* RMM datasets.                                          */
/*********************************************************************/
parse arg zcmd
address tso "ALTLIB ACT APPL(CLIST) DA('SYS1.DGTCLIB','SYS1.SEDGEXE1')"
address ispexec "LIBDEF ISPMLIB DATASET ID('SYS1.DFQMLIB',",
                                          "'SYS1.DGTMLIB',",
                                          "'SYS1.SEDGMENU') STACK"
address ispexec "LIBDEF ISPPLIB DATASET ID('SYS1.DFQPLIB',",
                                          "'SYS1.DGTPLIB',",
                                          "'SYS1.SEDGPENU') STACK"
address ispexec "LIBDEF ISPTABL DATASET ID('"userid()".ISPPROF') STACK"
/*********************************************************************/
/* This code determines which release of OS/390 is running because   */
/* at OS/390 V2R10 and higher, RMM eliminated SEDGSKL1 and SEDGTBL1. */
/* This modification is courtesy of David Alcock at American         */
/* Electric Power.                                                   */
/*********************************************************************/
if mvsvar('sysmvs') > 'SP6.0.9' then
   do
      address ispexec "LIBDEF ISPSLIB DATASET ID('SYS1.DGTSLIB') STACK"
      address ispexec "LIBDEF ISPTLIB DATASET ID('"userid()".ISPPROF',",
                                                "'SYS1.DGTTLIB') STACK"
   end
else
   do
      address ispexec "LIBDEF ISPSLIB DATASET ID('SYS1.DGTSLIB',",
                                                "'SYS1.SEDGSKL1') STACK"
      address ispexec "LIBDEF ISPTLIB DATASET ID('"userid()".ISPPROF',",
                                                "'SYS1.DGTTLIB',",
                                                "'SYS1.SEDGTBL1') STACK"
   end
address ispexec "SELECT PGM(DGTFMD01) PARM("zcmd") NEWAPPL(DGT)",
                "NOCHECK PASSLIB SCRNAME(ISMF)"
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPPLIB"
address ispexec "LIBDEF ISPSLIB"
address ispexec "LIBDEF ISPTABL"
address ispexec "LIBDEF ISPTLIB"
address tso "ALTLIB DEACT APPL(CLIST)"

