                                          "'zebbpref.ISPTLIB',",
                                          "'oasispref.ISPTLIB') STACK"
/*********************************************************************/
/* The following code is wrapped around the SELECT PANEL to prevent  */
/* dialog termination if ztrail contains an invalid option.  Instead */
/* of the ISPF dialog termination panel, the "Invalid option" error  */
/* message will be displayed.  This is only necessary for            */
/* non-primary option menus (menus defined with &ZPRIM=NO in the     */
/* )INIT section of the panel) using the IBM default ZSEL syntax.    */
/*********************************************************************/
address ispexec "CONTROL ERRORS RETURN"
do forever
   address ispexec "SELECT PANEL(ALT@PRIM) OPT("zcmd") NEWAPPL(ZEE)",
                   "PASSLIB SCRNAME(ZEKE)"
   if rc <= 4 then
      leave
   else
      do
         address ispexec "SETMSG MSG("zerrmsg")"
         if zerrmsg <> 'ISPD221' then
            leave
      end
   ztrail = ''
end
address ispexec "LIBDEF ISPMLIB"
address ispexec "LIBDEF ISPPLIB"
address ispexec "LIBDEF ISPTLIB"
address tso "ALTLIB DEACT APPL(CLIST)"

DSNECPRI.text:
PROC 0 +
   SSID(DEFAULT) ZTRAIL()
/********************************************************************/
/* CONLEY ADD ZTRAIL CODE TO PROCESS NESTED MENU SELECTIONS.        */
/********************************************************************/
/** PROLOGUE ********************************************************/
/*  MODULE-NAME = DSNECPRI                                          */
/*  DESCRIPTIVE-NAME = DB2I PRIMARY CLIST                           */
/*    LICENSED MATERIALS - PROPERTY OF IBM                          */
/*    5645-DB2                                                      */
/*    (C) COPYRIGHT 1982, 1998 IBM CORP.  ALL RIGHTS RESERVED.      */
/*                                                                  */
/*    STATUS = VERSION 6                                            */
/*  FUNCTION = INITIALIZE THE DB2I DEFAULT SUBSYSTEM ID AND THEN    */
/*             DISPLAY THE DB2I PRIMARY OPTIONS PANEL.              */
/*  NOTES                                                           */
/*   DEPENDENCIES = ISPF/TSO                                        */
/*   RESTRICTIONS = NONE                                            */
/*   REGISTER-CONVENTIONS = N/A                                     */
/*   PATCH-LABEL  = NONE                                            */
/*                                                                  */
/*  MODULE-TYPE  = CLIST                                            */
/*   PROCESSOR   = TSO CLIST PROCESSOR                              */
/*   MODULE-SIZE = APPROXIMATELY 70 LINES                           */
/*   ATTRIBUTES  = CLIST                                            */
/*                                                                  */
/*  ENTRY-POINT = DSNECPRI                                          */
/*   PURPOSE = SEE FUNCTION                                         */
/*   LINKAGE = CALLED BY PANEL ISRPRIM OR A USER WRITTEN PANEL      */
/*                                                                  */
/*  INPUT                                                           */
/*   SYMBOLIC LABEL/NAME = SSID                                     */
/*     DESCRIPTION = OPTIONAL PARAMETER IDENTIFING THE REQUESTED    */
/*                   DB2I DEFAULT SUBSYSTEM ID TO BE USED FOR THIS  */
/*                   SESSION.                                       */
/*   SYMBOLIC LABEL/NAME = VARIOUS ISPF VARIABLES.                  */
/*     DESCRIPTION = THE PANEL VARIABLES ARE STORED IN THE ISPF POOL*/
/*  OUTPUT                                                          */
/*   SYMBOLIC LABEL/NAME = DSNEOV01                                 */
/*     DESCRIPTION = UPDATES THE DB2I DEFAULT SSID IN THE ISPF      */
/*                   VARIABLE POOL.                                 */
/*   SYMBOLIC LABEL/NAME = N/A                                      */
/*     DESCRIPTION = DISPLAYS THE DB2I PRIMARY OPTIONS PANEL        */
/*                                                                  */
/*  EXIT-NORMAL                                                     */
/*   RETURN-CODES = 0                                               */
/*   REASON-CODES = 0                                               */
/*                                                                  */
/*  EXIT-ERROR                                                      */
/*   RETURN-CODES   = 0                                             */
/*   ABEND-CODES    = 0                                             */
/*                                                                  */
/*  EXTERNAL REFERENCES                                             */
/*   ROUTINES/SERVICES  = ISPEXEC: VGET, VPUT, SELECT               */
/*                                                                  */
/*   DATA-AREAS     = NONE                                          */
/*   CONTROL-BLOCK  = NONE                                          */
/*                                                                  */
/*  TABLES          = NONE                                          */
/*  MACROS          = NONE                                          */
/*  CHANGE-ACTIVITY                                                 */
/*  06/17/92 = CREATED BY DB2I INVOCATION CHANGE            TU2720T1*/
/*  04/16/93 = REMOVE GLOBALS (REF: APARS OY45756/OY37640)   KEF0273*/
/*             APPLY KDW0091 FOR DSN310                             */
/*  03/31/94   CHANGE RELEASE LEVEL TO 410                   KEW0160*/
/*  01/24/95 = SUPPORT BOOKMANAGER DCR D8158                 KED0120*/
/** PSEUDOCODE ******************************************************/
/*  RETRIEVE TRACE VARIABLES AND SETUP TRACE IF REQUESTED           */
/*  ALWAYS INVOKE DSNEMC01 CLIST                             KED0120*/
/*  IF SSID IS ¬= NONE THEN                                         */
/*    ISSUE A VPUT OF THE DB2I DEFAULT SSID VALUE                   */
/*  ISSUE A VGET OF THE DB2I DEFAULT SSID VALUE                     */
/*  ISPF SELECT DB2I PRIMARY OPTIONS PANEL                          */
/** END PROLOGUE ****************************************************/
CONTROL PROMPT
/********************************************************************/
/* ALWAYS INVOKE INITIALIZATION CLIST (DSNEMC01)             KED0120*/
/********************************************************************/
%DSNEMC01                        /* INIT DEFAULT VARIABLES   KED0120*/
ISPEXEC VGET DSNEAV05 PROFILE    /* RETREIVE OLD TRACE VARIABLE     */
/********************************************************************/
/*  DETERMINE TYPE OF CLIST TRACING TO PERFORM                      */
/********************************************************************/
IF &DSNEAV05 = NO THEN           /* DECIDE IF AND HOW WE ARE TRACING*/-
  CONTROL NOLIST NOCONLIST NOSYMLIST
ELSE -
  DO
    IF &DSNEAV05 = LIST  THEN -
      CONTROL LIST NOCONLIST NOSYMLIST
    ELSE -
      IF &DSNEAV05 = CONLIST THEN -
        CONTROL LIST CONLIST NOSYMLIST
      ELSE -
        CONTROL LIST CONLIST SYMLIST
  END
/********************************************************************/
/*  SEE IF WE WERE PASSED A SSID. IF SO THEN VPUT TO ISPF PROFILE   */
/********************************************************************/
IF &STR(&SSID) ¬= &STR(DEFAULT) THEN +
  DO
    SET &DSNEOV01 = &SSID
    ISPEXEC VPUT (DSNEOV01) PROFILE
  END
/*******************************************************************/
/*  ISSUE A VGET TO ENSURE THAT DSNEOV01 (DEFAULT SSID) HAS THE    */
/*  THE CORRECT VALUE.                                             */
/*******************************************************************/
ISPEXEC VGET (DSNEOV01) PROFILE
/********************************************************************/
/* CONLEY ADD ZTRAIL CODE TO PROCESS NESTED MENU SELECTIONS.        */
/********************************************************************/
ISPEXEC SELECT PANEL(DSNEPRI) OPT(&ZTRAIL)
EXIT

EOXBKMGR.text:
/*REXX*/
/*-------------------------------------------------------------------*/
/* This exec has been modified for Dynamic ISPF to add the PASSLIB   */
/* parm to the SELECT NEWAPPL statement.                             */
/*-------------------------------------------------------------------*/
/* APAR PN20713 states IBM's official position that BookManager READ */
/* does not support LIBDEF's.  PN20713 goes on to state that IBM     */
/* will not support anyone wishing to use LIBDEF when invoking       */
/* Book Manager READ.  IBM cites four reasons as the basis for       */
/* denying LIBDEF support in BookManager READ.  In a Dynamic ISPF    */
/* environment, only one of the four reasons is valid.  The fourth   */
/* reason listed in PN20713 describes the BookManager "Edit source"  */
/* function.  This function performs a split screen and brings up an */
/* edit session so the user can directly edit the manual they're     */
/* working with.  This function will not work because the LIBDEF's   */
/* are not carried across the split screen.  If you require the      */
/* "Edit source" function in BookManager, you will have to create a  */
/* LOGON proc and hard allocate the BookManager datasets.  If you do */
/* not require the "Edit source" function, this exec will correctly  */
/* invoke BookManager READ, but you do so at your own risk.  IBM     */
/* will not support any dialog errors you encounter unless you have  */
/* hard allocated datasets in a LOGON PROC.                          */
/*-------------------------------------------------------------------*/
/*  BookManager Read/MVS * 5695-046 *                                */
/*  (C) COPYRIGHT IBM CORPORATION 1990, 1994                         */
/*  LICENSED MATERIAL - PROGRAM PROPERTY OF IBM                      */
/*  REFER TO COPYRIGHT INSTRUCTIONS: FORM G120-2083                  */
/*-------------------------------------------------------------------*/
/*                                                                   */
/* Exec Name:    BOOKMGR                                             */
/*                                                                   */
/* Date Written: 10/17/90                                            */
/*                                                                   */
/* Description:  Primary entry command path for BookManager MVS.     */
/*                                                                   */
/* Invocation:   BOOKMGR <bookshelf> <book> <command>                */
/*                                                                   */
/*-------------------------------------------------------------------*/
trace Off

parse arg args                            /* Get input parm list     */
parse source . . me .                     /* Get our EXEC name       */

/*-------------------------------------------------------------------*/
/* Add closing parenthesis if missing                                */
/*-------------------------------------------------------------------*/
lf_parenct = 0
rt_parenct = 0
Do i = 1 to length(args)
  if substr( args, i, 1 ) = '(' then lf_parenct = lf_parenct + 1
  if substr( args, i, 1 ) = ')' then rt_parenct = rt_parenct + 1
End
Do While( rt_parenct < lf_parenct )
  args = args || ")"
  rt_parenct = rt_parenct + 1
End

/*-------------------------------------------------------------------*/
/* See if ISPF is active. If not, re-invoke ourselves after          */
/* starting ISPF.                                                    */
/*-------------------------------------------------------------------*/
if (sysvar("SYSISPF") ¬= "ACTIVE") then do
  "ISPSTART CMD(%"me args ")"
  exit rc
  end

/*-------------------------------------------------------------------*/
/* Invoke EOXVSTRT exec to begin BookManager Read. Invoke under      */
/* the application ID of EOXR.                                       */
/*-------------------------------------------------------------------*/
"ISPEXEC SELECT CMD(%EOXVSTRT" args ")",
               "MODE(FSCR) SUSPEND NEWAPPL(EOXR) PASSLIB"
retcode = rc

/*-------------------------------------------------------------------*/
/* Free the list of bookshelves data sets if still allocated         */
/* (usually if an abort occurs)                                      */
/*-------------------------------------------------------------------*/
address ISPEXEC "VGET ZSCREEN SHARED" /* Get logical screen number   */
shelfdd = "EOXSHLF"||zscreen       /* to complete shelf dd name.     */
                                   /* Keeps split screens separate.  */

x = MSG("OFF")                     /* Turn off messages              */
address TSO "FREE FILE("shelfdd")" /* Free in-case already allocated */
x = MSG(x)                         /* Restore message status         */

exit retcode

