CBKABSTR.text:
//CBKABSTR  PROC  CBN='?', NAME OF OBJECT CODEBOOK OR LIBRARY
//     CBPREFX='SYS2.TPL.V6',  PEFIX(ACCOUNT & INITIALS) FOR CODEBOOK<<
//     CORE=500K,     REGION ACTUALLY USED FOR PROGRAMS OF CB STEP
//     CBKMAX=2000K,  MAXIMUM REGION USED DURING CB STEP
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     CBUNIT=TSOWRK, DEVICE TYPE FOR OBJECT CODEBOOK       <<
//     QUOT='''',     TO ALLOW OS-ILLEGAL CBN VALUE
//     ABSDSN='SYS2.TPL.V6.OBJLIB(ABSOBJ)',
//     ABSOPT=,
//     CBKBLK=2976,CBVER=6,
//     CONTROL=IEFBR14,CONMAX=100K,
//     FREE=100,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB'    <<
//*********************************************************************
//*                                                                   *
//*      C B K A B S T R - - A PROCEDURE TO CREATE AN ABSTRACT OF AN  *
//*                EXISTING CODEBOOK WITHIN THE TPL SYSTEM.           *
//*                                                                   *
//*      VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:        *
//*           CBN='cccccccc',CBPREFX='CNaaaa.iii'                     *
//*                                                                   *
//*      IF THE CODEBOOK(S) TO BE ABSTRACTED ARE CONTAINED WITHIN     *
//*           A CODEBOOK LIBRARY, THEN THE STATEMENTS SPECIFYING      *
//*           WHICH CODEBOOKS TO ABSTRACT MUST FOLLOW A               *
//*           "//CB.INPUT" DD CARD.                                   *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF ANY PROBLEMS, CONTACT THE TPL SYSTEM TEAM;        *
//*            TELEPHONE:  357-5999
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//OBJECT  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  UNIT=&CBUNIT,DISP=(SHR,PASS,KEEP)
//*
//*
//CB  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&ABSOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     CREATE THE ABSTRACT OF THE CODEBOOK               *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&ABSDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT3  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//FILE1  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=(SHR,KEEP,KEEP)
//FILE4  DD  DSN=*.FILE1,DISP=SHR
//SYSIN  DD  DUMMY
//INPUT2  DD  DDNAME=INPUT
//INPUT  DD  DUMMY
//*           END OF PROCEDURE FOR CREATING AN ABSTRACT LISTING OF
//*                AN EXISTING CODEBOOK.
//*  PEND

CBKENTER.text:
//CBKENTER  PROC  CBN='NAME.FOR.CODEBOOK.LIBRARY.IS.REQUIRED',
//     CBPREFX='USERX', REQUIRED.FOR.ALL.USERS', ACCT & INITIALS  <<
//     CORE=500K,     CORE USED FOR PROGRAMS OF CB & WRITEIT STEPS
//     CBKMAX=2000K,  MAXIMUM REGION USED IN CB, SORT, & WRITEIT STEPS
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     NAMSP=350,     SPACE FOR NAME INDEX WORKFILE (IN 100'S OF NAMES)
//     NAMIN=35,      SECONDARY SPACE ALLOCATION SIZE FOR NAME INDEX
//     TOBSP=300,     NO. OF BLOCKS FOR TEMPORARY OBJECT CODEBOOK
//     SORTSP=160,    SPACE IN 100'S OF NAMES FOR EACH OF 6 SORTWORKS
//     TMPSP=100,     NO. OF BLOCKS FOR "TEMP" WORKFILES
//*    CBUNIT=3380,   DEVICE TYPE FOR CODEBOOK LIBRARY               <<
//     QUOT='''',     TO ALLOW OS-ILLEGAL CBN VALUE
//     ABSDSN='SYS2.TPL.V6.OBJLIB(ABSOBJ)', <
//     CBKBLK=2976,CBVER=6,
//     CONTROL=IEFBR14,CONMAX=100K,
//     FREE=100,
//     LIBDSN='SYS2.TPL.V6.OBJLIB(LIBOBJ)', <
//     LOKDSN='SYS2.TPL.V6.LOCK.FILE',
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB', <<
//     NAMBLK=3500,NAMLREC=35,NAMRECF=FB,
//     RDRDSN='SYS2.TPL.V6.OBJLIB(RDROBJ)', <
//     RDROPT=,
//     SORTPGM=SORT,                                                 <<
//     WRIDSN='SYS2.TPL.V6.OBJLIB(WRIOBJ)', <
//     WRTOPT=
//*********************************************************************
//*                                                                   *
//*      C B K E N T E R -- A PROCEDURE TO ENTER A CODEBOOK INTO      *
//*                         A CODEBOOK LIBRARY.                       *
//*                                                                   *
//*      THIS PROCEDURE CAN BE USED EITHER TO ADD A NEW CODEBOOK,     *
//*           OR TO REPLACE AN EXISTING CODEBOOK IN THE LIBRARY.      *
//*                                                                   *
//*      THE FOLLOWING PARAMETERS MUST BE SUPPLIED:                   *
//*           CBN='cccccccc',CBPREFX='CNaaaa.iii'                     *
//*                                                                   *
//*      THE STATEMENTS DESCRIBING THE SOURCE CODEBOOK MUST           *
//*           FOLLOW A "//CB.CODEBOOK" DD CARD.                       *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM;            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TMPOBJ  DD  DSN=&&TMPOBJ,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&CBKBLK,(&TOBSP))
//SORTWK01  DD  DSN=&&WORK1,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//NAMES  DD  DSN=&&NAMEREC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&NAMSP,&NAMIN)),
//  DCB=(RECFM=&NAMRECF,LRECL=&NAMLREC,BLKSIZE=&NAMBLK)
//NAMES2  DD  DSN=&&NAMEREC,VOL=REF=*.NAMES,DISP=(SHR,PASS)
//LIBRARY  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=(SHR,PASS)
//*
//*
//CB  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&RDROPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     READ THE SOURCE CODEBOOK AND BEGIN THE ANALYSIS   *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&RDRDSN,DISP=SHR
//INPUT7  DD  DUMMY
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&SORTCTL,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//OUTPUT3  DD  DSN=&&NAMEREC,DISP=(OLD,PASS),
//  SPACE=(&NAMBLK,(&NAMSP,&NAMIN),RLSE),
//  DCB=(RECFM=&NAMRECF,LRECL=&NAMLREC,BLKSIZE=&NAMBLK)
//OUTPUT4  DD  DSN=&&ABSCTL,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//FILE1  DD  DSN=&&TEMP1,DISP=(NEW,DELETE,DELETE),UNIT=WORK,
//  SPACE=(&CBKBLK,(&TMPSP))
//FILE2  DD  DSN=&&TMPOBJ,DISP=(OLD,PASS),SPACE=(&CBKBLK,(&TOBSP))
//SYSIN  DD  DDNAME=CODEBOOK
//CODEBOOK  DD  DUMMY
//*
//*
//SORT  EXEC  PGM=&SORTPGM,REGION=&CBKMAX,
//  COND=((0,LT,SYSCNTRL),(0,LT,CB))
//* *****     SORT THE NAME RECORDS INTO ALPHABETIC SEQUENCE    *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&SORTCTL,DISP=(OLD,PASS)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&NAMEREC,DISP=(OLD,PASS)
//SORTOUT  DD  DSN=&&NAMEREC,SPACE=(&NAMBLK,(&NAMSP,&NAMIN),RLSE),
//  DISP=(OLD,PASS)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE)
//*
//*
//WRITEIT  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&WRTOPT',
//  COND=((0,LT,SYSCNTRL),(4,LT,CB),(0,LT,SORT))
//* *****     WRITE THE OBJECT CODEBOOK ON TEMPORARY DISK SPACE,*******
//* *****        INSERT IT INTO THE CODEBOOK LIBRARY, AND THEN  *******
//* *****        CREATE THE ABSTRACT LISTING OF ITS CONTENTS.   *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&WRIDSN,DISP=SHR
//  DD  DSN=&LIBDSN,DISP=SHR
//  DD  DSN=&ABSDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT3  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  DSN=&&ABSCTL,UNIT=WORK,DISP=(OLD,DELETE,DELETE),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//FILE1  DD  DSN=&&TMPOBJ,DISP=(OLD,DELETE,DELETE),
//  SPACE=(&CBKBLK,(&TOBSP),RLSE)
//FILE2  DD  DSN=*.FILE1,VOL=REF=*.FILE1,DISP=(SHR,DELETE,DELETE)
//FILE3  DD  DSN=&&TEMP2,UNIT=WORK,SPACE=(&CBKBLK,(&TMPSP)),
//  DISP=(NEW,DELETE,DELETE)
//FILE4  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=(SHR,KEEP,KEEP)
//FILE5  DD  DSN=&LOKDSN,DISP=SHR
//SYSIN  DD  DUMMY
//INPUT2  DD  DSN=*.OUTPUT4,VOL=REF=*.OUTPUT4,DISP=(SHR,DELETE,DELETE)
//INPUT3  DD  DSN=&&NAMEREC,DISP=(OLD,DELETE,DELETE)
//NAMEREC  DD  DSN=&&NAMEREC,DISP=(OLD,DELETE,DELETE)
//INPUT4  DD  DUMMY
//INPUT5  DD  DUMMY
//INPUT7  DD  DUMMY
//SORTCTL  DD  DSN=&&SORTCTL,DISP=(OLD,DELETE,DELETE)
//*      END OF THE PROCEDURE TO ADD A NEW CODEBOOK OR REPLACE AN
//*           EXISTING CODEBOOK IN A CODEBOOK LIBRARY.
//*  PEND

CBKLIB.text:
//CBKLIB  PROC  CBN='?', NAME OF CODEBOOK LIBRARY
//*    CBVOL='?',     DISK PACK (VOL SER #) FOR CODEBOOK LIBRARY
//     CBPREFX='SYS2.TPL.V6',   INITIALS PREFIX FOR CODEBOOK LIBRARY <<
//     CORE=500K,     CORE ACTUALLY USED FOR PROGRAM OF CB STEP
//     CBKMAX=2000K,  MAXIMUM REGION USED DURING CB STEP
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//*    CBUNIT=3380,   DEVICE TYPE FOR CODEBOOK LIBRARY               <<
//     QUOT='''',     TO ALLOW OS-ILLEGAL CBN VALUE
//     CBKBLK=2976,CBVER=6,
//     CONTROL=IEFBR14,CONMAX=100K,
//     FREE=100,
//     LIBDSN='SYS2.TPL.V6.OBJLIB(LIBOBJ)',
//     LIBOPT=,
//     LOKDSN='SYS2.TPL.V6.LOCK.FILE',
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB'  <<
//*********************************************************************
//*                                                                   *
//*     C B K L I B  --  A PROCEDURE TO DO MAINTENANCE WORK           *
//*                      ON A CODEBOOK LIBRARY.                       *
//*                                                                   *
//*     THE FOLLOWING PARAMETERS MUST BE SPECIFIED:                   *
//*        CBN='cccccccc',CBPREFX='CNaaaa.iii'                        *
//*                                                                   *
//*     THE STATEMENTS DESCRIBING THE MAINTENANCE WORK TO BE DONE     *
//*          MUST FOLLOW A "//CB.INPUT" DD CARD.                      *
//*                                                                   *
//*     THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.          *
//*                                                                   *
//*     IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.             *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//LIBRARY  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=(SHR,PASS)
//*
//*
//CB  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&LIBOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     READ THE STATEMENTS DESCRIBING THE LIBRARY WORK   ******
//* *****        TO BE PERFORMED, AND THEN DO IT.               ******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&LIBDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT4  DD  DUMMY
//FILE4  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,DISP=SHR
//FILE5  DD  DSN=&LOKDSN,DISP=SHR
//SYSIN  DD  DUMMY
//INPUT4  DD  DDNAME=INPUT
//INPUT  DD  DUMMY
//INPUT7  DD  DUMMY
//*     END OF THE PROCEDURE TO PERFORM MAINTENANCE WORK ON A
//*          CODEBOOK LIBRARY.
//*  PEND

CBKMKLIB.text:
//CBKMKLIB  PROC  CBN='NAME.FOR.CODEBOOK.LIBRARY.IS.REQUIRED',
//     CBSP='?',      NO. OF BLOCKS FOR CODEBOOK LIBRARY
//     CBPREFX='USERX',    REQUIRED.FOR.ALL.USERS', ACCT & INITIALS  <<
//     CBSTATE=NEW,   INITIAL STATUS OF LIBRARY (NEW OR OLD)
//     CBDISP=CATLG,  TO ALLOW CATALOGING OF LIBRARY
//     CORE=500K,     CORE ACTUALLY USED FOR PROGRAM OF CB STEP
//     CBKMAX=2000K,  MAXIMUM REGION USED DURING CB STEP
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     CBIN=5,        SECONDARY ALLOCATION SIZE FOR LIB
//     CBUNIT=TSOWRK, DEVICE TYPE FOR CODEBOOK LIBRARY               <<
//     QUOT=, * ='''',TO ALLOW OS-ILLEGAL CBN VALUE OR TO CATALOG CBK
//     CBKBLK=2976,CBVER=6,
//     CONTROL=IEFBR14,CONMAX=100K,
//     FREE=100,
//     LIBOPT=,
//     MKLDSN='SYS2.TPL.V6.OBJLIB(MKLOBJ)',
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB' <<
//*********************************************************************
//*                                                                   *
//*      C B K M K L I B -- A PROCEDURE TO MAKE A LIBRARY OF          *
//*                         CODEBOOKS.  THE NEW LIBRARY WILL          *
//*                         INITIALLY BE EMPTY.                       *
//*                                                                   *
//*      THE FOLLOWING PARAMETERS MUST BE SUPPLIED:                   *
//*           CBN='cccccccc',CBSP=bbb,                                *
//*           CBPREFX='CNaaaa.iii'                                    *
//*                                                                   *
//*      IF THE LIBRARY ALREADY EXISTS AND IS BEING RE-INITIALIZED,   *
//*           THEN THE FOLLOWING PARAMETER MUST BE ADDED:             *
//*              CBSTATE=OLD                                          *
//*                                                                   *
//*                                                                   *
//*      THE STATEMENTS DESCRIBING THE LIBRARY MUST FOLLOW A          *
//*           "//CB.INPUT" DD CARD.                                   *
//*                                                                   *
//*      IF AN OLD CODEBOOK LIBRARY IS BEING COPIED INTO AN EXPANDED  *
//*           CODEBOOK LIBRARY, THEN PRECEDING THE "//CB.INPUT" DD    *
//*           THE FOLLOWING DD CARD MUST BE ADDED:                    *
//*                //CB.OLDLIB  DD  (COMPLETE DD CARD DESCRIBING THE  *
//*                                  OLD CODEBOOK LIBRARY)            *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM;            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//LIBRARY  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  UNIT=&CBUNIT,DISP=(&CBSTATE,&CBDISP),SPACE=(&CBKBLK,(&CBSP,&CBIN))
//*
//*
//CB  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&LIBOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     INITIALIZE THE NEW CODEBOOK LIBRARY               ******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&MKLDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//INPUT7  DD  DUMMY
//FILE1  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  SPACE=(&CBKBLK,(&CBSP,&CBIN),RLSE),DISP=(OLD,KEEP)
//FILE4  DD  DDNAME=OLDLIB
//OLDLIB  DD  DUMMY
//SYSIN  DD  DDNAME=INPUT
//INPUT  DD  DUMMY
//*     END OF PROCEDURE TO MAKE A NEW CODEBOOK LIBRARY.
//*  PEND

CBKSINGL.text:
//CBKSINGL  PROC   CBN='NAME.FOR.TRANSLATED.CODEBOOK.IS.REQUIRED',
//     CBPREFX='USERX',    REQUIRED.FOR.ALL.USERS', ACCT & INITIALS  <<
//     CBSTATE=OLD,   STATE OF DATASET STORING CBK (OLD OR NEW)
//     CBDISP=CATLG,  TO ALLOW CATALOGING OF CODEBOOK
//     CBSP=15,       NO. OF BLOCKS FOR STORING TRANSLATED CODEBOOK
//     CBIN=10,       SECONDARY SPACE ALLOCATION SIZE FOR STORING
//     ERRORS=4,      STORE TRANSLATED CODEBOOK IF ONLY MILD ERRORS
//     CORE=500K,     CORE USED FOR PGMS, STEPS CB, WRITEIT, & ABSTRACT
//     CBKMAX=2000K,  MAXIMUM REGION USED IN CB, SORT, & WRITEIT STEPS
//     CPYCORE=150K,  REGION FOR COPYING PROGRAM
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     NAMSP=350,     SPACE FOR NAME INDEX WORKFILE (IN 100'S OF NAMES)
//     NAMIN=35,      SECONDARY SPACE ALLOCATION SIZE FOR NAME INDEX
//     TOBSP=300,     NO. OF BLOCKS FOR TEMPORARY OBJECT CODEBOOK
//     SORTSP=160,    NO. OF BLOCKS FOR EACH OF 6 SORT WORKFILES
//     TMPSP=400,     NO. OF BLOCKS FOR "TEMP" WORKFILES
//     CBUNIT=TSOWRK, DEVICE TYPE FOR STORING TRANSLATED CODEBOOK    <<
//     QUOT=,  *'''',TO ALLOW OS-ILLEGAL CBN VALUE OR TO CATALOG CBK
//     ABSDSN='SYS2.TPL.V6.OBJLIB(ABSOBJ)',
//     ABSOPT=,
//     CBBLK=2976,
//     CBKBLK=2976,CBVER=6,
//     CBLRECL=2976,
//     CBRECFM=F,
//     CONTROL=IEFBR14,CONMAX=100K,
//     COPYPGM=IEBGENER,
//     FREE=100,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB', <<
//     NAMBLK=3500,NAMLREC=35,NAMRECF=FB,
//     RDRDSN='SYS2.TPL.V6.OBJLIB(RDROBJ)',
//     RDROPT=,
//     SORTPGM=SORT,                                                 <<
//     WRIDSN='SYS2.TPL.V6.OBJLIB(WRIOBJ)', <
//     WRTOPT=
//*********************************************************************
//*                                                                   *
//*      C B K S I N G L - - A PROCEDURE TO TRANSLATE A CODEBOOK      *
//*                WITHIN THE TPL SYSTEM.                             *
//*      THE TRANSLATED CODEBOOK WILL BE STORED WITHIN AN OBJECT      *
//*           CODEBOOK DATASET, WHICH WILL CONTAIN ONLY THE           *
//*           SINGLE CODEBOOK.                                        *
//*                                                                   *
//*      VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:        *
//*           CBN='cccccccc',CBPREFX='CNaaaa.iii'                     *
//*                                                                   *
//*      THE STATEMENTS DESCRIBING THE SOURCE CODEBOOK MUST BE PLACED *
//*           AFTER A "//CB.CODEBOOK"  DD CARD.                       *
//*                                                                   *
//*      IF THE OBJECT CODEBOOK DATASET INTO WHICH THE TRANSLATED     *
//*           CODEBOOK IS TO BE STORED DOES NOT ALREADY EXIST, THEN   *
//*           THE FOLLOWING PARAMETER MUST BE ADDED:                  *
//*              CBSTATE=NEW                                          *
//*                                                                   *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF ANY PROBLEMS, CONTACT THE TPL SYSTEM TEAM;        *
//*            TELEPHONE:  (PLACE YOUR AREA CODE & NUMBER HERE)     <<*
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TMPOBJ  DD  DSN=&&TMPOBJ,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&CBKBLK,(&TOBSP))
//SORTWK01  DD  DSN=&&WORK1,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//NAMES  DD  DSN=&&NAMEREC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&NAMSP,&NAMIN)),
//  DCB=(RECFM=&NAMRECF,LRECL=&NAMLREC,BLKSIZE=&NAMBLK)
//NAMES2  DD  DSN=&&NAMEREC,VOL=REF=*.NAMES,DISP=(SHR,PASS)
//OBJECT  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  UNIT=&CBUNIT,DCB=(RECFM=&CBRECFM,LRECL=&CBLRECL,BLKSIZE=&CBBLK),
//  DISP=(&CBSTATE,&CBDISP),SPACE=(&CBKBLK,(&CBSP,&CBIN))
//*
//*
//CB  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&RDROPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     READ THE SOURCE CODEBOOK AND TRANSFORM THE        *******
//* *****        INFORMATION INTO INTERNAL FORMATS.             *******
//STEPLIB  DD  DSN=&MONLIB,
//  DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&RDRDSN,DISP=SHR
//INPUT7  DD  DUMMY
//SYSIN  DD  DDNAME=CODEBOOK
//CODEBOOK  DD  DUMMY
//FILE1  DD  DSN=&&TEMP1,UNIT=WORK,SPACE=(&CBKBLK,(&TMPSP))
//FILE2  DD  DSN=&&TMPOBJ,DISP=(OLD,PASS)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&SORTCTL,DISP=(NEW,PASS),UNIT=WORK,
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=400),SPACE=(TRK,(1,1),RLSE)
//OUTPUT3  DD  DSN=&&NAMEREC,DISP=(OLD,PASS),
//  SPACE=(&NAMBLK,(&NAMSP,&NAMIN),RLSE),
//  DCB=(RECFM=&NAMRECF,LRECL=&NAMLREC,BLKSIZE=&NAMBLK)
//OUTPUT4  DD  DUMMY
//*
//*
//SORT  EXEC  PGM=&SORTPGM,REGION=&CBKMAX,
//  COND=((0,LT,SYSCNTRL),(0,LT,CB))
//* *****     SORT THE NAME RECORDS INTO ALPHABETIC SEQUENCE.   *******
//SYSIN  DD  DSN=&&SORTCTL,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&NAMEREC,DISP=(OLD,PASS)
//SORTOUT  DD  DSN=&&NAMEREC,SPACE=(&NAMBLK,(&NAMSP,&NAMIN),RLSE),
//  DISP=(OLD,PASS)
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE,DELETE)
//*
//*
//WRITEIT  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&WRTOPT',
//  COND=((0,LT,SYSCNTRL),(0,LT,CB),(0,LT,SORT))
//* *****     WRITE THE OBJECT CODEBOOK ON TEMPORARY DISK SPACE.*******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&WRIDSN,DISP=SHR
//SYSIN  DD  DUMMY
//SYSPRINT  DD  SYSOUT=&LISTING
//FILE1  DD  DSN=&&TMPOBJ,DISP=(OLD,PASS),
//  SPACE=(&CBKBLK,(&TOBSP),RLSE)
//FILE2  DD  DSN=*.FILE1,VOL=REF=*.FILE1,DISP=(SHR,PASS)
//FILE3  DD  DSN=&&TEMP2,UNIT=WORK,SPACE=(&CBKBLK,(&TMPSP))
//INPUT3  DD  DSN=&&NAMEREC,DISP=(OLD,DELETE)
//NAMEREC  DD  DSN=&&NAMEREC,DISP=(OLD,DELETE)
//INPUT5  DD  DDNAME=TEST&CBSTATE
//TESTNEW  DD  DUMMY
//TESTOLD  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=(OLD,PASS,KEEP)
//INPUT7  DD  DUMMY
//*
//*
//COPY  EXEC  PGM=&COPYPGM,REGION=&CPYCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,CB),(0,LT,SORT),(&ERRORS,LT,WRITEIT))
//* *****     COPY THE OBJECT CODEBOOK FROM THE WORK DISK ONTO  *******
//* *****        ITS PERMANENT PLACE.                           *******
//SYSIN  DD  DUMMY
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSUT1  DD  DSN=&&TMPOBJ,DISP=(OLD,DELETE,DELETE)
//SYSUT2  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=(OLD,PASS,KEEP),SPACE=(&CBKBLK,(&CBSP,&CBIN),RLSE)
//TMPOBJ  DD  DSN=&&TMPOBJ,DISP=(OLD,DELETE,DELETE)
//*
//*
//ABSTRACT  EXEC  PGM=&MONITOR,REGION=&CBKMAX,
//  PARM='FILE=&CBKBLK,FREE=&FREE,MAX=&CORE,&ABSOPT',
//  COND=((0,LT,SYSCNTRL),(0,LT,CB),(0,LT,SORT),(&ERRORS,LT,WRITEIT),
//        (0,LT,COPY))
//* *****     CREATE AN ABSTRACT OF THE PERMANENT OBJECT        *******
//* *****        CODEBOOK.                                      *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&ABSDSN,DISP=SHR
//SYSIN  DD  DUMMY
//SYSPRINT  DD  SYSOUT=&LISTING
//FILE1  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=(SHR,KEEP,KEEP)
//INPUT7  DD  DUMMY
//FILE4  DD  DSN=*.FILE1,VOL=REF=*.FILE1,DISP=(SHR,KEEP,KEEP)
//INPUT2  DD  DUMMY
//OUTPUT3  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//*     END OF THE PROCEDURE TO TRANSLATE A CODEBOOK, AND STORE
//*          THE TRANSLATED CODEBOOK IN A DATASET WHICH CONTAINS
//*          ONLY THE SINGLE CODEBOOK.
//*  PEND
//*                                          LAST UPDATED ON 08/21/84 *

SASTOTPL.text:
//SASTOTPL  PROC  INDSN='??',  NAME OF INPUT SAS DATASET
//  OPT1='S=72,NODATE,NOCENTER', SAS OPTIONS FOR 1ST SAS STEP
//  OPT2='S=72', SAS OPTIONS FOR 2ND SAS STEP ("GENDATA")
//  DATA='??',     NAME FOR GENERATED DATAFILE
//  DATADEV='TSOWRK', DEVICE TYPE FOR GENERATED DATAFILE      <<
//  LAB='SL',      LABEL TYPE FOR GENERATED DATAFILE
//  SEQ='1',       SEQUENCE # ON ITS VOLUME FOR GEN. DATAFILE
//  DATTYPE='NEW', INITIAL STATUS OF GENERATED DATAFILE
//  DATDISP='CATLG', FINAL DISPOSITION (KEEP,CATLG) OF GEN. DATAFILE
//  DATBOMB='KEEP', DISPOSITION OF GEN. DATAFILE IF ABEND OCCURS
//  DATSP='20',    PRIMARY # TRKS FOR GENERATED DATAFILE
//  DATIN='5',     SECONDARY ALLOCATION # TRKS FOR GEN. DATAFILE
//  DATRLSE=',RLSE', ALLOWS RELEASE OF UNUSED SPACE FOR GEN. DATAFILE
//  CBKNAME='&&CODEBOOK',  DSNAME FOR GENERATED SOURCE CODEBOOK
//  CBKUNIT='TSOWRK', DEVICE TYPE FOR GENERATED SOURCE CODEBOOK
//  CBKSP=50,      PRIMARY NO. BLOCKS SPACE FOR GEN. SOURCE CODEBOOK
//  CBKIN=20,      SECONDARY SPACE SIZE FOR GENERATED SOURCE CODEBOOK
//  CBKTYPE=NEW,   INITIAL STATUS OF GENERATED SOURCE CODEBOOK
//  CBKDISP=PASS,  FINAL DISPOSITION OF GENERATED SOURCE CODEBOOK
//  CBKBOMB=DELETE, DISPOSITION OF GENERATED SOURCE CODEBOOK IF ABEND
//  CBKBLK=6160,   BLOCKSIZE OF GENERATED SOURCE CODEBOOK
//  CBKLREC=80,    RECORD LENGTH OF GENERATED SOURCE CODEBOOK
//  CBKRECF=FB,    RECORD FORMAT OF GENERATED SOURCE CODEBOOK
//  PUTNAME='&&PUT',  DSNAME FOR GENERATED SAS PUT STMTS
//  PUTUNIT='TSOWRK', DEVICE TYPE FOR GENERATED SAS PUT STMTS
//  PUTSP=50,      PRIMARY NO. BLOCKS SPACE FOR GEN. SAS PUT STMTS
//  PUTIN=20,      SECONDARY SPACE SIZE FOR GENERATED SAS PUT STMTS
//  PUTTYPE=NEW,   INITIAL STATUS OF GENERATED SAS PUT STMTS
//  PUTDISP=CATLG, FINAL DISPOSITION OF GENERATED SAS PUT STMTS
//  PUTBOMB=DELETE, DISPOSITION OF GENERATED SAS PUT STMTS IF ABEND
//  PUTBLK=6160,   BLOCKSIZE OF GENERATED SAS PUT STMTS
//  PUTLREC=80,    RECORD LENGTH OF GENERATED SAS PUT STMTS
//  PUTRECF=FB,    RECORD FORMAT OF GENERATED SAS PUT STMTS
//  LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS             <<
//  PUNCH=B,       SYSOUT SPECIFICATION FOR PUNCH OUTPUT FROM SAS    <<
//  CNTSP=200,     PRIMARY # BLKS DISK SPACE FOR CONTENTS WORKFILE
//  CNTIN=50,      SECONDARY # BLKS FOR CONTENTS WORKFILE
//  CNTBLK=6650,CNTLREC=133,CNTRECF=FB,
//  CONTROL=IEFBR14,CONMAX=100K,
//  CTLCORE='300K',
//  ENTRY=SAS,
//  GENCORE='500K',
//  GSTDSN='SYS2.TPL.V6.SASTOTPL.LOADLIB', <
//  LINKPGM='SASTOTPL',
//  PUTCORE='500K',
//  QUOT=''''
//  SASDSN='SYS2.SAS.LIBRARY',                                       <<
//  SORT=1,
//  SORTMSG=NO,
//*
//*********************************************************************
//*                                                                   *
//*      S A S T O T P L - - A PROCEDURE TO CREATE A SEQUENTIAL FILE  *
//*           OF DATA FROM A SAS DATASET, AND A TPL SOURCE CODEBOOK   *
//*           TO DESCRIBE IT.                                         *
//*                                                                   *
//*      THE FOLLOWING PARAMETERS ARE REQUIRED:                       *
//*                                                                   *
//*           INDSN:    DSNAME OF THE SAS DATASET.                    *
//*           DATA:     DSNAME OF THE GENERATED DATAFILE              *
//*                                                                   *
//*      THE SAS STATEMENT INVOKING "PROC CONTENTS" FOR THE SAS       *
//*           DATASET MUST FOLLOW A "//GENERATE.CONTENTS" DD CARD.    *
//*                                                                   *
//*      THE CONTROL STATEMENTS FOR THE SASTOTPL LINKAGE PROGRAM MUST *
//*           FOLLOW A "//CONTROL.CARDS" DD CARD.                     *
//*                                                                   *
//*      IN CASE OF ANY PROBLEMS, CONTACT THE TPL SYSTEM TEAM;        *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//CODEBOOK  DD  DSN=&CBKNAME,DISP=(&CBKTYPE,PASS),UNIT=&CBKUNIT,
//  SPACE=(&CBKBLK,(&CBKSP,&CBKIN)),
//  DCB=(RECFM=&CBKRECF,LRECL=&CBKLREC,BLKSIZE=&CBKBLK)
//PUT  DD  DSN=&PUTNAME,DISP=(&PUTTYPE,PASS),UNIT=&PUTUNIT,
//  SPACE=(&PUTBLK,(&PUTSP,&PUTIN)),
//  DCB=(RECFM=&PUTRECF,LRECL=&PUTLREC,BLKSIZE=&PUTBLK)
//OUT  DD  DSN=&QUOT.&DATA.&QUOT,UNIT=&DATADEV,
//  DISP=(&DATTYPE,PASS),LABEL=(&SEQ,&LAB),
//  SPACE=(TRK,(&DATSP,&DATIN))
//*
//*
//GENERATE  EXEC  PGM=&ENTRY,PARM='&OPT1',REGION=&GENCORE,
//  COND=(0,LT,SYSCNTRL)
//LIBRARY  DD  DSN=&&LIBRARY,SPACE=(CYL,(1,,20)),
//  DISP=(MOD,DELETE)
//STEPLIB  DD  DSN=&SASDSN,DISP=SHR
//  DD  DSN=SYS1.PLIBASE,DISP=SHR                                    <<
//  DD  DSN=*.LIBRARY,DISP=(OLD,DELETE),VOL=REF=*.LIBRARY
//WORK  DD  UNIT=SYSDA,SPACE=(CYL,(4,24))  TEMPORARY DATA SETS
//FT11F001  DD  SYSOUT=&LISTING                       LOG
//FT12F001  DD  DSN=&&CONTENTS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&CNTBLK,(&CNTSP,&CNTIN),RLSE),
//  DCB=(RECFM=&CNTRECF,LRECL=&CNTLREC,BLKSIZE=&CNTBLK)
//FT13F001  DD  SYSOUT=&PUNCH
//FT15F001  DD  UNIT=TSOWRK,SPACE=(TRK,(1,5)),  PARMCARDS
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=800,BUFNO=1)
//SORTMSGS  DD  DDNAME=&SORTMSG
//YES  DD  SYSOUT=&LISTING
//NO  DD  DUMMY
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SORTWK01  DD  UNIT=WORK,SPACE=(CYL,(&SORT,8))
//SORTWK02  DD  UNIT=WORK,SPACE=(CYL,(&SORT,8))
//SORTWK03  DD  UNIT=WORK,SPACE=(CYL,(&SORT,8))
//IN  DD  DSN=&INDSN,DISP=(SHR,PASS)
//SYSIN  DD  DDNAME=CONTENTS
//CONTENTS  DD  DUMMY
//*
//*
//CONTROL  EXEC  PGM=&LINKPGM,PARM=OKAY,REGION=&CTLCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,GENERATE))
//STEPLIB  DD  DSN=SYS1.COBLIB,DISP=SHR                              <<
//  DD  DSN=&GSTDSN,DISP=SHR
//  DD  DSN=SYS1.SORTLIB,DISP=SHR                                    <<
//SORTWK01  DD  UNIT=WORK,SPACE=(TRK,50,,CONTIG)
//SORTWK02  DD  UNIT=WORK,SPACE=(TRK,50,,CONTIG)
//SORTWK03  DD  UNIT=WORK,SPACE=(TRK,50,,CONTIG)
//SORTMSGS DD  SYSOUT=&LISTING
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSDBOUT  DD  SYSOUT=&LISTING
//SYSOUT  DD  SYSOUT=&LISTING
//SYSUDUMP  DD  SYSOUT=&LISTING
//CONTENTS  DD  DSN=&&CONTENTS,DISP=(OLD,PASS)
//CODEBOOK  DD  DSN=&CBKNAME,DISP=(OLD,&CBKDISP,&CBKBOMB),
//  SPACE=(&CBKBLK,(&CBKSP,&CBKIN),RLSE),
//  DCB=(RECFM=&CBKRECF,LRECL=&CBKLREC,BLKSIZE=&CBKBLK)
//PUT  DD  DSN=&PUTNAME,DISP=(OLD,&PUTDISP,&PUTBOMB),
//  SPACE=(&PUTBLK,(&PUTSP,&PUTIN),RLSE),
//  DCB=(RECFM=&PUTRECF,LRECL=&PUTLREC,BLKSIZE=&PUTBLK)
//SYSIN  DD  DDNAME=CARDS
//CARDS  DD  DUMMY
//*
//*
//PUTDATA  EXEC  PGM=&ENTRY,PARM='&OPT2',REGION=&PUTCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,CONTROL),(0,LT,GENERATE))
//LIBRARY  DD  UNIT=WORK,DSN=&&LIBRARY,SPACE=(CYL,(1,,20)),
//  DISP=(MOD,DELETE)
//STEPLIB  DD  DSN=&SASDSN,DISP=SHR
//  DD  DSN=SYS1.PLIBASE,DISP=SHR                                    <<
//  DD  DSN=*.LIBRARY,DISP=(OLD,PASS),VOL=REF=*.LIBRARY
//WORK  DD  UNIT=SYSDA,SPACE=(CYL,(4,24))  TEMPORARY DATA SETS
//FT11F001  DD  SYSOUT=&LISTING                       LOG
//FT12F001  DD  SYSOUT=&LISTING                       PRINT
//FT13F001  DD  SYSOUT=&PUNCH
//FT15F001  DD  UNIT=WORK,SPACE=(TRK,(1,5)),  PARMCARDS
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=800,BUFNO=1)
//SORTMSGS  DD  DDNAME=&SORTMSG
//YES  DD  SYSOUT=&LISTING
//NO  DD  DUMMY
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SORTWK01  DD  UNIT=SYSDA,SPACE=(CYL,(&SORT,8))
//SORTWK02  DD  UNIT=SYSDA,SPACE=(CYL,(&SORT,8))
//SORTWK03  DD  UNIT=SYSDA,SPACE=(CYL,(&SORT,8))
//IN  DD  DSN=&INDSN,DISP=SHR
//SYSIN  DD  DSN=&PUTNAME,DISP=(OLD,&PUTDISP,&PUTBOMB)
//OUT  DD  DSN=&QUOT.&DATA.&QUOT,DISP=(OLD,&DATDISP,&DATBOMB),
//  LABEL=(&SEQ,&LAB),SPACE=(TRK,(&DATSP,&DATIN)&DATRLSE)
//*
//*  END OF PROCEDURE TO CREATE A SEQUENTIAL DATAFILE FROM A SAS
//*      DATASET AND A TPL CODEBOOK TO DESCRIBE THAT DATAFILE.
//*
//* PEND

TPLCLEAN.text:
//TPLCLEAN  PROC  CONTROL=IEFBR14,CONCORE=100K
//*********************************************************************
//*                                                                   *
//*      T P L C L E A N   - -   A PROCEDURE TO CLEAN UP ALL          *
//*               TEMPORARY DATASETS LEFT AROUND AT THE CONCLUSION    *
//*               OF A PRECEDING TPLERCHK, TPLGENTB, OR TPLTPGEN STEP.*
//*                                                                   *
//*     NO PARAMETERS OR DD CARDS ARE NECESSARY FOR THIS PROCEDURE.   *
//*                                                                   *
//*     THIS PROCEDURE MUST BE RUN AFTER EACH STEP WHICH EXECUTES     *
//*        EITHER TPLERCHK, TPLGENTB, OR TPLTPGEN, IF THAT STEP       *
//*        IS TO BE FOLLOWED BY ANOTHER STEP EXECUTING ANY OF THESE   *
//*        PROCEDURES, OR ANY OF TPLMULGN, TPLRERUN, TPLPHOTO,        *
//*        TPLCREATE, OR TPLCRPHO.                                    *
//*                                                                   *
//*     THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.          *
//*                                                                   *
//*     IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM;             *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONCORE
//* *****     DELETE ALL TEMPORARY DATASETS LEFT LYING AROUND.  *******
//TBSPECS  DD  DSN=&&TBSPECS,DISP=(OLD,DELETE,DELETE)
//TBCELLS  DD  DSN=&&TBCELLS,DISP=(OLD,DELETE,DELETE)
//CONSTR  DD  DSN=&&CONSTR,DISP=(OLD,DELETE,DELETE)
//TPLSTR  DD  DSN=&&TPLSTR,DISP=(OLD,DELETE,DELETE)
//*      END OF THE PROCEDURE TO CLEAN UP (DELETE) ALL REMAINING
//*         DATASETS AFTER THE END OF A STEP EXECUTING ONE OF THE
//*         PROCEDURES TPLERCHK, TPLGENTB, OR TPLTPGEN.
//*  PEND

TPLCREAT.text:
//TPLCREAT  PROC  LISTING='*',  SYSOUT SPECIFICATION FOR ALL LISTINGS
//     TABLES='A',    SYSOUT SPECIFICATION IF TABLES ARE SEPARATED   <<
//     CRTCORE=500K,  REGION ACTUALLY USED FOR PROGRAM OF CREATE STEP
//     CRTMAX=2000K,  MAXIMUM REGION USED DURING CREATE STEP
//     CORE=500K,     REGION ACTUALLY USED FOR PROGRAMS OF TPL STEP
//     TPLMAX=2000K,  MAXIMUM REGION USED DURING TPL STEP
//     PSRCORE=500K,  REGION SIZE FOR PCLSORT STEP
//     LAYCORE=500K,  REGION ACTUALLY USED FOR PROGRAM OF LAYOUT STEP
//     LAYMAX=2000K,  MAXIMUM REGION USED DURING LAYOUT STEP
//     WKUN=WORK,    DEVICE TYPE FOR SORT WORKFILES
//     WKSP=1600,     NO. OF BLKS FOR SORT WORKFILES
//     COORSP=1000,   NO. OF BLKS FOR COORDINATES WORKFILE
//     CONSP=400,     NO. OF BLKS FOR TABLE CONSTRUCTIONS WORKFILE
//     CONIN=80,      SECONDARY ALLOCATION SIZE FOR TABLE CONSTRUC.
//     SPC1SP=200,    INITIAL NO. OF BLKS FOR TABLE SPECS WORKFILE
//     SPC2SP=200,    NO. OF BLKS FOR PCL'S NEW TBSPECS WORKFILE
//     CL3UN=WORK,   DEVICE TYPE FOR "TBCELLS" WORKFILE
//     CL3SP=4000,    NO OF BLKS FOR "TBCELLS" WORKFILE
//     TSLDSN='SYS2.TPL.V6.TYPE.STYLES', NAME OF TYPE STYLE LIBRARY<<
//     STYLE=NOPH,  WAS=SET1, NAME OF DEFAULT TYPE STYLE  <<
//     REQSP=40,      INITIAL NUMBER OF BLOCKS FOR GENERATED TPL
//     REQIN=20,      SECONDARY NUMBER OF BLOCKS FOR GENERATED TPL
//     REQUNIT=WORK, UNIT FOR GENERATED TPL (DEFAULT: TEMP DISK)
//     CHGSP=40,      INITIAL NUMBER OF BLOCKS FOR GENERATED PCL
//     CHGIN=40,      SECONDARY NUMBER OF BLOCKS FOR GENERATED PCL
//     CHGUNIT=WORK, UNIT FOR GENERATED PCL (DEFAULT: TEMP DISK)
//     CALPRE=,   INITIIALS PREFIX FOR USE IN TPLCRPHO  <<
//     CALNAME='USERX', NAME FOR USE IN TPLCRPHO
//     CALTYPE=NEW,   STATUS FOR USE IN TPLCRPHO
//     CALDISP=CATLG, TO PERMIT CATALOGING IN TPLCRPHO
//     CALMOD=OLD,    FOR USE OF MOD IN TPLCRPHO
//     CALSP=640,     INITIAL SPACE IN BLOCKS FOR USE IN TPLCRPHO
//     CALIN=160,     SECONDARY SPACE FOR USE IN TPLCRPHO
//     CALUNIT=TSOWRK, UNIT TYPE FOR USE IN TPLCRPHO         <<
//*    CALSEQ=1,      DATASET SEQUENCE # FOR USE IN TPLCRPHO
//*    CALLAB=SL,     LABEL TYPE FOR USE IN TPLCRPHO
//*    EXPRET='RETPD', CHOICE OF "EXPDT" OR "RETPD" FOR TPLCRPHO     <<
//*    RETPD=00,     # OF DAYS TO RETAIN TAPE FOR TPLCRPHO          <<
//     DICNAME='BLS.HYPHEN.DICT',  NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CBKBLK=2976,CBVER=6,
//     CBN='TPL.CREATE.TABLE',CBPREFX='SYS2',
//     CALBLK=1300,CALLREC=130,CALRECF=FB,
//     CL3RECF=FB,CL3LREC=32,
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONBLK=3059,CONLREC=133,CONRECF=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     CRTFREE=100,CRTOPT=,
//     CTLDSN='SYS2.TPL.V6.OBJLIB(CTLOBJ)',
//     FREE=100,
//     GENBLK=12960,GENLREC=80,GENRECF=FB,
//     LAYDSN='SYS2.TPL.V6.OBJLIB(LAYOBJ)',
//     LAYOPT=,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB',  <<
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',
//     QUOT='''',
//     SORTPGM=SORT,                                                 <<
//     TPLBLK=2976,
//     TPLOPT=,
//     TRNDSN='SYS2.TPL.V6.OBJLIB(TRNOBJ)'
//*********************************************************************
//*                                                                   *
//*      T P L C R E A T - - A PROCEDURE TO CREATE TABLES CONTAINING  *
//*           USER-SPECIFIED CELL VALUES AND LABELS, AND THEN         *
//*           PRINT THE TABLES ON THE COMPUTER'S PRINTER.             *
//*                                                                   *
//*     THE CREATE TABLE STATEMENTS COMPRISING THE REQUEST MUST BE    *
//*          PLACED AFTER A "//CREATE.TABLES" DD CARD.                *
//*                                                                   *
//*     ANY PCL STATEMENTS MODIFYING THE TABLES, IF PRESENT, MUST     *
//*          BE PLACED AFTER A "//TPL.CHANGES" DD CARD, WHICH MUST    *
//*          APPEAR AFTER THE CREATE TABLE STATEMENTS.                *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TPLSPEC  DD  DSN=&&TPLSPEC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC1SP))
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//COORD  DD  DSN=&&COORD,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//COORD2  DD  DSN=*.COORD,VOL=REF=*.COORD,DISP=(SHR,PASS)
//CELLS  DD  DSN=&&TBCELLS,UNIT=&CL3UN,DISP=(NEW,PASS),
//  DCB=(RECFM=&CL3RECF,LRECL=&CL3LREC,BLKSIZE=&TPLBLK),
//  SPACE=(&TPLBLK,(&CL3SP))
//CONSTR  DD  DSN=&&CONSTR,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN))
//SORTWK01  DD  DSN=&&WORK1,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//REQUEST  DD  DSN=&&REQUEST,DISP=(NEW,PASS),
//  DCB=(RECFM=&GENRECF,LRECL=&GENLREC,BLKSIZE=&GENBLK),
//  SPACE=(&GENBLK,(&REQSP,&REQIN)),UNIT=&REQUNIT
//CHANGES  DD  DSN=&&CHANGES,DISP=(NEW,PASS),
//  DCB=(RECFM=&GENRECF,LRECL=&GENLREC,BLKSIZE=&GENBLK),
//  SPACE=(&GENBLK,(&CHGSP,&CHGIN)),UNIT=&CHGUNIT
//PROFILE  DD  DSN=&PRODSN,DISP=SHR
//*
//*
//CREATE  EXEC  PGM=&MONITOR,REGION=&CRTMAX,
//  PARM='FILE=&CRTBLK,FREE=&CRTFREE,MAX=&CRTCORE,&CRTOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     THE CREATE TABLE PHASE                            *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&CTLDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&REQUEST,DISP=(OLD,PASS),
//  SPACE=(&GENBLK,(&REQSP,&REQIN),RLSE)
//OUTPUT3  DD  DSN=&&CHANGES,DISP=(OLD,PASS),
//  SPACE=(&GENBLK,(&CHGSP,&CHGIN),RLSE)
//FILE6  DD  DSN=&&TBCELLS,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&CL3SP),RLSE),
//  DCB=(RECFM=&CL3RECF,LRECL=&CL3LREC,BLKSIZE=&TPLBLK)
//SYSIN  DD  DUMMY
//INPUT3  DD  DDNAME=TABLES
//TABLES  DD  DUMMY
//INPUT7  DD  DUMMY
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  PARM='FILE=&TPLBLK,FILE2=&CBKBLK,FREE=&FREE,MAX=&CORE,&TPLOPT',
//  COND=((0,LT,SYSCNTRL),(0,LT,CREATE))
//* *****     THE TPL TRANSLATION PHASE, FOLLOWED BY THE PCL    *******
//* *****     TRANSLATION PHASE.                                *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&TRNDSN,DISP=SHR
//  DD  DSN=&PCLDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&PCLSORT,UNIT=WORK,SPACE=(TRK,(1,1)),
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=400),DISP=(NEW,PASS)
//OUTPUT3  DD  DSN=&&CONSTR,DISP=(OLD,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN),RLSE)
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//FILE1  DD  DSN=&&TPLSPEC,DISP=(OLD,DELETE,KEEP),
//  SPACE=(&TPLBLK,(&SPC1SP),RLSE)
//FILE2  DD  DSN=&CBPREFX..CB.V&CBVER..&CBN,DISP=SHR
//FILE3  DD  DSN=&&COORD,DISP=(OLD,PASS,DELETE),
//  SPACE=(&TPLBLK,(&COORSP),RLSE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP),RLSE)
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//SYSIN  DD  DSN=&&REQUEST,DISP=(OLD,DELETE)
//INPUT3  DD  DSN=&&CHANGES,DISP=(OLD,DELETE)
//  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//INPUT4  DD  DUMMY
//INPUT7  DD  DUMMY
//*
//*
//PCLSORT  EXEC  PGM=&SORTPGM,REGION=&PSRCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,CREATE),(16,LE,TPL))
//* *****     THE PCL COORDINATE RECORD SORT STEP               *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&PCLSORT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTOUT  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE,DELETE)
//*
//*
//LAYOUT  EXEC  PGM=&MONITOR,REGION=&LAYMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&LAYCORE,&LAYOPT',
//  COND=((0,LT,SYSCNTRL),(0,LT,CREATE),(16,LE,TPL))
//* *****     THE TABLE LAYOUT STEP                             *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&LAYDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT7  DD  SYSOUT=&TABLES,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//SYSPUNCH  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//INPUT5  DD  DSN=&TSLDSN(&STYLE.CON),DISP=(SHR,KEEP)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,DELETE,DELETE)
//FILE6  DD  DSN=&&TBCELLS,DISP=(OLD,DELETE,DELETE)
//INPUT7  DD  DUMMY
//SYSIN  DD  DUMMY
//COORD  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//CALLFILE  DD  DUMMY,SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE),
//  DSN=&QUOT.&CALPRE.&CALMOD.VIDEO.CALL.&CALNAME.&QUOT,
//  UNIT=&CALUNIT,
//  DISP=(&CALTYPE,&CALDISP,KEEP),
//* LABEL=(&CALSEQ,&CALLAB,&EXPRET=&RETPD),                          <<
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK)
//*      END OF PROCEDURE FOR CREATING TABLES AND PRINTING
//*           THE TABLES ON THE COMPUTER'S PRINTER.
//*  PEND

TPLCRPHO.text:
//TPLCRPHO  PROC  LISTING='*',  SYSOUT SPECIFICATION FOR ALL LISTINGS
//     CRTCORE=500K,  REGION ACTUALLY USED FOR PROGRAM OF CREATE STEP
//     CRTMAX=2000K,  MAXIMUM REGION USED DURING CREATE STEP
//     CORE=500K,     REGION ACTUALLY USED FOR PROGRAMS OF TPL STEP
//     TPLMAX=2000K,  MAXIMUM REGION USED DURING TPL STEP
//     PSRCORE=500K,  REGION SIZE FOR PCLSORT STEP
//     LAYCORE=500K,  REGION ACTUALLY USED FOR PROGRAM OF LAYOUT STEP
//     LAYMAX=2000K,  MAXIMUM REGION USED DURING LAYOUT STEP
//     CSRCORE=500K,  REGION SIZE FOR CALLSORT STEP
//     WKUN=WORK,    DEVICE TYPE FOR SORT WORKFILES
//     WKSP=1600,     NO. OF BLKS FOR SORT WORKFILES
//     COORSP=1000,   NO. OF BLKS FOR COORDINATES WORKFILE
//     CONSP=400,     NO. OF BLKS FOR TABLE CONSTRUCTIONS WORKFILE
//     CONIN=80,      SECONDARY ALLOCATION SIZE FOR TABLE CONSTRUC.
//     SPC1SP=200,    INITIAL NO. OF BLKS FOR TABLE SPECS WORKFILE
//     SPC2SP=200,    NO. OF BLKS FOR PCL'S NEW TBSPECS WORKFILE
//     CL3UN=WORK,   DEVICE TYPE FOR "TBCELLS" WORKFILE
//     CL3SP=4000,    NO OF BLKS FOR "TBCELLS" WORKFILE
//     TSLDSN='SYS2.TPL.V6.TYPE.STYLES', NAME OF TYPE STYLE LIBRARY<<
//     STYLE=NOPH,    NAME OF DEFAULT TYPE STYLE                     <<
//     PHOTDSN='&&PHOTOUT', DSNAME OF PHOTO WORKFILE
//     PHOTUNT=WORK, UNIT TYPE FOR PHOTO WORKFILE
//     PHOTTYP=NEW,   PHOTO WORKFILE STATUS
//     PHOTDEL=DELETE, PHOTO WORKFILE FINAL DISPOSITION
//     PHSPACE=480,   NO. OF BLOCKS FOR PHOTO WORKFILE
//     PHINCR=200,    SECONDARY ALLOC BLOCKS FOR PHOTO WORKFILE
//     PHOTLAB='SL',  PHOTO WORKFILE LABEL TYPE
//     PHOTSEQ=1,     PHOTO WORKFILE SEQUENCE
//     CALPRE='SYS2.TPL.V6',  & INITIALS PREFIX FOR CALL FILE        <<
//     CALNAME=,      NAME OF CALL FILE
//     CALTYPE=NEW,   STATUS OF CALL FILE DATA SET
//     CALDISP=CATLG, TO PERMIT CATALOGING OF CALL FILE
//     CALMOD=OLD,    TO PERMIT MOD OF CALL FILE DATA SET
//     CALSP=640,     INITIAL SPACE IN BLOCKS FOR CALL FILE
//     CALIN=160,     SECONDARY SPACE FOR CALL FILE
//     CALUNIT=TSOWRK, NIT TYPE FOR CALL FILE                        <<
//     CALSEQ=1,      DATASET SEQUENCE # FOR CALL FILE IF ON TAPE
//     CALLAB=SL,     LABEL TYPE FOR CALL FILE IF ON TAPE
//*    EXPRET='RETPD', CHOICE: 'EXPDT' OR 'RETPD' IF TAPE CALL FILE  <<
//*    RETPD=10,     # OF DAYS TO RETAIN CALL FILE IF ON TAPE       <<
//     CLWKUN=TSOWRK, UNIT TYPE FOR PHOTO OUTPUT WORK FILE
//     REQSP=40,      INITIAL NUMBER OF BLOCKS FOR GENERATED TPL
//     REQIN=20,      SECONDARY NUMBER OF BLOCKS FOR GENERATED TPL
//     REQUNIT=TSOWRK, NIT FOR GENERATED TPL (DEFAULT: TEMP DISK)
//     CHGSP=40,      INITIAL NUMBER OF BLOCKS FOR GENERATED PCL
//     CHGIN=40,      SECONDARY NUMBER OF BLOCKS FOR GENERATED PCL
//     CHGUNIT=WORK, UNIT FOR GENERATED PCL (DEFAULT: TEMP DISK)
//     DICNAME='BLS.HYPHEN.DICT',  NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CBKBLK=2976,CBVER=6,
//     CBN='TPL.CREATE.TABLE',CBPREFX='SYS2',
//     CALBLK=1300,CALLREC=130,CALRECF=FB,
//     CL3RECF=FB,CL3LREC=32,
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONBLK=3059,CONLREC=133,CONRECF=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     CRTFREE=100,CRTOPT=,
//     CTLDSN='SYS2.TPL.V6.OBJLIB(CTLOBJ)',
//     CVTDSN='SYS2.TPL.V6.OBJLIB(CVTOBJ)',
//     FREE=100,
//     GENBLK=12960,GENLREC=80,GENRECF=FB,
//     LAYDSN='SYS2.TPL.V6.OBJLIB(LAYOBJ)',
//     LAYOPT=,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB',
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',
//     PHBLK=2560,PHLRECL=256,PHRECFM=FB,
//     QUOT=,      *'''',
//     SORTPGM=SORT,                                                 <<
//     TPLBLK=2976,
//     TPLOPT=,
//     TRNDSN='SYS2.TPL.V6.OBJLIB(TRNOBJ)'
//*********************************************************************
//*                                                                   *
//*     T P L C R P H O - - A PROCEDURE TO CREATE TABLES CONTAINING   *
//*          USER-SPECIFIED CELL VALUES AND LABELS, AND THEN PRODUCE  *
//*          A CALL FILE FOR PHOTOCOMPOSING THE TABLES.               *
//*                                                                   *
//*     THE FOLLOWING PARAMETERS ARE NECESSARY:                       *
//*          CALPRE - THE ACCOUNT NUMBER & INITIALS UNDER WHICH THE  *
//*                    CALL FILE IS STORED.                           *
//*          CALL    - THE NAME USED TO IDENTIFY THE CALL FILE.       *
//*                                                                   *
//*                                                                   *
//*     THE CREATE TABLE STATEMENTS COMPRISING THE REQUEST MUST BE    *
//*           PLACED AFTER A "//CREATE.TABLES" DD CARD.               *
//*                                                                   *
//*     ANY PCL STATEMENTS MODIFYING THE REQUEST, IF PRESENT, MUST    *
//*          BE PLACED AFTER A "//TPL.CHANGES" DD CARD, WHICH MUST    *
//*          APPEAR AFTER THE CREATE TABLE STATEMENTS.                *
//*                                                                   *
//*     THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.          *
//*                                                                   *
//*     IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.             *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TPLSPEC  DD  DSN=&&TPLSPEC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC1SP))
//COORD  DD  DSN=&&COORD,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//CELLS  DD  DSN=&&TBCELLS,UNIT=&CL3UN,DISP=(NEW,PASS),
//  DCB=(RECFM=&CL3RECF,LRECL=&CL3LREC,BLKSIZE=&TPLBLK),
//  SPACE=(&TPLBLK,(&CL3SP))
//COORD2  DD  DSN=*.COORD,VOL=REF=*.COORD,DISP=(SHR,PASS)
//CONSTR  DD  DSN=&&CONSTR,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN))
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//SORTWK01  DD  DSN=&&WORK1,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//PHOTOUT  DD  DSN=&PHOTDSN,DISP=(&PHOTTYP,PASS,&PHOTDEL),
//  UNIT=TSOWRK,
//  DCB=(RECFM=&PHRECFM,LRECL=&PHLRECL,BLKSIZE=&PHBLK),
//  LABEL=(&PHOTSEQ,&PHOTLAB),SPACE=(&PHBLK,(&PHSPACE,&PHINCR))
//CALLWRK  DD  DSN=&&CALLWRK,UNIT=&CLWKUN,DISP=(NEW,PASS,DELETE),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK),
//  SPACE=(&CALBLK,(&CALSP,&CALIN))
//CALLFILE  DD  DSN=&QUOT.&CALPRE..PCL.VIDEO.CALL.&CALNAME.&QUOT,
//  UNIT=&CALUNIT,
//  DISP=(&CALTYPE,&CALDISP,KEEP),LABEL=(&CALSEQ,&CALLAB),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK),
//  SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE)
//REQUEST  DD  DSN=&&REQUEST,DISP=(NEW,PASS),
//  DCB=(RECFM=&GENRECF,LRECL=&GENLREC,BLKSIZE=&GENBLK),
//  SPACE=(&GENBLK,(&REQSP,&REQIN)),UNIT=&REQUNIT
//CHANGES  DD  DSN=&&CHANGES,DISP=(NEW,PASS),
//  DCB=(RECFM=&GENRECF,LRECL=&GENLREC,BLKSIZE=&GENBLK),
//  SPACE=(&GENBLK,(&CHGSP,&CHGIN)),UNIT=&CHGUNIT
//*
//*
//CREATE  EXEC  PGM=&MONITOR,REGION=&CRTMAX,
//  PARM='FILE=&TPLBLK,FREE=&CRTFREE,MAX=&CRTCORE,&CRTOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     THE CREATE TABLE PHASE                            *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&CTLDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&REQUEST,DISP=(OLD,PASS),
//  SPACE=(&GENBLK,(&REQSP,&REQIN),RLSE)
//OUTPUT3  DD  DSN=&&CHANGES,DISP=(OLD,PASS),
//  SPACE=(&GENBLK,(&CHGSP,&CHGIN),RLSE)
//FILE6  DD  DSN=&&TBCELLS,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&CL3SP),RLSE),
//  DCB=(RECFM=&CL3RECF,LRECL=&CL3LREC,BLKSIZE=&TPLBLK)
//SYSIN  DD  DUMMY
//INPUT3  DD  DDNAME=TABLES
//TABLES  DD  DUMMY
//INPUT7  DD  DUMMY
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  PARM='FILE=&TPLBLK,FILE2=&CBKBLK,FREE=&FREE,MAX=&CORE,&TPLOPT',
//  COND=((0,LT,SYSCNTRL),(0,LT,CREATE))
//* *****     THE TPL TRANSLATION PHASE, FOLLOWED BY THE PCL    *******
//* *****     TRANSLATION PHASE.                                *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&TRNDSN,DISP=SHR
//  DD  DSN=&PCLDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&PCLSORT,UNIT=WORK,SPACE=(TRK,(1,1),RLSE),
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=400),DISP=(NEW,PASS)
//OUTPUT3  DD  DSN=&&CONSTR,DISP=(OLD,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN),RLSE)
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//FILE1  DD  DSN=&&TPLSPEC,DISP=(OLD,DELETE,KEEP),
//  SPACE=(&TPLBLK,(&SPC1SP),RLSE)
//FILE2  DD  DSN=&CBPREFX..CB.V&CBVER..&CBN,DISP=SHR
//FILE3  DD  DSN=&&COORD,DISP=(OLD,PASS,DELETE),
//  SPACE=(&TPLBLK,(&COORSP),RLSE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP),RLSE)
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//SYSIN  DD  DSN=&&REQUEST,DISP=(OLD,DELETE)
//INPUT3  DD  DSN=&&CHANGES,DISP=(OLD,DELETE)
//  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//INPUT4  DD  DUMMY
//INPUT7  DD  DUMMY
//*
//*
//PCLSORT  EXEC  PGM=&SORTPGM,REGION=&PSRCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,CREATE),(16,LE,TPL))
//* *****     THE PCL COORDINATE RECORD SORT STEP               *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&PCLSORT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTOUT  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,PASS)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,PASS)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,PASS)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,PASS)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,PASS)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,PASS)
//*
//*
//LAYOUT  EXEC  PGM=&MONITOR,REGION=&LAYMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&LAYCORE,&LAYOPT',
//  COND=((0,LT,SYSCNTRL),(0,LT,CREATE),(4,LE,TPL),(0,LT,PCLSORT))
//* *****     THE TABLE LAYOUT AND PRINTING STEP, AND THEN      *******
//* *****     THE PHOTOCOMPOSITION CONVERSION STEP.             *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,KEEP)
//PROGRAM  DD  DSN=&LAYDSN,DISP=SHR
//  DD  DSN=&CVTDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING,DCB=BLKSIZE=133
//SYSPUNCH  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  DSN=&PHOTDSN,DISP=(OLD,&PHOTDEL,&PHOTDEL),
//  LABEL=(&PHOTSEQ,&PHOTLAB),SPACE=(&PHBLK,(&PHSPACE,&PHINCR),RLSE),
//  DCB=(RECFM=&PHRECFM,LRECL=&PHLRECL,BLKSIZE=&PHBLK)
//INPUT3  DD  DUMMY
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,DELETE,DELETE)
//FILE6  DD  DSN=&&TBCELLS,UNIT=&CL3UN,DISP=(OLD,DELETE,KEEP)
//INPUT5  DD  DSN=&TSLDSN(&STYLE.CON),DISP=(SHR,KEEP),
//  DD  DSN=&TSLDSN(&STYLE.TYPE),DISP=(SHR,KEEP),
//INPUT4  DD  DSN=*.OUTPUT4,DISP=(OLD,&PHOTDEL,&PHOTDEL),
//  LABEL=(&PHOTSEQ,&PHOTLAB),
//  VOL=REF=*.OUTPUT4
//OUTPUT3  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT5  DD  DSN=&&CALLWRK,DISP=(OLD,PASS,DELETE),
//  SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK)
//INPUT2  DD  DDNAME=LIMIT
//LIMIT  DD  DUMMY
//INPUT7  DD  DUMMY
//OUTPUT6  DD  DSN=&&CALLSRT,UNIT=WORK,DISP=(NEW,PASS,DELETE),
//  SPACE=(TRK,(1,1)),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//SYSIN  DD  DUMMY
//COORD  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//*
//*
//CALLSORT  EXEC  PGM=&SORTPGM,REGION=&CSRCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,CREATE),(4,LE,TPL),(0,LT,PCLSORT),
//        (0,LT,LAYOUT))
//* *****     SORT THE CALLFILE USED BY THE TPLCRTP PROCEDURE   *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&CALLSRT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&CALLWRK,
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK),
//  DISP=(OLD,DELETE,DELETE)
//SORTOUT  DD  DSN=&QUOT.&CALPRE..PCL.VIDEO.CALL.&CALNAME.&QUOT,
//  DISP=(&CALMOD,KEEP,KEEP),
//* LABEL=(&CALSEQ,&CALLAB,&EXPRET=&RETPD),                          <<
//  SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE,DELETE)
//*     END OF PROCEDURE TO CREATE TABLES AND PRODUCE
//*          A CALL FILE FOR PHOTOCOMPOSING THE TABLES.
//*  PEND

TPLCRTP.text:
//TPLCRTP  PROC  CALPRE='USERX',     INITIALS PREFIX FOR CALL FILE   <<
//     CALNAME=,        NAME OF CALL FILE
//     CALSEQ=1,        DATASET SEQUENCE # FOR CALL FILE
//     CALLAB=SL,       LABEL TYPE FOR CALL FILE
//     LISTING='A',     SYSOUT SPECIFICATION FOR ALL LISTINGS        <<
//     CORE=150K,       REGION IN WHICH TO RUN
//     DRVPRE=,         ACCOUNT & INITIALS PREFIX FOR DRIVER TAPE
//     DRVNAME=,        NAME ASSIGNED TO DRIVER TAPE
//     DRVTYPE=NEW,     STATUS OF DRIVER TAPE
//     DRVDISP=CATLG,   TO PERMIT CATALOGING OF DRIVER TAPE
//     DRVBOMB=KEEP,    DRIVER TAPE DISPOSITION IF ABEND OCCURS
//     DRVUNIT=TAPE,  UNIT TO BE USED FOR DRIVER TAPE              <<
//     DRVLAB=SL,       LABEL TYPE FOR DRIVER TAPE
//     DRVSEQ=1,        SEQUENCE NUMBER ON DRIVER TAPE
//     EXPRET='RETPD',  CHOICE OF 'EXPDT' OR 'RETPD' FOR DRIVER TAPE
//     RETPD=00,       RETENTION PERIOD FOR DRIVER TAPE             <<
//     DRVRECF=FB,      RECORD FORMAT FOR DRIVER TAPE
//     DRVLREC=200,     RECORD LENGTH FOR DRIVER TAPE
//     DRVBLK=200,      BLOCKSIZE FOR DRIVER TAPE
//     DRVSP=2000,      INITIAL # BLKS (IF DISK INSTEAD OF TAPE)
//     DRVIN=500,       SECONDARY # BLKS (IF DISK INSTEAD OF TAPE)
//     PHCLIB='SYS2.TPL.V6.LOADLIB',
//     QUOT=,       *'''',
//     TAPEMBR=CREATAPE,
//     VDIDSN='SYS2.TPL.V6.PCL.VIDEO.DIRECT'
//*********************************************************************
//*                                                                   *
//*      T P L C R T P - -  A PROCEDURE FOR RETRIEVING ONE (OR MORE)  *
//*           STORED PHOTOCOMPOSITION CALL FILE(S) AND CREATING A     *
//*           VIDEOCOMP DRIVER TAPE.                                  *
//*                                                                   *
//*        THE FOLLOWING PARAMETERS ARE NECESSARY:                    *
//*                                                                   *
//*        CALPRE='CNaaaa.iii',CALNAME='file name',                   *
//*        DRVPRE='CNbbbb.jjj',DRVNAME='tape name'                    *
//*                                                                   *
//*        WHERE:                                                     *
//*           CALPRE = THE ACCOUNT NUMBER & INITIALS UNDER WHICH THE  *
//*                        CALL FILE IS STORED.                       *
//*           CALNAME = THE NAME USED TO IDENTIFY THE CALL FILE.      *
//*           DRVPRE = THE ACCOUNT NUMBER & INITIALS UNDER WHICH THE  *
//*                        CREATED DRIVER TAPE IS STORED.             *
//*           DRVNAME = THE NAME USED TO IDENTIFY THE DRIVER TAPE.    *
//*                                                                   *
//*                                                                   *
//*                                                                   *
//*        IF MULTIPLE CALL FILES ARE BEING COMBINED FOR PRODUCING    *
//*           A SINGLE DRIVER TAPE, THEN A SEPARATE "DD" CARD FOR     *
//*           EACH CALL FILE MUST BE WRITTEN, AS FOLLOWS:             *
//*            //DRIVER.CALLFILE  DD  (DESCRIPTION OF 1ST CALL FILE)  *
//*            //  DD  (DESCRIPTION OF 2ND CALL FILE)                 *
//*               .                                                   *
//*               .                                                   *
//*               .                                                   *
//*            //  DD  (DESCRIPTION OF LAST CALL FILE)                *
//*                                                                   *
//*    THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.           *
//*                                                                   *
//*    IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.              *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//DRIVER  EXEC  PGM=&TAPEMBR,REGION=&CORE
//* *****     CREATE VIDEOCOMP DRIVER TAPE                      *******
//STEPLIB  DD  DSN=&PHCLIB,
//  DISP=SHR
//FT03F001  DD  SYSOUT=&LISTING
//FT04F001  DD  DUMMY
//FT05F001  DD  DDNAME=CALLFILE
//CALLFILE  DD  DSN=&QUOT.&CALPRE..PCL.VIDEO.CALL.&CALNAME.&QUOT,
//  LABEL=(&CALSEQ,&CALLAB,,IN),
//  DISP=(OLD,KEEP,KEEP)
//FT06F001  DD  SYSOUT=&LISTING
//FT10F001  DD  DSN=&QUOT.&DRVPRE..PCL.VIDEO.TAPE.&DRVNAME.&QUOT,
//  LABEL=(&DRVSEQ,&DRVLAB,,OUT,&EXPRET=&RETPD),
//  UNIT=(&DRVUNIT,,DEFER),SPACE=(&DRVBLK,(&DRVSP,&DRVIN),RLSE),
//  DCB=(DEN=2,BUFNO=2,DSORG=PS,RECFM=&DRVRECF,LRECL=&DRVLREC,
//  BLKSIZE=&DRVBLK),DISP=(&DRVTYPE,&DRVDISP,&DRVBOMB)
//FT12F001  DD  DSN=&VDIDSN,UNIT=&VDIUNIT,VOL=SER=&VDIVOL,DISP=SHR,
//  LABEL=(,,,IN)
//FT13F001  DD  DUMMY
//FT14F001  DD  DUMMY
//FT15F001  DD  SYSOUT=&LISTING
//*      END OF PROCEDURE TO CREATE A VIDEOCOMP DRIVER TAPE FROM
//*           A PREVIOUSLY-CREATED TPL CALL FILE.
//*  PEND

TPLDICT.text:
//TPLDICT  PROC  DICNAME='???', NAME OF DICTIONARY
//     DICPRE='???', PREFIX FOR STORING DICTIONARY
//     DICSP=80,     NO. OF BLOCKS FOR DICTIONARY
//     DICINCR=20,   SECONDARY SPACE ALLOCATION SIZE FOR DICTIONARY
//     DICTYPE=NEW,  INITIAL STATUS OF DICTIONARY
//     DICDISP=CATLG, O ALLOW CATALOGING OF DICTIONARY
//     ERRORS=4,     WRITE A DICTIONARY IF ONLY MILD ERRORS
//     LISTING='*',  SYSOUT SPECIFICATION FOR ALL LISTINGS           <<
//     CORE=500K,    REGION USED FOR PROGRAMS OF DICT & WRITER STEPS
//     DICMAX=2000K, MAX. REGION USED DURING DICT, SORT, & WRITER STEPS
//     CPYCORE=150K, REGION FOR COPYING PROGRAM
//     INFOSP=800,   NO. OF BLOCKS FOR INFORMATION WORKFILE
//     NAMESP=400,   NO. OF BLOCKS FOR NAME INDEX WORKFILE
//     NAMEIN=40,    SECONDARY SPACE ALLOCATION SIZE FOR NAME INDEX
//     TDICSP=1200,  NO. OF BLOCKS FOR TEMPORARY DICTIONARY
//     SORTSP=40,    NO. OF BLOCKS FOR EACH OF 6 SORT WORKFILES
//     TMPSP=100,    NO. OF BLOCKS FOR "TEMP" WORKFILES
//     DICUNIT='TSOWRK', VICE TYPE FOR DICTIONARY                    <<
//     CONTROL=IEFBR14,CONMAX=100K,
//     COPYPGM=IEBGENER,
//     DCTDSN='SYS2.TPL.V6.OBJLIB(DCTOBJ)',
//     DCTOPT=,
//     DCWDSN='SYS2.TPL.V6.OBJLIB(DCWOBJ)',
//     DCWOPT=,
//     DICBLK=2976,DICLREC=2976,DICRECF=F,
//     FREE=100,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB',
//     NAMBLK=3500,NAMLREC=35,NAMRECF=FB,
//     SORTPGM=SORT                                                  <<
//*********************************************************************
//*                                                                   *
//*      T P L D I C T - - A PROCEDURE TO ESTABLISH A DICTIONARY      *
//*         WHICH SPECIFIES STANDARD HYPHENATIONS FOR THE TPL SYSTEM. *
//*                                                                   *
//*      VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:        *
//*                WHERE,                                             *
//*           DICPRE     -- SPECIFIES THE PREFIX ACCOUNT & INITIALS   *
//*                          FOR THE DICTIONARY'S DATASET NAME.       *
//*           DICNAME    -- SPECIFIES THE NAME IDENTIFYING THE        *
//*                          DICTIONARY.                              *
//*                                                                   *
//*      THE SOURCE DICTIONARY STATEMENTS MUST BE PLACED AFTER A      *
//*        "//DICT.WORDS"  DD CARD.                                   *
//*                                                                   *
//*      IF THE DICTIONARY ALREADY EXISTS, AND IS BEING UPDATED,      *
//*         THEN ADD THE FOLLOWING PARAMETER:                         *
//*            DICTYPE=OLD                                            *
//*                                                                   *
//*      IF THE DICTIONARY IS NOT TO BE PLACED ON A "3380" DISK,      *
//*          THEN ADD THE FOLLOWING PARAMETER:                        *
//*             DICUNIT='device-type'                                 *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//DICOBJ  DD  DSN=&&DICOBJ,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&DICBLK,(&TDICSP))
//SORTWK01  DD  DSN=&&WORK1,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&SORTSP),,CONTIG)
//INFO  DD  DSN=&&INFOBLK,DISP=(NEW,PASS),SPACE=(&DICBLK,(&INFOSP)),
//  DCB=(RECFM=&DICRECF,LRECL=&DICLREC,BLKSIZE=&DICBLK),UNIT=WORK
//NAMES  DD  DSN=&&NAMEREC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&NAMBLK,(&NAMESP,&NAMEIN)),
//  DCB=(RECFM=&NAMRECF,LRECL=&NAMLREC,BLKSIZE=&NAMBLK)
//OBJECT  DD  DSN=&DICPRE..&DICNAME..DICOBJ,UNIT=&DICUNIT,
//  DCB=(RECFM=&DICRECF,LRECL=&DICLREC,BLKSIZE=&DICBLK),
//  DISP=(&DICTYPE,&DICDISP),SPACE=(&DICBLK,(&DICSP,&DICINCR))
//*
//*
//DICT  EXEC  PGM=&MONITOR,REGION=&DICMAX,
//  PARM='FREE=&FREE,FILE=&DICBLK,MAX=&CORE,&DCTOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****   READ THE SOURCE DICTIONARY AND BEGIN THE ANALYSIS   *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&DCTDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&SORTCTL,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//OUTPUT3  DD  DSN=&&NAMEREC,DISP=(OLD,PASS),
//  SPACE=(&NAMBLK,(&NAMESP,&NAMEIN),RLSE)
//FILE1  DD  DSN=&&TEMP1,DISP=(NEW,DELETE,DELETE),UNIT=WORK,
//  SPACE=(&DICBLK,(&TMPSP))
//FILE2  DD  DSN=&&INFOBLK,DISP=(OLD,PASS),
//  SPACE=(&DICBLK,(&INFOSP),RLSE)
//SYSIN  DD  DUMMY
//INPUT3  DD  DDNAME=WORDS
//WORDS  DD  DUMMY
//INPUT7  DD  DUMMY
//*
//*
//SORT  EXEC  PGM=&SORTPGM,REGION=&DICMAX,
//  COND=((0,LT,SYSCNTRL),(0,LT,DICT))
//* *****     SORT THE NAME RECORDS INTO ALPHABETIC SEQUENCE    *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&SORTCTL,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&NAMEREC,DISP=(OLD,PASS)
//SORTOUT  DD  DSN=*.SORTIN,VOL=REF=*.SORTIN,DISP=(OLD,PASS),
//  SPACE=(&NAMBLK,(&NAMESP,&NAMEIN),RLSE)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE)
//*
//*
//WRITER  EXEC  PGM=&MONITOR,REGION=&DICMAX,
//  PARM='FREE=&FREE,FILE=&DICBLK,MAX=&CORE,&DCWOPT',
//  COND=((0,LT,SYSCNTRL),(0,LT,DICT),(0,LT,SORT))
//* *****     COMPLETE THE ANALYSIS AND WRITE A TEMPORARY       *******
//* *****      OBJECT DICTIONARY                                *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&DCWDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//FILE1  DD  DSN=&&DICOBJ,DISP=(OLD,PASS,KEEP),
//  SPACE=(&DICBLK,(&TDICSP),RLSE)
//FILE2  DD  DSN=&&INFOBLK,DISP=(OLD,DELETE,DELETE)
//FILE3  DD  DSN=&&TEMP2,UNIT=WORK,DISP=(NEW,DELETE,DELETE),
//  SPACE=(&DICBLK,(&TMPSP))
//SYSIN  DD  DUMMY
//INPUT3  DD  DSN=&&NAMEREC,DISP=(OLD,DELETE)
//*
//*
//COPY  EXEC  PGM=&COPYPGM,REGION=&CPYCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,DICT),(0,LT,SORT),
//        (&ERRORS,LT,WRITER))
//* *****   COPY THE TEMPORARY DICTIONARY ONTO ITS              *******
//* *****          PERMANENT PLACE                              *******
//SYSUT1  DD  DSN=&&DICOBJ,DISP=(OLD,DELETE,DELETE)
//SYSUT2  DD  DSN=&DICPRE..&DICNAME..DICOBJ,
//  DCB=(RECFM=&DICRECF,LRECL=&DICLREC,BLKSIZE=&DICBLK),UNIT=&DICUNIT,
//  DISP=(OLD,KEEP),SPACE=(&DICBLK,(&DICSP,&DICINCR),RLSE)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*           END OF PROCEDURE FOR ESTABLISHING A DICTIONARY
//*                SPECIFYING HYPHENATIONS FOR THE TPL SYSTEM.
//* PEND

TPLERCHK.text:
//TPLERCHK  PROC  CBN='?', NAME FOR CODEBOOK OR LIBRARY
//     CBPREFX='SYS2.TPL.V6', INITIALS FOR CODEBOOK OR LIBRARY       <<
//     DATA=NULLFILE, NAME OF DATA FILE TO BE TABULATED
//     SEQ=1,         FILE SEQUENCE NO. ON ITS VOL. FOR DATA FILE
//     LAB=SL,        TYPE OF LABELS ON ITS VOL. FOR DATA FILE
//     DBGDSN='SYS2.TPL.V6.LOADLIB',     NAME FOR DATABASE "DBGEN"   <<
//     DBMS=NONE,     NAME OF DATABASE MANAGEMENT SYSTEM BEING USED  <<
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     TABLES='A',    SYSOUT SPECIFICATION IF TABLES ARE SEPARATED   <<
//     CORE=500K,     REGION ACTUALLY USED FOR PROGRAMS OF TPL STEP
//     TPLMAX=2000K,  MAXIMUM REGION USED DURING TPL STEP
//     PSRCORE=500K,  REGION SIZE FOR PCLSORT STEP
//     LAYCORE=500K,  REGION ACTUALLY USED FOR PROGRAMS OF LAYOUT STEP
//     LAYMAX=2000K,  MAXIMUM REGION USED DURING LAYOUT STEP
//     WKUN=WORK,    DEVICE TYPE FOR SORT WORKFILES
//     WKSP=1600,     NO. OF BLKS FOR SORT WORKFILES
//     COORSP=1000,   NO. OF BLKS FOR COORDINATES WORKFILE
//     CONSP=400,     NO. OF BLKS FOR TABLE CONSTRUCTIONS WORKFILE
//     CONIN=80,      SECONDARY ALLOCATION SIZE FOR TABLE CONSTRUC.
//     SPC1SP=200,    INITIAL NO. OF BLKS FOR TABLE SPECS WORKFILE
//     SPC2SP=200,    NO. OF BLKS FOR PCL'S NEW TBSPECS WORKFILE
//     TMPSP=125,     NO OF BLKS FOR "TEMP" WORKFILE
//     CL3UN=WORK,   DEVICE TYPE FOR "TBCELLS" WORKFILE
//     CL3SP=4000,    NO OF BLKS FOR "TBCELLS" WORKFILE
//     TSLDSN='SYS2.TPL.V6.TYPE.STYLES', NAME OF TYPE STYLE LIBRARY<<
//     STYLE=NOPH,  WAS=SET1, NAME OF DEFAULT TYPE STYLE        <<
//     DICNAME='BLS.HYPHEN.DICT',  NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CBKBLK=2976,CBVER=6,
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONBLK=3059,CONLREC=133,CONRECF=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     DATFREE=100,
//     DSPDSN='SYS2.TPL.V6.OBJLIB(DSPOBJ)',
//     FREE=100,
//     LAYDSN='SYS2.TPL.V6.OBJLIB(LAYOBJ)',
//     LAYOPT=,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB',
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',
//     POTDSN='SYS2.TPL.V6.OBJLIB(POTOBJ)',
//     QUOT='''',
//     RAPDSN='NULLFILE', <<
//     SISDSN='SYS2.TPL.V6.OBJLIB(SISOBJ)',
//     SORTPGM=SORT,                                                 <<
//     TOTDSN='NULLFILE', <<
//     TPLBLK=2976,
//     TPLOPT=,
//     TRNDSN='SYS2.TPL.V6.OBJLIB(TRNOBJ)'
//*********************************************************************
//*                                                                   *
//*      T P L E R C H K - - A PROCEDURE TO CHECK TPL STATEMENTS AND  *
//*           OPTIONAL ASSOCIATED PCL STATEMENTS FOR ERRORS, AND ALSO *
//*           DISPLAY THE RESULTANT TABLE STRUCTURES.                 *
//*                                                                   *
//*      VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:        *
//*        CBN='cccccccc',CBPREFX='CNaaaa.iii'                        *
//*                                                                   *
//*      THE TPL STATEMENTS COMPRISING THE REQUEST MUST BE PLACED     *
//*           AFTER A "//TPL.REQUEST" DD CARD.                        *
//*                                                                   *
//*      ANY PCL STATEMENTS MODIFYING THE REQUEST, IF PRESENT, MUST   *
//*           BE PLACED AFTER A "//TPL.CHANGES" DD CARD, WHICH MUST   *
//*           APPEAR AFTER THE TPL REQUEST.                           *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TPLSPEC  DD  DSN=&&TPLSPEC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC1SP))
//COORD  DD  DSN=&&COORD,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//COORD2  DD  DSN=*.COORD,VOL=REF=*.COORD,DISP=(SHR,PASS)
//CONSTR  DD  DSN=&&CONSTR,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN))
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//SORTWK01  DD  DSN=&&WORK1,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//PROFILE  DD  DSN=&PRODSN,DISP=SHR
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  COND=(0,LT,SYSCNTRL),
//  PARM='FILE=&TPLBLK,FILE2=&CBKBLK,FREE=&DATFREE,MAX=&CORE,&TPLOPT'
//* *****     THE FIRST STEP:  THE TPL TRANSLATION PHASE, THE   *******
//* *****     PCL TRANSLATION PHASE, THE POST-TRANSLATION PHASE,*******
//* *****     AND THE DISPATCHER PHASE.                         *******
//STEPLIB  DD  DSN=&DBGDSN,DISP=SHR
//  DD  DDNAME=&DBMS.LIB
//  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//TOTALLIB  DD  DSN=&TOTDSN,DISP=SHR
//RAPIDLIB  DD  DSN=&RAPDSN,DISP=SHR
//NONELIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&TRNDSN,DISP=SHR
//  DD  DSN=&PCLDSN,DISP=SHR
//  DD  DSN=&POTDSN,DISP=SHR
//  DD  DSN=&DSPDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT3  DD  DSN=&&CONSTR,DISP=(OLD,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN),RLSE)
//FILE1  DD  DSN=&&TPLSPEC,DISP=(OLD,DELETE,KEEP),
//  SPACE=(&TPLBLK,(&SPC1SP),RLSE)
//FILE2  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=SHR
//INPUT2  DD  DDNAME=DATA
//DATA  DD  DUMMY,DSN=&QUOT.&DATA.&QUOT,UNIT=WORK,
//  LABEL=(&SEQ,&LAB),DISP=SHR
//*      NOTE:  DATA IS A DUMMY DATASET TO TAKE CARE OF
//*      UNNECESSARY PARAMETERS ON THE "PROC" STATEMENT.
//SYSIN  DD  DDNAME=REQUEST
//REQUEST  DD  DUMMY
//INPUT4  DD  DUMMY
//INPUT3  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//SYSPUNCH  DD  DSN=&&PCLSORT,UNIT=WORK,SPACE=(TRK,(1,1)),
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=400),DISP=(NEW,PASS)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,PASS,DELETE),
//  SPACE=(&TPLBLK,(&COORSP),RLSE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS)
//FILE5  DD  DSN=&&TEMP,DISP=(NEW,DELETE,DELETE),UNIT=WORK,
//  SPACE=(&TPLBLK,(&TMPSP),RLSE)
//OUTPUT6  DD  DSN=&&TPLSTR,UNIT=WORK,DISP=(NEW,PASS,DELETE),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=133,BLKSIZE=3059)
//INPUT7  DD  DDNAME=INSWITCH
//INSWITCH  DD  DUMMY
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//RAPSYSP  DD  SYSOUT=&LISTING
//*
//*
//PCLSORT  EXEC  PGM=&SORTPGM,REGION=&PSRCORE,
//  COND=((0,LT,SYSCNTRL),(16,LE,TPL))
//* *****     THE PCL COORDINATE RECORD SORT STEP               *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&PCLSORT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTOUT  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE,DELETE)
//*
//*
//TAYOUT  EXEC  PGM=&MONITOR,REGION=&LAYMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&LAYCORE,&LAYOPT',
//  COND=((0,LT,SYSCNTRL),(16,LE,TPL),(0,LT,PCLSORT))
//* *****     THE TABLE LAYOUT STEP                             *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&SISDSN,DISP=SHR
//  DD  DSN=&LAYDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT7  DD  SYSOUT=&TABLES,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//SYSPUNCH  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//INPUT5  DD  DSN=&TSLDSN(&STYLE.CON),DISP=(SHR,KEEP)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS,KEEP)
//FILE6  DD  DSN=&&TBCELLS,UNIT=&CL3UN,DISP=(NEW,PASS,KEEP),
//  SPACE=(&TPLBLK,(&CL3SP),RLSE)
//INPUT7  DD  DUMMY
//SYSIN  DD  DUMMY
//COORD  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//*      END OF PROCEDURE FOR CHECKING TPL AND PCL STATEMENTS FOR
//*           ERRORS, AND DISPLAYING THE TABLE STRUCTURES.
//*  PEND

TPLGENTB.text:
//TPLGENTB  PROC  CBN='?', NAME FOR CODEBOOK OR LIBRARY
//     CBPREFX=,      ACCT. & INITIALS PREFIX FOR CODEBOOK OR LIBRARY<<
//     DATA=NULLFILE, NAME OF DATA FILE TO BE TABULATED
//     SEQ=1,         FILE SEQUENCE NO. ON ITS VOL. FOR DATA FILE
//     LAB=SL,        TYPE OF LABELS ON ITS VOL. FOR DATA FILE
//     DBGDSN='SYS2.TPL.V6.LOADLIB', NAME FOR DATABASE "DBGEN"       <<
//     DBMS=NONE,     NAME OF DATABASE MANAGEMENT SYSTEM BEING USED  <<
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     TABLES='A',    SYSOUT SPECIFICATION IF TABLES ARE SEPARATED   <<
//     CORE=500K,     REGION ACTUALLY USED FOR PGMS OF TPL - SORT STEPS
//     TPLMAX=2000K,  MAXIMUM REGION USED DURING TPL THRU SORT STEPS
//     PSRCORE=500K,  REGION SIZE FOR PCLSORT STEP
//     LAYCORE=500K,  REGION ACTUALLY USED FOR PROGRAMS OF LAYOUT STEP
//     LAYMAX=2000K,  MAXIMUM REGION USED DURING LAYOUT STEP
//     WKUN=WORK,    DEVICE TYPE FOR SORT WORKFILES
//     WKSP=1600,     NUMBER OF BLOCKS FOR SORT WORKFILES
//     CL1UN=WORK,   DEVICE TYPE FOR "TBCLS1" WORKFILE
//     CL1SP=4000,    NUMBER OF BLOCKS FOR "TBCLS1" WORKFILE
//     CL2UN=WORK,   DEVICE TYPE FOR "TBCLS2" WORKFILE
//     CL2SP=200,     NUMBER OF BLOCKS FOR "TBCLS2" WORKFILE
//     CL2IN=250,     NUMBER OF BLOCKS SECONDARY ALLOC. FOR "TBCLS2"
//     CL3UN=WORK,   DEVICE TYPE FOR TABLE CELLS WORKFILE
//     CL3SP=4000,    NUMBER OF BLOCKS FOR TABLE CELLS WORKFILE
//     COORSP=400,    NUMBER OF BLOCKS FOR COORDINATES WORKFILE
//     CONSP=400,     NUMBER OF BLOCKS FOR TABLE CONSTRUCTIONS WORKFILE
//     CONIN=80,      SECONDARY ALLOCATION SIZE FOR TABLE CONSTRUC.
//     SPC1SP=200,    INITIAL NUMBER OF BLOCKS FOR TABLE SPECS WORKFILE
//     SPC2SP=200,    NUMBER OF BLOCKS FOR PCL'S NEW TBSPECS WORKFILE
//     TMPSP=125,     NUMBER OF BLOCKS FOR "TEMP" WORKFILE
//     TSLDSN='SYS2.TPL.V6.TYPE.STYLES', NAME OF TYPE STYLE LIBRARY<<
//     STYLE=NOPH,  WAS=SET1, NAME OF DEFAULT TYPE STYLE <<
//     DICNAME='BLS.HYPHEN.DICT',  NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CBKBLK=2976,CBVER=6,
//     CGNDSN='SYS2.TPL.V6.OBJLIB(CGNOBJ)',
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONBLK=3059,CONLREC=133,CONRECF=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     DATFREE=100,
//     DSPDSN='SYS2.TPL.V6.OBJLIB(DSPOBJ)',
//     FREE=100,
//     LAYDSN='SYS2.TPL.V6.OBJLIB(LAYOBJ)',
//     LAYOPT=,
//     MODLIB='SYS2.TPL.V6.LOADLIB', <<
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB', <<
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',
//     POTDSN='SYS2.TPL.V6.OBJLIB(POTOBJ)',
//     QUOT='''',
//     RAPDSN='NULLFILE', <<
//     SISDSN='SYS2.TPL.V6.OBJLIB(SISOBJ)',         <<
//     SORTPGM=SORT,                                                 <<
//     SRTLREC=32,SRTBLK=2976,
//     SUBDSN='SYS2.TPL.V6.OBJLIB(SUBOBJ)',
//     SUBOPT=,
//     TOTDSN='NULLFILE', <<
//     TPLBLK=2976,
//     TPLOPT=,
//     TRNDSN='SYS2.TPL.V6.OBJLIB(TRNOBJ)'
//*********************************************************************
//*                                                                   *
//*      T P L G E N T B - - A PROCEDURE TO GENERATE TABLES WITH TPL. *
//*           DIRECT-ACCESS DEVICES (DISKS) WILL BE USED FOR THE      *
//*           MAJOR WORKFILES.                                        *
//*                                                                   *
//*      VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:        *
//*      CBN='cccccccc',DATA='zzz',                                   *
//*      CBPREFX='CNaaaa.iii'                                         *
//*                                                                   *
//*      THE TPL STATEMENTS COMPRISING THE REQUEST MUST BE PLACED     *
//*           AFTER A "//TPL.REQUEST" DD CARD.                        *
//*                                                                   *
//*      ANY PCL STATEMENTS MODIFYING THE REQUEST, IF PRESENT, MUST   *
//*           BE PLACED AFTER A "//TPL.CHANGES" DD CARD, WHICH MUST   *
//*           APPEAR AFTER THE TPL REQUEST.                           *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TPLSPEC  DD  DSN=&&TPLSPEC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC1SP))
//TBCLS1  DD  DSN=&&TBCLS1,DISP=(NEW,PASS,DELETE),
//  UNIT=&CL1UN,SPACE=(&TPLBLK,(&CL1SP))
//TBCLS12  DD  DSN=*.TBCLS1,VOL=REF=*.TBCLS1,DISP=(SHR,PASS)
//TBCLS2  DD  DSN=&&TBCLS2,DISP=(NEW,PASS,DELETE),
//  UNIT=&CL2UN,SPACE=(&TPLBLK,(&CL2SP,&CL2IN))
//COORD  DD  DSN=&&COORD,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//COORD2  DD  DSN=*.COORD,VOL=REF=*.COORD,DISP=(SHR,PASS)
//CONSTR  DD  DSN=&&CONSTR,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN))
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//SORTWK01  DD  DSN=&&WORK1,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//PROFILE  DD  DSN=&PRODSN,DISP=SHR
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  COND=(0,LT,SYSCNTRL),
//  PARM='FILE=&TPLBLK,FILE2=&CBKBLK,FREE=&DATFREE,MAX=&CORE,&TPLOPT'
//* *****     THE FIRST STEP:  THE TPL TRANSLATION PHASE, THE   *******
//* *****     PCL TRANSLATION PHASE, THE POST-TRANSLATION       *******
//* *****     PHASE, THE DISPATCHER PHASE, AND THE CELL         *******
//* *****     GENERATION PHASE.                                 *******
//STEPLIB  DD  DSN=&DBGDSN,DISP=SHR
//  DD  DDNAME=&DBMS.LIB
//  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//TOTALLIB  DD  DSN=&TOTDSN,DISP=SHR
//RAPIDLIB  DD  DSN=&RAPDSN,DISP=SHR
//NONELIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&TRNDSN,DISP=SHR
//  DD  DSN=&PCLDSN,DISP=SHR
//  DD  DSN=&POTDSN,DISP=SHR
//  DD  DSN=&DSPDSN,DISP=SHR
//  DD  DSN=&CGNDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT3  DD  DSN=&&CONSTR,DISP=(OLD,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN),RLSE)
//FILE1  DD  DSN=&&TPLSPEC,DISP=(OLD,DELETE,KEEP),
//  SPACE=(&TPLBLK,(&SPC1SP),RLSE)
//FILE2  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=SHR
//*INPUT2  DD  DDNAME=DATA
//*DATA  DD  DSN=&QUOT.&DATA.&QUOT,UNIT=&DATADEV,
//*  LABEL=(&SEQ,&LAB),DISP=SHR
//INPUT2  DD  DDNAME=DATA
//DATA  DD  DSN=&QUOT.&DATA.&QUOT,,
//  LABEL=(&SEQ,&LAB),DISP=SHR
//SYSIN  DD  DDNAME=REQUEST
//REQUEST  DD  DUMMY
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS)
//FILE5  DD  DSN=&&TEMP,UNIT=WORK,SPACE=(&TPLBLK,(&TMPSP)),
//  DISP=(NEW,DELETE)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,PASS),SPACE=(&TPLBLK,(&COORSP),RLSE)
//SYSPUNCH  DD  DSN=&&PCLSORT,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//INPUT4  DD  DUMMY
//INPUT3  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//OUTPUT6  DD  DSN=&&TPLSTR,UNIT=WORK,DISP=(NEW,PASS,DELETE),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=133,BLKSIZE=3059)
//OUTPUT4  DD  DUMMY
//INPUT7  DD  DDNAME=INSWITCH
//INSWITCH  DD  DUMMY
//FILE6  DD  DSN=&&TBCLS1,DISP=(OLD,PASS)
//OUTPUT5  DD  DSN=&&SORTCTL,UNIT=WORK,SPACE=(TRK,(1,1),RLSE),
//  DISP=(NEW,PASS),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//FILE7  DD  DSN=*.FILE5,VOL=REF=*.FILE5,DISP=(SHR,DELETE)
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//RAPSYSP  DD  SYSOUT=&LISTING
//*
//*
//PRESORT  EXEC  PGM=&SORTPGM,REGION=&TPLMAX,
//  COND=((0,LT,SYSCNTRL),(0,NE,TPL))
//* *****     THE SORT OF THE TABLE CELL INFORMATION GENERATED  *******
//* *****     DIRECTLY FROM THE DATA FILE.                      *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//MODLIB  DD  DSN=&MODLIB,DISP=SHR
//SYSIN  DD  DSN=&&SORTCTL,DISP=(OLD,PASS,KEEP)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&TBCLS1,DISP=(OLD,PASS),
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTOUT  DD  DSN=&&TBCLS2,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&CL2SP,&CL2IN),RLSE),
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,PASS,KEEP)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,PASS,KEEP)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,PASS,KEEP)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,PASS,KEEP)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,PASS,KEEP)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,PASS,KEEP)
//*
//*
//SUBCELL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&CORE,&SUBOPT',
//  COND=((0,LT,SYSCNTRL),(4,LT,TPL),(0,LT,PRESORT))
//* *****     GENERATE CELL INFORMATION DERIVABLE FROM OTHER    *******
//* *****     CELLS IN THE CURRENT REQUEST.                     *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&SUBDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS)
//FILE6  DD  DSN=&&TBCLS1,DISP=(OLD,PASS),SPACE=(&TPLBLK,(&CL1SP),RLSE)
//FILE7  DD  DSN=&&TBCLS2,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&CL2SP,&CL2IN),RLSE)
//OUTPUT5  DD  DSN=&&SORTCTL,UNIT=SYSDA,SPACE=(TRK,(1,1)),
//  DISP=(OLD,PASS),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//INPUT7  DD  DUMMY
//*
//*
//PCLSORT  EXEC  PGM=&SORTPGM,REGION=&PSRCORE,
//  COND=((0,LT,SYSCNTRL),(16,LE,TPL),(0,LT,PRESORT))
//* *****     THE PCL COORDINATE RECORD SORT STEP               *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&PCLSORT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTOUT  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,PASS,KEEP)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,PASS,KEEP)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,PASS,KEEP)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,PASS,KEEP)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,PASS,KEEP)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,PASS,KEEP)
//*
//*
//SORT  EXEC  PGM=&SORTPGM,REGION=&TPLMAX,
//  COND=((0,LT,SYSCNTRL),(4,LT,TPL),(4,LT,SUBCELL),(0,LT,PRESORT),
//  (0,LT,PCLSORT))
//* *****     THE TABLE CELL SORT AND DATA REDUCTION STEP       *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//MODLIB  DD  DSN=&MODLIB,DISP=SHR
//SYSIN  DD  DSN=&&SORTCTL,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&TBCLS1,DISP=(OLD,PASS),
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTOUT  DD  DSN=&&TBCLS1,DISP=(OLD,PASS,DELETE),
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE,DELETE)
//*
//*
//LAYOUT  EXEC  PGM=&MONITOR,REGION=&LAYMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&LAYCORE,&LAYOPT',
//  COND=((0,LT,SYSCNTRL),(16,LE,TPL),(0,LT,PRESORT),(0,LT,SORT),
//  (0,LT,PCLSORT))
//* *****     FINISH PREPARING TABLE CELLS AND DO POSTCOMPUTES, *******
//* *****     THEN PRINT THE TABLES.                            *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&SISDSN,DISP=SHR
//  DD  DSN=&LAYDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//FILE2  DD  DSN=&&TBCLS1,DISP=(OLD,DELETE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE)
//FILE6  DD  DSN=&&TBCELLS,DISP=(NEW,PASS,KEEP),UNIT=&CL3UN,
//  SPACE=(&TPLBLK,(&CL3SP),RLSE)
//FILE7  DD  DSN=&&TBCLS2,DISP=(OLD,DELETE)
//OUTPUT7  DD  SYSOUT=&TABLES,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//SYSPUNCH  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//INPUT5  DD  DSN=&TSLDSN(&STYLE.CON),DISP=(SHR,KEEP)
//INPUT7  DD  DUMMY
//COORD  DD  DSN=&&COORD,DISP=(OLD,DELETE)
//TBCLS1  DD  DSN=&&TBCLS1,DISP=(OLD,DELETE)
//*        END OF PROCEDURE FOR GENERATING TABLES WITH TPL.
//*               DIRECT-ACCESS DEVICES (DISKS) ARE USED FOR THE
//*               MAJOR WORKFILES.
//*  PEND

TPLMOVE.text:
//TPLMOVE  PROC  OLDOWN=,    OWNER OF INPUT SAVED TPL DATA
//     NEWOWN=,       OWNER OF OUTPUT SAVED TPL DATA
//     OLDNAME=,      NAME IDENTIFYING INPUT SAVED TPL DATA
//     NEWNAME=,      NAME IDENTIFYING OUTPUT SAVED TPL DATA
//     NEWUNIT=TSOWRK,
//     MOVED=0,       USED TO ALLOW OPTIONAL MOVING OF TPLSTR DATASET
//     OTBCSEQ=1,     DATASET SEQ # FOR INPUT TBCELLS FILE (IF TAPE)
//     OCONSEQ=2,     DATASET SEQ # FOR INPUT CONSTR FILE (IF TAPE)
//     OTBSSEQ=3,     DATASET SEQ # FOR INPUT TBSPECS FILE (IF TAPE)
//     OSTRSEQ=4,     DATASET SEQ # FOR INPUT TPLSTR FILE (IF TAPE)
//     NTBCSEQ=1,     DATASET SEQ # FOR OUTPUT TBCELLS FILE (IF TAPE)
//     NCONSEQ=2,     DATASET SEQ # FOR OUTPUT CONSTR FILE (IF TAPE)
//     NTBSSEQ=3,     DATASET SEQ # FOR OUTPUT TBSPECS FILE (IF TAPE)
//     NSTRSEQ=4,     DATASET SEQ # FOR OUTPUT TPLSTR FILE (IF TAPE)
//     OLDLAB=SL,     LABEL TYPE FOR INPUT SAVED DATA (IF ON TAPE)
//     NEWLAB=SL,     LABEL TYPE FOR OUTPUT SAVED DATA (IF ON TAPE)
//     NEWTYPE=NEW,   TYPE OF OUTPUT TPL DATA SETS:  OLD OR NEW
//     NEWDISP=CATLG, DISPOSITION FOR OUTPUT TPL DATASETS
//     EXPRET='RETPD', CHOICE OF 'EXPDT' OR 'RETPD' FOR TAPE OUTPUT
//     RETPD=00,     # OF DAYS OUTPUT TAPES ARE TO BE RETAINED      <<
//     TBCSP=100,     # OF TRACKS FOR OUTPUT TBCELLS FILE
//     TBSSP=25,      # OF TRACKS FOR OUTPUT TBSPECS FILE
//     CONSP=25,      # OF TRACKS FOR OUTPUT CONSTR FILE
//     STRSP=1,       # OF TRACKS FOR OUTPUT TPLSTR FILE
//     STRINC=1,      SECONDARY ALLOCATION (TRKS) FOR TPLSTR FILE
//     INCREM=15,     SECONDARY SPACE ALLOCATION (TRKS)FOR OTHER FILES
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     CORE=90K,      REGION USED FOR COPY PROGRAM
//     COPYPGM=IEBGENER,
//     QUOT=,     *'''',
//     VER=6
//*********************************************************************
//*                                                                   *
//*      T P L M O V E - A PROCEDURE TO MOVE SAVED TPL OUTPUT DATA,   *
//*            INCLUDING THE STANDARD TRIO OF DATASETS, "TBCELLS",    *
//*            "CONSTR", AND "TBSPECS", USED FOR REPRINTING AND/OR    *
//*            PHOTOCOMPOSING THE TABLES.                             *
//*            OPTIONALLY, THE FOURTH DATASET, "TPLSTR", WHICH IS     *
//*            USED IN COMMUNICATING INFORMATION ABOUT THE TABLES TO  *
//*            STATISTICAL ANALYSIS ROUTINES, MAY ALSO BE MOVED, IF   *
//*            IT WAS ORIGINALLY SAVED.                               *
//*                                                                   *
//*    VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:          *
//*      OLDOWN='CNaaaa.iii',OLDNAME='input quartet',                 *
//*      NEWOWN='CNbbbb.jjj',NEWNAME='output quartet'                 *
//*    WHERE:                                                         *
//*      OLDOWN  = THE ACCOUNT NUMBER & INITIALS PREFIX UNDER WHICH   *
//*                  THE INPUT GROUP OF SAVED DATASETS IS STORED.     *
//*      OLDNAME = THE NAME IDENTIFYING THE INPUT GROUP OF DATASETS.  *
//*      NEWOWN  = THE ACCOUNT NUMBER & INITIALS PREFIX UNDER WHICH   *
//*                  THE OUTPUT GROUP OF SAVED DATASETS IS TO BE      *
//*                  STORED.                                          *
//*      NEWNAME = THE NAME IDENTIFYING THE OUTPUT GROUP OF           *
//*                  DATASETS.                                        *
//*                                                                   *
//*      IF THE FULL QUARTET OF OUTPUT DATASETS ARE TO BE  MOVED,     *
//*           INCLUDING THE "TPLSTR" DATASET, THEN ONE ADDITIONAL     *
//*           PARAMETER MUST BE SUPPLIED:   MOVED=4                   *
//*                                                                   *
//*      IF THE OUTPUT IS NOT TO BE ON A PUBLIC PACK, AN ADDITIONAL   *
//*        PARAMETER MUST BE SUPPLIED:  NEWUNIT='device-type'         *
//*                                                                   *
//*      IF THE OUTPUT IS TO BE CATALOGED, TWO ADDITIONAL PARAMETERS  *
//*        MUST BE SUPPLIED:  QUOT=,NEWDISP=CATLG                     *
//*                                                                   *
//*      IF THE INPUT GROUP IS NOT ON A PUBLIC PACK, AN ADDITIONAL    *
//*        PARAMETER MUST BE SUPPLIED:  OLDUNIT='device-type'         *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//MOVE1  EXEC  PGM=&COPYPGM,REGION=&CORE
//* *****     COPY THE TABLE CELLS FILE.                        *******
//SYSUT1  DD  DSN=&QUOT.&OLDOWN..TPL.&OLDNAME..V&VER..TBCELLS&QUOT,
//  LABEL=(&OTBCSEQ,&OLDLAB),DISP=(SHR,KEEP)
//SYSUT2  DD  DSN=&QUOT.&NEWOWN..TPL.&NEWNAME..V&VER..TBCELLS&QUOT,
//  DISP=(&NEWTYPE,&NEWDISP),
//  LABEL=(&NTBCSEQ,&NEWLAB,,OUT,&EXPRET=&RETPD),
//  SPACE=(TRK,(&TBCSP,&INCREM),RLSE),UNIT=(&NEWUNIT,,DEFER)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*
//*
//MOVE2  EXEC  PGM=&COPYPGM,REGION=&CORE,
//  COND=(0,LT,MOVE1)
//* *****     COPY THE TABLE CONSTRUCTIONS FILE.                *******
//SYSUT1  DD  DSN=&QUOT.&OLDOWN..TPL.&OLDNAME..V&VER..CONSTR&QUOT,
//  LABEL=(&OCONSEQ,&OLDLAB),DISP=(SHR,KEEP)
//SYSUT2  DD  DSN=&QUOT.&NEWOWN..TPL.&NEWNAME..V&VER..CONSTR&QUOT,
//  LABEL=(&NCONSEQ,&NEWLAB,,OUT,&EXPRET=&RETPD),
//  DISP=(&NEWTYPE,&NEWDISP),
//  SPACE=(TRK,(&CONSP,&INCREM),RLSE),UNIT=(&NEWUNIT,,DEFER)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*
//*
//MOVE3  EXEC  PGM=&COPYPGM,REGION=&CORE,
//  COND=((0,LT,MOVE1),(0,LT,MOVE2))
//* *****     COPY THE TABLE SPECS FILE.                        *******
//SYSUT1  DD  DSN=&QUOT.&OLDOWN..TPL.&OLDNAME..V&VER..TBSPECS&QUOT,
//  LABEL=(&OTBSSEQ,&OLDLAB),DISP=(SHR,KEEP)
//SYSUT2  DD  DSN=&QUOT.&NEWOWN..TPL.&NEWNAME..V&VER..TBSPECS&QUOT,
//  LABEL=(&NTBSSEQ,&NEWLAB,,OUT,&EXPRET=&RETPD),
//  DISP=(&NEWTYPE,&NEWDISP),
//  SPACE=(TRK,(&TBSSP,&INCREM),RLSE),UNIT=(&NEWUNIT,,DEFER)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*
//*
//MOVE4  EXEC  PGM=&COPYPGM,REGION=&CORE,
//  COND=((0,LT,MOVE1),(0,LT,MOVE2),(0,LT,MOVE3),(&MOVED,LE))
//* *****     COPY THE TABLE STRUCTURE FILE TO MOVE IT.         *******
//* *****     THIS STEP IS NOT EXECUTED UNLESS THE USER         *******
//* *****     SPECIFIES THE SYMBOLIC PARAMETER:  MOVED=4        *******
//SYSUT1  DD  DSN=&QUOT.&OLDOWN..TPL.&OLDNAME..V&VER..TPLSTR&QUOT,
//  LABEL=(&OSTRSEQ,&OLDLAB),DISP=(SHR,KEEP)
//SYSUT2  DD  DSN=&QUOT.&NEWOWN..TPL.&NEWNAME..V&VER..TPLSTR&QUOT,
//  LABEL=(&NSTRSEQ,&NEWLAB,,OUT,&EXPRET=&RETPD),
//  DISP=(&NEWTYPE,&NEWDISP),SPACE=(TRK,(&STRSP,&STRINC),RLSE),
//  UNIT=(&NEWUNIT,,DEFER)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*      END OF PROCEDURE FOR MOVING GROUPS OF SAVED TPL OUTPUT DATA.
//*  PEND

TPLMULGN.text:
//TPLMULGN  PROC  CBN='?', NAME FOR CODEBOOK OR LIBRARY
//     CBPREFX='USERX', INITIALS PREFIX FOR CODEBOOK OR LIBRARY<<
//     DATA=NULLFILE, NAME OF DATA FILE TO BE TABULATED
//     SEQ=1,         FILE SEQUENCE NO. ON ITS VOL. FOR DATA FILE
//     LAB=SL,        TYPE OF LABELS ON ITS VOL. FOR DATA FILE
//     DBGDSN='SYS2.TPL.V6.LOADLIB', NAME FOR DATABASE "DBGEN"       <<
//     DBMS=NONE,     NAME OF DATABASE MANAGEMENT SYSTEM BEING USED  <<
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     CORE=500K,     REGION ACTUALLY USED FOR PROGRAMS OF TPL STEP
//     TPLMAX=2000K,  MAXIMUM REGION USED DURING TPL STEP
//     CLNAME='&&CELLS', NAME FOR CELLS WORKFILE PASSED TO NEXT STEP
//     CLUNIT=WORK,  UNIT TYPE FOR CELLS WORKFILE
//     CLTYPE=NEW,    INITIAL STATUS OF CELLS WORKFILE
//     CLDISP=PASS,   DISPOSITION OF CELLS WORKFILE AT NORMAL STEP END
//     CLBOMB='DELETE', DISPOSITION OF CELLS WORKFILE IF ABEND OCCURS
//     CLSP=4000,     INITIAL # BLOCKS SPACE FOR CELLS WORKFILE
//     CLIN=20,       SECONDARY SPACE # BLOCKS FOR CELLS WORKFILE
//     CLSEQ=1,       SEQUENCE NO. ON VOLUME FOR CELLS WORKFILE
//     CLLAB='SL',    LABEL TYPE FOR CELLS WORKFILE
//     EXPRET='RETPD', CHOICE OF "EXPDT" OR "RETPD" FOR CELLS IF TAPE<<
//     RETPD=00,     DEFAULT RETPD FOR CELLS WORKFILE IF ON TAPE    <<
//     SWNAME='&&SWITCH', NAME FOR SWITCH WORKFILE PASSED TO NEXT STEP
//     SWUNIT='WORK', UNIT TYPE FOR SWITCH WORKFILE
//     SWTYPE='NEW',  INITIAL STATUS FOR SWITCH WORKFILE
//     SWDISP='PASS', DISPOSITION OF SWITCH WORKFILE AT NORMAL STEP END
//     SWBOMB='DELETE', DISPOSITION FOR SWITCH WORKFILE IF ABEND OCCURS
//     SWSP=1,        INITIAL # BLOCKS SPACE FOR SWITCH WORKFILE
//     SWIN=1,        SECONDARY SPACE (# BLOCKS) FOR SWITCH WORKFILE
//     COORSP=400,    NO. OF BLKS FOR COORDINATES WORKFILE
//     SPC1SP=200,    INITIAL NO. OF BLKS FOR TABLE SPECS WORKFILE
//     SPC2SP=200,    NO. OF BLKS FOR PCL'S NEW TBSPECS WORKFILE
//     CBUNIT=3380,   DEVICE TYPE FOR CODEBOOK OR LIBRARY            <<
//     TMPSP=125,     NO OF BLKS FOR "TEMP" WORKFILE
//     DICNAME='BLS.HYPHEN.DICT',  NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CBKBLK=2976,CBVER=6,
//     CGNDSN='SYS2.TPL.V6.OBJLIB(CGNOBJ)',
//     CLLREC='32',CLRECF='FB',
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     DATFREE=100,
//     DSPDSN='SYS2.TPL.V6.OBJLIB(DSPOBJ)',
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB', <<
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',
//     POTDSN='SYS2.TPL.V6.OBJLIB(POTOBJ)',
//     QUOT='''',
//     RAPDSN='NULLFILE', <<
//     SWBLK='800',SWLREC='80',SWRECF='FB',
//     TOTDSN='NULLFILE', <<
//     TPLBLK=2976,
//     TPLOPT=,
//     TRNDSN='SYS2.TPL.V6.OBJLIB(TRNOBJ)'
//*********************************************************************
//*                                                                   *
//*      T P L M U L G N - - A PROCEDURE TO GENERATE TABLES WITH TPL, *
//*           IN A MULTIPLE-STEP PROCESS.  THIS CAN BE USED WITH A    *
//*           MULTIPLE-COPY DATABASE, OR WITH A DATA FILE SPLIT AMONG *
//*           SEVERAL LARGE DATASETS.                                 *
//*           A CELL WORKFILE AND A SWITCH WORKFILE WILL BE PRODUCED, *
//*           AND WILL BE PASSED DOWN TO A FOLLOWING STEP WHICH MUST  *
//*           EXECUTE EITHER THIS PROCEDURE AGAIN, OR ONE OF EITHER   *
//*           "TPLGENTB" OR "TPLTPGEN".                               *
//*                                                                   *
//*      VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:        *
//*           CBN='cccccccc',CBPREFX='CNaaaa.iii',                    *
//*           DATA='zzz'                                              *
//*                                                                   *
//*      FOR THE FIRST USE OF THIS PROCEDURE IN THE JOB, A DD CARD    *
//*           MUST BE ADDED IN THE "TPL" STEP, AFTER THE TPL REQUEST  *
//*           AND (IF PRESENT) THE TPL CHANGES, (BUT BEFORE ANY DD    *
//*           CARDS FOR THE DATABASE, IF IT IS A TOTAL OR RAPID JOB)  *
//*           AS FOLLOWS:                                             *
//*                //TPL.INSWITCH  DD  DUMMY                          *
//*                                                                   *
//*      FOR THE INTERMEDIATE STEPS (NEITHER FIRST NOR LAST STEPS),   *
//*           NO ADDITIONAL DD CARDS ARE REQUIRED BEYOND THOSE        *
//*           NORMALLY PRESENT IN A TABLE GENERATION RUN.             *
//*                                                                   *
//*       ON THE "EXEC" CARD IN EACH INTERMEDIATE STEP, THE FOLLOWING *
//*          TWO PARAMETERS MUST BE SPECIFIED:                        *
//*             SWTYPE=OLD,CLNAME='UNIQUE NAME'                       *
//*          WHERE "UNIQUE NAME" IS A UNIQUE NAME FOR THE CELL        *
//*          WORKFILE BEING CREATED IN THAT STEP.                     *
//*                                                                   *
//*      FOR THE LAST TABLE GENERATION STEP, WHICH EXECUTES EITHER    *
//*           "TPLGENTB" OR "TPLTPGEN", THE FIRST REQUIRED CARD IS    *
//*           THE "//TPL.INSWITCH" DD CARD WHICH DESCRIBES THE        *
//*           CARD-FORMAT DATASET PRODUCED BY THE IMMEDIATELY         *
//*           PRECEDING STEP WHICH EXECUTED "TPLMULGN".               *
//*           A BLANK "//PRESORT.SORTIN" DD CARD MUST BE ADDED IN THE *
//*           FINAL STEP, FOLLOWED BY CONCATENATED DD CARDS WHICH     *
//*           DESCRIBE EACH PASSED CELL WORKFILE COMPLETELY.          *
//*                                                                   *
//*      THE TPL STATEMENTS COMPRISING THE REQUEST MUST BE DESCRIBED  *
//*           BY A "//TPL.REQUEST" DD CARD.  THE IDENTICAL TPL        *
//*           REQUEST MUST BE USED IN EACH "TPLMULGN" AND "TPLGENTB"  *
//*           OR "TPLTPGEN" STEP IN A JOB.                            *
//*                                                                   *
//*      ANY PCL STATEMENTS MODIFYING THE TABLES MUST BE DESCRIBED BY *
//*           A "//TPL.CHANGES" DD CARD, WHICH MUST ITSELF FOLLOW THE *
//*           "//TPL.REQUEST" DD CARD.                                *
//*                                                                   *
//*      THE "//TPL.INSWITCH" CARD, WHEN PRESENT, MUST FOLLOW AFTER   *
//*           THE "//TPL.CHANGES" DD CARD; IF NO PCL STATEMENTS ARE   *
//*           PRESENT, THEN IT MUST FOLLOW AFTER THE "//TPL.REQUEST"  *
//*           DD CARD.  THIS PLACEMENT REQUIREMENT IS TRUE FOR        *
//*           "TPLGENTB", "TPLTPGEN", AND "TPLMULGN".                 *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TPLSPEC  DD  DSN=&&TPLSPEC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC1SP))
//TBCLS1  DD  DSN=&CLNAME,UNIT=&CLUNIT,
//  DISP=(&CLTYPE,PASS),SPACE=(&TPLBLK,(&CLSP,&CLIN))
//COORD  DD  DSN=&&COORD,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//PROFILE  DD  DSN=&PRODSN,DISP=SHR
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  COND=(0,LT,SYSCNTRL),
//  PARM='FILE=&TPLBLK,FILE2=&CBKBLK,FREE=&DATFREE,MAX=&CORE,&TPLOPT'
//* *****     THE WORKING STEP:  THE TPL TRANSLATION PHASE, THE *******
//* *****     PCL TRANSLATION PHASE, THE POST-TRANSLATION       *******
//* *****     PHASE, THE DISPATCHER PHASE, AND THE CELL         *******
//* *****     GENERATION PHASE.                                 *******
//STEPLIB  DD  DSN=&DBGDSN,DISP=SHR
//  DD  DDNAME=&DBMS.LIB
//  DD  DSN=&MONLIB,DISP=SHR
//TOTALLIB  DD  DSN=&TOTDSN,DISP=SHR
//RAPIDLIB  DD  DSN=&RAPDSN,DISP=SHR
//NONELIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&TRNDSN,DISP=SHR
//  DD  DSN=&PCLDSN,DISP=SHR
//  DD  DSN=&POTDSN,DISP=SHR
//  DD  DSN=&DSPDSN,DISP=SHR
//  DD  DSN=&CGNDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT3  DD  DUMMY
//FILE1  DD  DSN=&&TPLSPEC,DISP=(OLD,DELETE,KEEP)
//FILE2  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  UNIT=&CBUNIT,DISP=SHR
//INPUT2  DD  DDNAME=DATA
//DATA  DD  DSN=&QUOT.&DATA.&QUOT,
//  LABEL=(&SEQ,&LAB),DISP=SHR
//SYSIN  DD  DDNAME=REQUEST
//REQUEST  DD  DUMMY
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,DELETE,DELETE)
//FILE5  DD  DSN=&&TEMP,UNIT=WORK,SPACE=(&TPLBLK,(&TMPSP)),
//  DISP=(NEW,DELETE)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE)
//SYSPUNCH  DD  DUMMY
//INPUT4  DD  DUMMY
//INPUT3  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//OUTPUT6  DD  DUMMY
//OUTPUT4  DD  DSN=&SWNAME,UNIT=&SWUNIT,
//  DISP=(&SWTYPE,&SWDISP,&SWBOMB),SPACE=(&SWBLK,(&SWSP,&SWIN),RLSE),
//  DCB=(RECFM=&SWRECF,LRECL=&SWLREC,BLKSIZE=&SWBLK)
//INPUT7  DD  DDNAME=INSWITCH
//INSWITCH  DD  DSN=*.OUTPUT4,VOL=REF=*.OUTPUT4,
//  DISP=(SHR,&SWDISP,&SWBOMB)
//FILE6  DD  DSN=&CLNAME,DISP=(OLD,&CLDISP,&CLBOMB),
//  DCB=(RECFM=&CLRECF,LRECL=&CLLREC,BLKSIZE=&TPLBLK),
//  SPACE=(&TPLBLK,(&CLSP,&CLIN),RLSE),
//  LABEL=(&CLSEQ,&CLLAB,,OUT,&EXPRET=&RETPD)                        <<
//OUTPUT5  DD  DUMMY
//FILE7  DD  DSN=*.FILE5,VOL=REF=*.FILE5,DISP=(SHR,DELETE)
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//RAPSYSP  DD  SYSOUT=&LISTING
//*        END OF PROCEDURE FOR GENERATING TABLES IN A MULTIPLE STEP
//*               PROCESS.
//*  PEND

TPLPHOTO.text:
//TPLPHOTO  PROC  OWNER=,  ACCOUNT & INITIALS FOR SAVED OUTPUT
//     SAVNAME=,      NAME WHICH IDENTIFIES SAVED OUTPUT
//     TBCSEQ=1,      DATASET SEQUENCE # FOR TBCELLS
//     CONSEQ=2,      DATASET SEQUENCE # FOR CONSTR
//     TBSSEQ=3,      DATASET SEQUENCE # FOR TBSPECS
//     LAB=SL,        LABEL TYPE FOR SAVED OUTPUT
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     CORE=500K,     REGION ACTUALLY USED FOR PROGRAM OF TPL STEP
//     TPLMAX=2000K,  MAXIMUM REGION USED DURING TPL STEP
//     PSRCORE=500K,  REGION SIZE FOR PCLSORT STEP
//     LAYCORE=500K,  REGION ACTUALLY USED FOR PROGRAMS OF LAYOUT STEP
//     LAYMAX=2000K,  MAXIMUM REGION USED DURING LAYOUT STEP
//     CSRCORE=500K,  REGION SIZE FOR CALLSORT STEP
//     WKUN=WORK,    DEVICE TYPE FOR SORT WORKFILES
//     WKSP=1200,     NO. OF BLOCKS FOR SORT WORKFILES
//     COORSP=1000,   NO. OF BLOCKS FOR COORDINATES WORKFILE
//     SPC2SP=200,    NO. OF BLOCKS FOR PCL'S NEW TBSPECS WORKFILE
//     PHOTDSN='&&PHOTOUT', DSNAME OF PHOTO WORKFILE
//     PHOTUNT=WORK, UNIT TYPE FOR PHOTO WORKFILE
//     PHOTTYP=NEW,   PHOTO WORKFILE STATUS
//     PHOTDEL=DELETE,  PHOTO WORKFILE FINAL DISPOSITION
//     PHSPACE=480,   NO. OF BLOCKS FOR PHOTO WORKFILE
//     PHINCR=200,    SECONDARY ALLOC BLOCKS FOR PHOTO WORKFILE
//     PHOTLAB='SL',  PHOTO WORKFILE LABEL TYPE
//     PHOTSEQ=1,     PHOTO WORKFILE SEQUENCE
//     TSLDSN='SYS2.TPL.V6.TYPE.STYLES', NAME OF TYPE STYLE LIBRARY<<
//     STYLE=NOPH,    NAME OF DEFAULT TYPE STYLE                     <<
//     CALPRE='SYS2.TPL.V6',  & INITIALS PREFIX FOR CALL FILE        <<
//     CALNAME=,      NAME OF CALL FILE
//     CALTYPE=NEW,   STATUS OF CALL FILE DATA SET
//     CALDISP=CATLG, TO PERMIT CATALOGING OF CALL FILE
//     CALMOD=OLD,    TO PERMIT MOD OF CALL FILE DATA SET
//     CALSP=640,     INITIAL SPACE IN BLOCKS FOR CALL FILE
//     CALIN=160,     SECONDARY SPACE FOR CALL FILE
//     CALSEQ=1,      DATASET SEQUENCE # FOR CALL FILE
//     CALLAB=SL,     LABEL TYPE FOR CALL FILE
//*    EXPRET='RETPD', CHOICE: 'EXPDT' OR 'RETPD' IF TAPE CALL FILE  <<
//*    RETPD=00,     # OF DAYS TO RETAIN CALL FILE IF ON TAPE       <<
//     CLWKUN=WORK,  UNIT TYPE FOR PHOTO OUTPUT WORK FILE
//     DICNAME='BLS.HYPHEN.DICT', NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CALBLK=1300,CALLREC=130,CALRECF=FB,
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     CVTDSN='SYS2.TPL.V6.OBJLIB(CVTOBJ)',
//     FREE=100,
//     LAYDSN='SYS2.TPL.V6.OBJLIB(LAYOBJ)',LAYVOL=NSF023,LAYUNIT=3380,
//     LAYOPT=,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB',MONUNIT=NSF023,MONVOL=3380, <<
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',PCLVOL=NSF023,PCLUNIT=3380,
//     PHBLK=2560,PHLRECL=256,PHRECFM=FB,
//     QUOT=,     '''',
//     SORTPGM=SORT,                                                 <<
//     TPLBLK=2976,
//     TPLOPT=,
//     VER=6
//*********************************************************************
//*                                                                   *
//*      T P L P H O T O - - A PROCEDURE FOR RETRIEVING STORED TPL    *
//*           OUTPUT AND PRODUCE AN INTERMEDIATE (CALL) FILE FOR      *
//*           PHOTOCOMPOSING THE TABLES.                              *
//*                                                                   *
//*     THE FOLLOWING PARAMETERS ARE NECESSARY:                       *
//*          OWNER='CNaaaa.iii',SAVNAME='output name',                *
//*          CALPRE='CNbbbb.jjj',CALNAME='file name'                  *
//*       WHERE:                                                      *
//*           OWNER   = THE ACCOUNT NUMBER & INITIALS PREFIX UNDER    *
//*                        WHICH THE SAVED OUTPUT IS STORED.          *
//*           SAVNAME = THE NAME USED TO IDENTIFY THE SAVED OUTPUT.   *
//*           CALPRE = THE ACCOUNT NUMBER & INITIALS PREFIX UNDER     *
//*                        WHICH THE CALL FILE IS STORED.             *
//*           CALL    = THE NAME USED TO IDENTIFY THE CALL FILE.      *
//*                                                                   *
//*     ANY PCL STATEMENTS MODIFYING THE REQUEST, IF PRESENT, MUST    *
//*          BE PLACED AFTER A "//TPL.CHANGES" DD CARD.               *
//*                                                                   *
//*     IF THE CALL FILE IS TO BE ON A DEVICE OTHER THAN A PUBLIC     *
//*          DISK PACK, THEN ADD THE FOLLOWING PARAMETER:             *
//*             CALUNIT='device-type'                                 *
//*                                                                   *
//*                                                                   *
//*     THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.          *
//*                                                                   *
//*     IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.             *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//SPECSIN  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBSPECS&QUOT,
//  UNIT=TSOWRK,
//  DISP=(SHR,PASS),LABEL=(&TBSSEQ,&LAB)
//TBCELLS  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBCELLS&QUOT,
//  UNIT=TSOWRK,VOL=REF=*.SPECSIN,LABEL=(&TBCSEQ,&LAB),
//  DISP=(SHR,PASS)
//CONSTR  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..CONSTR&QUOT,
//  UNIT=TSOWRK,VOL=REF=*.SPECSIN,LABEL=(&CONSEQ,&LAB),
//  DISP=(SHR,KEEP)
//COORD  DD  DSN=&&COORD,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//SORTWK01  DD  DSN=&&WORK1,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//PHOTOUT  DD  DSN=&PHOTDSN,DISP=(&PHOTTYP,PASS,&PHOTDEL),
//  UNIT=TSOWRK,
//  DCB=(RECFM=&PHRECFM,LRECL=&PHLRECL,BLKSIZE=&PHBLK),
//  LABEL=(&PHOTSEQ,&PHOTLAB),SPACE=(&PHBLK,(&PHSPACE,&PHINCR))
//CALLWRK  DD  DSN=&&CALLWRK,UNIT=&CLWKUN,DISP=(NEW,PASS,DELETE),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK),
//  SPACE=(&CALBLK,(&CALSP,&CALIN))
//CALLFILE  DD  DSN=&QUOT.&CALPRE..PCL.VIDEO.CALL.&CALNAME.&QUOT,
//  UNIT=&CALUNIT,
//  DISP=(&CALTYPE,&CALDISP,KEEP),LABEL=(&CALSEQ,&CALLAB),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK),
//  SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE)
//PROFILE  DD  DSN=&PRODSN,DISP=SHR
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&CORE,&TPLOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     THE PCL TRANSLATOR STEP                           *******
//STEPLIB  DD  DSN=&MONLIB,
//  DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&PCLDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&PCLSORT,UNIT=WORK,SPACE=(TRK,(1,1),RLSE),
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=400),DISP=(NEW,PASS)
//OUTPUT4  DD  DUMMY
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//FILE1  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBSPECS&QUOT,
//  LABEL=(&TBSSEQ,&LAB),DISP=(SHR,KEEP)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&COORSP),RLSE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP),RLSE)
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//SYSIN  DD  DUMMY
//INPUT3  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//INPUT4  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..CONSTR&QUOT,
//  LABEL=(&CONSEQ,&LAB),DISP=(SHR,KEEP),VOL=REF=*.FILE1
//INPUT7  DD  DUMMY
//*
//*
//PCLSORT  EXEC  PGM=&SORTPGM,REGION=&PSRCORE,
//  COND=((0,LT,SYSCNTRL),(4,LE,TPL))
//* *****     SORT THE COORDINATE RECORDS FOR THE PCL SYSTEM    *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&PCLSORT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTOUT  DD  DSN=&&COORD,VOL=REF=*.SORTIN,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,PASS,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,PASS,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,PASS,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,PASS,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,PASS,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,PASS,DELETE)
//*
//*
//LAYOUT  EXEC  PGM=&MONITOR,REGION=&LAYMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&LAYCORE,&LAYOPT',
//  COND=((0,LT,SYSCNTRL),(4,LE,TPL),(0,LT,PCLSORT))
//* *****     THE TABLE LAYOUT AND PRINTING STEP, AND THEN      *******
//* *****     THE PHOTOCOMPOSITION CONVERSION STEP.             *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,KEEP)
//PROGRAM  DD  DSN=&LAYDSN,DISP=SHR
//  DD  DSN=&CVTDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING,DCB=BLKSIZE=133
//SYSPUNCH  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  DSN=&PHOTDSN,DISP=(OLD,&PHOTDEL,&PHOTDEL),
//  LABEL=(&PHOTSEQ,&PHOTLAB),SPACE=(&PHBLK,(&PHSPACE,&PHINCR),RLSE),
//  DCB=(RECFM=&PHRECFM,LRECL=&PHLRECL,BLKSIZE=&PHBLK)
//INPUT3  DD  DUMMY
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,DELETE,DELETE)
//FILE6  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBCELLS&QUOT,
//  LABEL=(&TBCSEQ,&LAB),DISP=(SHR,KEEP)
//INPUT5  DD  DSN=&TSLDSN(&STYLE.CON),DISP=(SHR,KEEP)
//  DD  DSN=&TSLDSN(&STYLE.TYPE),DISP=(SHR,KEEP)
//INPUT4  DD  DSN=*.OUTPUT4,DISP=(OLD,&PHOTDEL,&PHOTDEL),
//  LABEL=(&PHOTSEQ,&PHOTLAB),VOL=REF=*.OUTPUT4
//OUTPUT3  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT5  DD  DSN=&&CALLWRK,DISP=(OLD,PASS,DELETE),
//  SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK)
//INPUT2  DD  DDNAME=LIMIT
//LIMIT  DD  DUMMY
//INPUT7  DD  DUMMY
//OUTPUT6  DD  DSN=&&CALLSRT,UNIT=WORK,DISP=(NEW,PASS,DELETE),
//  SPACE=(TRK,(1,1)),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//SYSIN  DD  DUMMY
//COORD  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//*
//*
//CALLSORT  EXEC  PGM=&SORTPGM,REGION=&CSRCORE,
//  COND=((0,LT,SYSCNTRL),(4,LE,TPL),(0,LT,PCLSORT),(0,LT,LAYOUT))
//* *****     SORT THE CALLFILE USED BY THE TPLCRTP PROCEDURE   *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&CALLSRT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&CALLWRK,DISP=(OLD,DELETE,DELETE),
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK)
//SORTOUT  DD  DSN=&QUOT.&CALPRE..PCL.VIDEO.CALL.&CALNAME.&QUOT,
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK),
//  DISP=(&CALMOD,CATLG,KEEP),SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE,DELETE)
//*     END OF PROCEDURE TO RETRIEVE SAVED OUTPUT OF TPL TABLES
//*          AND PRODUCE AN INTERMEDIATE FILE FOR PHOTOCOMPOSING
//*          THE TABLES.
//*  PEND

TPLRERUN.text:
//TPLRERUN  PROC  OWNER=, ACCOUNT & INITIALS PREFIX FOR STORED OUTPUT
//     SAVNAME=,        NAME WHICH IDENTIFIES SAVED OUTPUT
//*    SAVUNIT=TSOWRK,  UNIT TYPE FOR SAVED OUTPUT                   <<
//     TBCSEQ=1,        DATASET SEQUENCE # FOR TBCELLS
//     CONSEQ=2,        DATASET SEQUENCE # FOR CONSTR
//     TBSSEQ=3,        DATASET SEQUENCE # FOR TBSPECS
//     LAB=SL,          LABEL TYPE FOR STORED OUTPUT
//     LISTING='A',     SYSOUT SPECIFICATION FOR ALL LISTINGS        <<
//     TABLES='A',      SYSOUT SPECIFICATION IF TABLES SEPARATED     <<
//     CORE=500K,       REGION ACTUALLY USED FOR PROGRAM OF TPL STEP
//     TPLMAX=2000K,    MAXIMUM REGION USED DURING TPL STEP
//     PSRCORE=500K,    REGION SIZE FOR PCLSORT STEP
//     LAYCORE=500K,    REGION ACTUALLY USED FOR PROGRAM OF LAYOUT STEP
//     LAYMAX=2000K,    MAXIMUM REGION USED DURING LAYOUT STEP
//     WKUN=WORK,      DEVICE TYPE FOR SORT WORKFILES
//     WKSP=800,        NO. OF BLKS FOR SORT WORKFILES
//     COORSP=1000,     NO. OF BLKS FOR COORDINATES WORKFILE
//     SPC2SP=200,      NO. OF BLKS FOR PCL'S NEW TBSPECS WORKFILE
//     TSLDSN='SYS2.TPL.V6.TYPE.STYLES', NAME OF TYPE STYLE LIBRARY<<
//     STYLE=NOPH, WAS=SET1, NAME OF DEFAULT TYPE STYLE   <<
//     CALPRE='SYS2.TPL.V6', NT & INITIALS PREFIX FOR USE IN PHOTO   <<
//     CALNAME='MMMM',  NAME FOR USE IN PHOTO
//     CALTYPE=NEW,     STATUS FOR USE IN PHOTO
//     CALDISP=CATLG,   TO PERMIT CATALOGING IN PHOTO
//     CALMOD=OLD,      FOR USE OF MOD IN PHOTO
//     CALSP=640,       INITIAL SPACE IN BLOCKS FOR USE IN PHOTO
//     CALIN=160,       SECONDARY SPACE FOR USE IN PHOTO
//*    CALUNIT=TSOWRK,  UNIT TYPE FOR USE IN PHOTO                   <<
//*    CALSEQ=1,        DATASET SEQUENCE # FOR USE IN PHOTO
//*    CALLAB=SL,       LABEL TYPE FOR USE IN PHOTO
//*    EXPRET='RETPD',  CHOICE: 'EXPDT' OR 'RETPD' IF TAPE CALL FILE <<
//*    RETPD=00,       # OF DAYS TO RETAIN CALL FILE IF ON TAPE     <<
//     DICNAME='BLS.HYPHEN.DICT', NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CALBLK=1300,CALLREC=130,CALRECF=FB,
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     FREE=100,
//     LAYDSN='SYS2.TPL.V6.OBJLIB(LAYOBJ)',
//     LAYOPT=,
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB', <<
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',
//     QUOT=,    *'''',
//     SORTPGM=SORT,                                                 <<
//     TPLBLK=2976,
//     TPLOPT=,
//     VER=6
//*********************************************************************
//*                                                                   *
//*      T P L R E R U N - - A PROCEDURE FOR RETRIEVING STORED TPL    *
//*           OUTPUT AND REPRINTING THE TABLES, ALLOWING OPTIONAL PCL *
//*           STATEMENTS TO BE ADDED IN ORDER TO MODIFY THE TABLES.   *
//*                                                                   *
//*           VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:   *
//*           OWNER='CNaaaa.iii',SAVNAME='named o/p'    *
//*                                                                   *
//*           WHERE:                                                  *
//*                                                                   *
//*           OWNER   = THE ACCOUNT NUMBER & INITIALS UNDER WHICH THE *
//*                     SAVED OUTPUT IS STORED.                       *
//*           SAVNAME = THE NAME USED TO IDENTIFY THE SAVED OUTPUT.   *
//*                                                                   *
//*     ANY PCL STATEMENTS MODIFYING THE REQUEST, IF PRESENT, MUST    *
//*          BE PLACED AFTER A "//TPL.CHANGES" DD CARD.               *
//*                                                                   *
//*     THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.          *
//*                                                                   *
//*     IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.             *
//*            TELEPHONE:  357-9999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//SPECSIN  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBSPECS&QUOT,
//  DISP=(SHR,PASS),LABEL=(&TBSSEQ,&LAB)
//TBCELLS  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBCELLS&QUOT,
//  LABEL=(&TBCSEQ,&LAB),
//  DISP=(SHR,PASS)
//CONSTR  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..CONSTR&QUOT,
//  LABEL=(&CONSEQ,&LAB),
//  DISP=(SHR,KEEP)
//COORD  DD  DSN=&&COORD,UNIT=SYSDA,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//COORD2  DD  DSN=*.COORD,VOL=REF=*.COORD,DISP=(SHR,PASS)
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//SORTWK01  DD  DSN=&&WORK1,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK05  DD  DSN=&&WORK5,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK06  DD  DSN=&&WORK6,DISP=(NEW,PASS),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//PROFILE  DD  DSN=&PRODSN,DISP=SHR
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&CORE,&TPLOPT',
//  COND=(0,LT,SYSCNTRL)
//* *****     THE PCL TRANSLATOR STEP                           *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&PCLDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&&PCLSORT,UNIT=WORK,SPACE=(TRK,(1,1),RLSE),
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=400),DISP=(NEW,PASS)
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//FILE1  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBSPECS&QUOT,
//  LABEL=(&TBSSEQ,&LAB),DISP=(SHR,KEEP)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&COORSP),RLSE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP),RLSE)
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//SYSIN  DD  DUMMY
//INPUT3  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//INPUT4  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..CONSTR&QUOT,
//  LABEL=(&CONSEQ,&LAB),DISP=(SHR,KEEP)
//INPUT7  DD  DUMMY
//*
//*
//PCLSORT  EXEC  PGM=&SORTPGM,REGION=&PSRCORE,
//  COND=((0,LT,SYSCNTRL),(8,LT,TPL))
//* *****     SORT THE COORDINATE RECORDS FOR PCL               *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&PCLSORT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTOUT  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//SORTWK05  DD  DSN=&&WORK5,DISP=(OLD,DELETE,DELETE)
//SORTWK06  DD  DSN=&&WORK6,DISP=(OLD,DELETE,DELETE)
//*
//*
//LAYOUT  EXEC  PGM=&MONITOR,REGION=&LAYMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&LAYCORE,&LAYOPT',
//  COND=((0,LT,SYSCNTRL),(8,LT,TPL),(0,LT,PCLSORT))
//* *****     THE TABLE LAYOUT AND PRINTING STEP                *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&LAYDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT7  DD  SYSOUT=&TABLES,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//SYSPUNCH  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,DELETE,DELETE)
//FILE6  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBCELLS&QUOT,
//  LABEL=(&TBCSEQ,&LAB),DISP=SHR
//SYSIN  DD  DUMMY
//INPUT5  DD  DSN=&TSLDSN(&STYLE.CON),DISP=(SHR,KEEP)
//COORD  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//CALLFILE  DD  DUMMY,SPACE=(&CALBLK,(&CALSP,&CALIN),RLSE),
//  DSN=&QUOT.&CALPRE.&CALMOD.VIDEO.CALL.&CALNAME.&QUOT,
//  DISP=(&CALTYPE,&CALDISP,KEEP),UNIT=TSOWRK,
//* LABEL=(&CALSEQ,&CALLAB,&EXPRET=&RETPD),                          <<
//  DCB=(RECFM=&CALRECF,LRECL=&CALLREC,BLKSIZE=&CALBLK)
//*     END OF PROCEDURE FOR RETRIEVING STORED TPL OUTPUT AND REPRINTING
//*          THE TABLES, WITH OPTIONAL PCL STATEMENTS ADDED.
//*  PEND

TPLSAVE.text:
//TPLSAVE  PROC  OWNER=,  ACCOUNT & INITIALS FOR STORING OUTPUT
//     SAVNAME=,      NAME TO IDENTIFY SAVED OUTPUT
//     SAVUNIT=TSOWRK, NIT TYPE FOR SAVED OUTPUT                     <<
//     SAVTYPE=NEW,   TYPE OF SAVED OUTPUT DATASETS: OLD OR NEW
//     SAVDISP=CATLG, DISPOSITION FOR OUTPUT DATASETS
//     SAVED=0,       USED TO ALLOW OPTIONAL SAVING OF TPLSTR DATASET
//     EXPRET='RETPD', CHOICE OF 'EXPDT' OR 'RETPD' FOR TAPE OUTPUT
//     RETPD=00,     # OF DAYS OUTPUT IS TO BE RETAINED IF TAPE     <<
//     TBCSP=100,     # OF TRACKS FOR STORING TBCELLS
//     CONSP=25,      # OF TRACKS FOR STORING CONSTR
//     TBSSP=25,      # OF TRACKS FOR STORING TBSPECS
//     STRSP=5,       # OF TRACKS FOR STORING TPLSTR
//     STRINC=1,      SECONDARY ALLOCATION (TRKS) FOR TPLSTR FILE
//     INCREM=15,     SECONDARY SPACE ALLOCATION (TRKS) FOR OTHER FILES
//     TBCSEQ=1,      DATASET SEQUENCE # FOR TBCELLS
//     CONSEQ=2,      DATASET SEQUENCE # FOR CONSTR
//     TBSSEQ=3,      DATASET SEQUENCE # FOR TBSPECS
//     STRSEQ=4,      DATASET SEQUENCE # FOR TPLSTR
//     LAB=SL,        LABEL TYPE FOR STORED OUTPUT
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     CORE=90K,      REGION IN WHICH TO RUN
//     COPYPGM=IEBGENER,
//     QUOT=,     *'''',
//     VER=6
//*********************************************************************
//*                                                                   *
//*      T P L S A V E - -  A PROCEDURE TO SAVE TPL OUTPUT, INCLUDING *
//*            INCLUDING THE STANDARD TRIO OF DATASETS, "TBCELLS",    *
//*            "CONSTR", AND "TBSPECS", USED FOR REPRINTING AND/OR    *
//*            PHOTOCOMPOSING THE TABLES.                             *
//*            OPTIONALLY, THE FOURTH DATASET, "TPLSTR", WHICH IS     *
//*            USED IN COMMUNICATING INFORMATION ABOUT THE TABLES TO  *
//*            STATISTICAL ANALYSIS ROUTINES, MAY ALSO BE SAVED.      *
//*                                                                   *
//*      THE FOLLOWING PARAMETERS MUST BE SUPPLIED:                   *
//*       OWNER='CNaaaa.iii',SAVNAME='output name'                    *
//*       WHERE,                                                      *
//*           OWNER   = THE ACCOUNT NUMBER & INITIALS PREFIX UNDER    *
//*                       WHICH THE SAVED OUTPUT IS TO BE STORED.     *
//*           SAVNAME = THE NAME USED TO IDENTIFY THE SAVED OUTPUT.   *
//*                                                                   *
//*      IF THE FULL QUARTET OF OUTPUT DATASETS ARE TO BE SAVED,      *
//*           INCLUDING THE "TPLSTR" DATASET, THEN ONE ADDITIONAL     *
//*           PARAMETER MUST BE SUPPLIED:  SAVED=4                    *
//*                                                                   *
//*      IF THE SAVED OUTPUT IS NOT TO BE PLACED ON A PUBLIC PACK,    *
//*           THEN AN ADDITIONAL PARAMETER MUST BE SUPPLIED:          *
//*                SAVUNIT='device-type'                              *
//*                                                                   *
//*      IF THE OUTPUT IS TO BE CATALOGED, TWO ADDITIONAL PARAMETERS  *
//*        MUST BE SUPPLIED:  QUOT=,SAVDISP=CATLG                     *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SAVE1  EXEC  PGM=&COPYPGM,REGION=&CORE
//* *****     COPY THE TABLE CELLS FILE TO SAVE IT.             *******
//SYSUT1  DD  DSN=&&TBCELLS,DISP=(SHR,PASS)
//SYSUT2  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBCELLS&QUOT,
//  DISP=(&SAVTYPE,&SAVDISP),
//  LABEL=(&TBCSEQ,&LAB,,OUT,&EXPRET=&RETPD),
//  UNIT=&SAVUNIT,SPACE=(TRK,(&TBCSP,&INCREM),RLSE)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*
//*
//SAVE2  EXEC  PGM=&COPYPGM,REGION=&CORE,
//  COND=(0,LT,SAVE1)
//* *****     COPY THE TABLE CONSTRUCTIONS FILE TO SAVE IT.     *******
//SYSUT1  DD  DSN=&&CONSTR,DISP=(SHR,PASS)
//SYSUT2  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..CONSTR&QUOT,
//  LABEL=(&CONSEQ,&LAB,,OUT,&EXPRET=&RETPD),DISP=(&SAVTYPE,&SAVDISP),
//  UNIT=&SAVUNIT,SPACE=(TRK,(&CONSP,&INCREM),RLSE)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*
//*
//SAVE3  EXEC  PGM=&COPYPGM,REGION=&CORE,
//  COND=((0,LT,SAVE1),(0,LT,SAVE2))
//* *****     COPY THE TABLE SPECS FILE TO SAVE IT.             *******
//SYSUT1  DD  DSN=&&TBSPECS,DISP=(SHR,PASS)
//SYSUT2  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TBSPECS&QUOT,
//  UNIT=&SAVUNIT,
//  LABEL=(&TBSSEQ,&LAB,,OUT,&EXPRET=&RETPD),DISP=(&SAVTYPE,&SAVDISP),
//  SPACE=(TRK,(&TBSSP,&INCREM),RLSE)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*
//*
//SAVE4  EXEC  PGM=&COPYPGM,REGION=&CORE,
//  COND=((0,LT,SAVE1),(0,LT,SAVE2),(0,LT,SAVE3),(&SAVED,LE))
//* *****     COPY THE TABLE STRUCTURE FILE TO SAVE IT.         *******
//* *****     THIS STEP IS NOT EXECUTED UNLESS THE USER         *******
//* *****     SPECIFIES THE SYMBOLIC PARAMETER:  SAVED=4        *******
//SYSUT1  DD  DSN=&&TPLSTR,DISP=(SHR,PASS)
//SYSUT2  DD  DSN=&QUOT.&OWNER..TPL.&SAVNAME..V&VER..TPLSTR&QUOT,
//  UNIT=&SAVUNIT,
//  LABEL=(&STRSEQ,&LAB,&EXPRET=&RETPD),DISP=(&SAVTYPE,&SAVDISP),
//  SPACE=(TRK,(&STRSP,&STRINC),RLSE)
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//*   END OF PROCEDURE TO COPY THE QUARTET OF DATAFILES USED TO
//*      COMMUNICATE TABULATED CELL VALUES TO STATISTICAL
//*      ANALYSIS ROUTINES, AS WELL AS FOR REPRINTING THE TABLES.
//* PEND

TPLSIMU.text:
//TPLSIMU  PROC  PLTPRE='SYS2.TPL.V6', NIT PREFIX FOR THE PLOT TAPE  <<
//     PLTDISP=CATLG,    DISPOSITION OF PLOT IF JOB ENDS NORMALLY
//     PLTTYPE=NEW,      STATUS OF CALCOMP PLOT TAPE DATASET
//     PLTUNIT=TAPE,  TAPE TYPE FOR CALCOMP PLOT TAPE             <<
//     PLTSEQ=1,         SEQUENCE ON ITS VOLUME OF PLOT TAPE
//     EXPRET='RETPD',   CHOICE OF 'EXPDT' OR 'RETPD' FOR PLOT TAPE
//     RETPD=00,         RETENTION PERIOD FOR PLOT TAPE              <<
//     PLTLAB=SL,        LABEL TYPE FOR CALCOMP PLOT TAPE
//     BADPLOT=KEEP,     DISPOSITION OF PLOT IF JOB ABENDS
//     CORE=150K,        REGION SIZE FOR CREATION OF CALCOMP PLOT
//     DRVPRE='???',     ACCOUNT & INITIALS PREFIX FOR DRIVER TAPE
//     DRVNAME='???',    NAME ASSIGNED TO DRIVER TAPE
//     DRVVOL='???',     VOLUME SERIAL NO. FOR DRIVER TAPE
//*    DRVTYPE=OLD,      STATUS OF DRIVER TAPE
//     DRVUNIT=TAPE,   UNIT TO BE USED FOR DRIVER TAPE             <<
//     DRVLAB=SL,        LABEL TYPE FOR DRIVER TAPE
//     DRVSEQ=1,         SEQUENCE NUMBER ON DRIVER TAPE
//     LISTING='A',      SYSOUT SPECIFICATION FOR ALL LISTINGS       <<
//     DRVRECF=FB,       RECORD FORMAT FOR DRIVER TAPE
//     DRVLREC=200,      RECORD LENGTH FOR DRIVER TAPE
//     DRVBLK=200,       BLOCKSIZE FOR DRIVER TAPE
//     BLANK=' ',
//     FRAMES=ALL,
//     PHCLIB='SYS2.TPL.V6.LOADLIB', <<
//     QUOT=,      * '''',
//     SIMUMBR=SIMULATE,
//     VDIDSN='SYS2.TPL.V6.PCL.VIDEO.DIRECT'
//*********************************************************************
//*                                                                   *
//*      T P L S I M U - - A PROCEDURE FOR PRODUCING A CALCOMP PLOT   *
//*           TO SIMULATE THE PHOTOCOMPOSED TABLES CONTAINED ON A     *
//*           VIDEOCOMP DRIVER TAPE WHICH WAS CREATED USING THE       *
//*           TPLCRTP PROCEDURE.                                      *
//*                                                                   *
//*      THE FOLLOWING PARAMETERS ARE NECESSARY:                      *
//*           DRVPRE='CNaaaa.iii',DRVNAME='tape name',                *
//*           PLTPRE='CNaaaa.iii'                                     *
//*                                                                   *
//*           WHERE:                                                  *
//*           DRVPRE = THE ACCOUNT NUMBER & INITIALS USED FOR THE     *
//*                      VIDEOCOMP DRIVER TAPE.                       *
//*           TAPE    = THE NAME USED TO IDENTIFY THE VIDEOCOMP       *
//*                      DRIVER TAPE.                                 *
//*           PLTPRE = ACCT AND INITIALS TO IDENTIFY THE CALCOMP      *
//*                      PLOT TAPE WHICH THIS PROCEDURE PRODUCES.     *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SIMULATE  EXEC  PGM=&SIMUMBR,REGION=&CORE,
//  PARM=&QUOT.VIDEO.&BLANK.&FRAMES.&QUOT.
//* *****     CREATE CALCOMP PLOT TAPE FOR SIMULATION           *******
//STEPLIB  DD  DSN=&PHCLIB,
//  DISP=SHR
//FT01F001  DD  DDNAME=SYSIN
//SYSIN  DD  DUMMY
//FT03F001  DD  SYSOUT=&LISTING
//FT06F001  DD  SYSOUT=&LISTING
//FT08F001  DD  DSN=&PLTPRE..PLOT.PCL,
//  LABEL=(&PLTSEQ,&PLTLAB,,OUT,&EXPRET=&RETPD),
//  SPACE=(TRK,(10,5),RLSE),DISP=(&PLTTYPE,&PLTDISP,&BADPLOT),
//  UNIT=(&PLTUNIT,,DEFER),DCB=(RECFM=VS,LRECL=360,BLKSIZE=368,DEN=3)
//FT10F001  DD  DUMMY
//FT11F001  DD  DSN=&QUOT.&DRVPRE..PCL.VIDEO.TAPE.&DRVNAME.&QUOT,
//  UNIT=(&DRVUNIT,,DEFER),LABEL=(&DRVSEQ,&DRVLAB,,IN),
//  DCB=(DEN=2,BUFNO=2,DSORG=PS,RECFM=&DRVRECF,LRECL=&DRVLREC,
//  BLKSIZE=&DRVBLK),DISP=(&DRVTYPE,KEEP,KEEP)
//FT12F001  DD  DSN=&VDIDSN,UNIT=&VDIUNIT,DISP=SHR,
//  LABEL=(,,,IN)
//FT15F001  DD  SYSOUT=&LISTING
//*
//*     END OF PROCEDURE TO CREATE A CALCOMP PLOT SIMULATING THE
//*          PHOTOCOMPOSED TABLES PRODUCED BY THE TPL/PCL SYSTEM.
//*
//*  PEND

TPLTOSAS.text:
//TPLTOSAS  PROC  SASCORE=300K, REGION IN WHICH TO RUN SAS           <<
//   LISTING='A',  SYSOUT SPECIFICATION FOR ALL LISTINGS             <<
//   PUNCH='B',    SYSOUT SPECIFICATION FOR PUNCHING CARDS
//   LIBSP=20,     PRIMARY NO. OF TRACKS FOR TEMP. LIBRARY
//   LIBIN=4,      SECONDARY SPACE ALLOCATION FOR TEMP. LIBRARY
//   LIBDIR=20,    NO. OF DIRECTORY BLKS FOR TEMP. LIBRARY
//   ENTRY=SAS,                                                      <<
//   OPTIONS=,
//   SASDSN='SAS.V518.LIBRARY',                                      <<
//   SORT=4
//*********************************************************************
//*                                                                   *
//*  T P L T O S A S  --  TPL-SAS LINKAGE PROCEDURE                   *
//*                                                                   *
//*            THE TPL-SAS LINKAGE WILL TRANSFORM THE OUTPUT FROM     *
//*            UP TO 10 TPL TABLES INTO SAS DATASETS.                 *
//*      IN CASE OF PROBLEMS, CONTACT THE STATISTICAL CONSULTANTS     *
//*         AT THE DIVISION OF GENERAL SYSTEMS, BLS.                  *
//*            TELEPHONE:  (PLACE YOUR AREA CODE & NUMBER HERE)     <<*
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//TPLTOSAS  EXEC  PGM=&ENTRY,PARM='&OPTIONS',REGION=&SASCORE
//LIBRARY  DD  DSN=&LIBRARY,UNIT=WORK,DISP=(NEW,DELETE),
//  SPACE=(TRK,(&LIBSP,&LIBIN,&LIBDIR))
//STEPLIB  DD  DSN=*.LIBRARY,DISP=(SHR,DELETE),VOL=REF=*.LIBRARY
//  DD  DSN=&SASDSN,DISP=SHR
//  DD  DSN=SYS1.PLIBASE,DISP=SHR                                    <<
//WORK  DD  UNIT=WORK,SPACE=(CYL,(20,10))
//FT11F001  DD  SYSOUT=&LISTING
//FT12F001  DD  SYSOUT=&LISTING
//FT13F001  DD  SYSOUT=&PUNCH
//FT15F001  DD  UNIT=WORK,SPACE=(CYL,(1,1)),
//  DCB=(RECFM=FB,LRECL=80,BLKSIZE=800,BUFNO=1)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SORTWK01  DD  UNIT=WORK,SPACE=(CYL,(&SORT),,CONTIG)
//SORTWK02  DD  UNIT=WORK,SPACE=(CYL,(&SORT),,CONTIG)
//SORTWK03  DD  UNIT=WORK,SPACE=(CYL,(&SORT),,CONTIG)
//TBCLS  DD  DSN=&&TBCELLS,DISP=(OLD,PASS),
//  DCB=(RECFM=FB,LRECL=32,BLKSIZE=2976)
//STR  DD  DSN=&&TPLSTR,DISP=(OLD,PASS),
//  DCB=(RECFM=F,LRECL=133,BLKSIZE=3059)
//*     END OF PROCEDURE TO FEED TABULATED DATA FROM TPL INTO THE
//*        SAS SYSTEM FOR FURTHER ANALYSIS.
//*  PEND

TPLTPGEN.text:
//TPLTPGEN  PROC  CBN='?', NAME FOR CODEBOOK OR LIBRARY
//     CBPREFX='SYS2.TPL.V6', INITIALS PREFIX FOR CODEBOOK OR LIBRARY<<
//     DATA=NULLFILE, NAME OF DATA FILE TO BE TABULATED
//     SEQ=1,         FILE SEQUENCE NO. ON ITS VOL. FOR DATA FILE
//     LAB=SL,        TYPE OF LABELS ON ITS VOL. FOR DATA FILE
//     DBGDSN='SYS2.TPL.V6.LOADLIB', NAME FOR DATABASE "DBGEN"       <<
//     DBMS=NONE,     NAME OF DATABASE MANAGEMENT SYSTEM BEING USED  <<
//     LISTING='A',   SYSOUT SPECIFICATION FOR ALL LISTINGS          <<
//     TABLES='A',    SYSOUT SPECIFICATION IF TABLES ARE SEPARATED   <<
//     CORE=500K,     REGION ACTUALLY USED FOR PGMS OF TPL - SORT STEPS
//     TPLMAX=2000K,  MAXIMUM REGION USED DURING TPL THRU SORT STEPS
//     PSRCORE=500K,  REGION SIZE FOR PCLSORT STEP
//     LAYCORE=500K,  REGION ACTUALLY USED FOR PROGRAMS OF LAYOUT STEP
//     LAYMAX=2000K,  MAXIMUM REGION USED DURING LAYOUT STEP
//     WKUN=WORK,   DEVICE TYPE FOR SORT WORKFILES                 <<
//     WKSP=100,      (NOT USED HERE -- RETAINED FOR COMPATIBILITY)
//     CL1UN=WORK,  DEVICE TYPE FOR "TBCLS1" WORKFILE              <<
//     CL1SP=100,     (NOT USED HERE -- RETAINED FOR COMPATIBILITY)
//     CL2UN=WORK,  DEVICE TYPE FOR "TBCLS2" WORKFILE              <<
//     CL2SP=100,     (NOT USED HERE -- RETAINED FOR COMPATIBILITY)
//     CL2IN=1,       (NOT USED HERE -- RETAINED FOR COMPATIBILITY)
//     CL3UN=WORK,  DEVICE TYPE FOR TABLE CELLS WORKFILE           <<
//     CL3SP=100,     (NOT USED HERE -- RETAINED FOR COMPATIBILITY)
//     COORSP=1000,   NO. OF BLKS FOR COORDINATES WORKFILE
//     CONSP=400,     NO. OF BLKS FOR TABLE CONSTRUCTIONS WORKFILE
//     CONIN=80,      SECONDARY ALLOCATION SIZE FOR TABLE CONSTRUC.
//     SPC1SP=200,    INITIAL NO. OF BLKS FOR TABLE SPECS WORKFILE
//     SPC2SP=200,    NO. OF BLKS FOR PCL'S NEW TBSPECS WORKFILE
//     TMPSP=125,     NO OF BLKS FOR "TEMP" WORKFILE
//     TSLDSN='SYS2.TPL.V6.TYPE.STYLES', NAME OF TYPE STYLE LIBRARY<<
//     STYLE=NOPH, WAS=SET1, NAME OF DEFAULT TYPE STYLE    <<
//     DICNAME='BLS.HYPHEN.DICT',  NAME OF DICTIONARY
//     DICPRE='SYS2.TPL.V6', PREFIX FOR DICTIONARY DSNAME            <<
//     PRODSN='SYS2.TPL.V6.DEFAULT.PROFILE', DSNAME OF PROFILE       <<
//     CBKBLK=2976,CBVER=6,
//     CGNDSN='SYS2.TPL.V6.OBJLIB(CGNOBJ)',
//     COBLK=2976,COLRECL=24,CORECFM=FB,
//     CONBLK=3059,CONLREC=133,CONRECF=FB,
//     CONTROL=IEFBR14,CONMAX=100K,
//     DATFREE=100,
//     DSPDSN='SYS2.TPL.V6.OBJLIB(DSPOBJ)',
//     FREE=100,
//     LAYDSN='SYS2.TPL.V6.OBJLIB(LAYOBJ)',
//     LAYOPT=,
//     MODLIB='SYS2.TPL.V6.LOADLIB', <<
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB', <<
//     PCLDSN='SYS2.TPL.V6.OBJLIB(PCLOBJ)',
//     POTDSN='SYS2.TPL.V6.OBJLIB(POTOBJ)',
//     QUOT=,  *'''',
//     RAPDSN='NULLFILE', <<
//     SISDSN='SYS2.TPL.V6.OBJLIB(SISOBJ)',
//     SORTPGM=SORT,                                                 <<
//     SRTLREC=32,SRTBLK=2976,
//     SUBDSN='SYS2.TPL.V6.OBJLIB(SUBOBJ)',
//     SUBOPT=,
//     TOTDSN='NULLFILE',  <<
//     TPLBLK=2976,
//     TPLOPT=,
//     TRNDSN='SYS2.TPL.V6.OBJLIB(TRNOBJ)'
//*********************************************************************
//*                                                                   *
//*      T P L T P G E N - - A PROCEDURE TO GENERATE TABLES WITH TPL. *
//*           DISKS WILL BE USED FOR THE MAJOR WORKFILES.             *
//*                                                                   *
//*      VALUES FOR THE FOLLOWING PARAMETERS MUST BE SUPPLIED:        *
//*      CBN='cccccccc',DATA='zzz',                                   *
//*      CBPREFX='CNaaaa.iii'                                         *
//*                                                                   *
//*      THE TPL STATEMENTS COMPRISING THE REQUEST MUST BE PLACED     *
//*           AFTER A "//TPL.REQUEST" DD CARD.                        *
//*                                                                   *
//*      ANY PCL STATEMENTS MODIFYING THE REQUEST, IF PRESENT, MUST   *
//*           BE PLACED AFTER A "//TPL.CHANGES" DD CARD, WHICH MUST   *
//*           APPEAR AFTER THE TPL REQUEST.                           *
//*                                                                   *
//*      THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.         *
//*                                                                   *
//*      IN CASE OF PROBLEMS, CONTACT THE TPL SYSTEM TEAM.            *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TPLSPEC  DD  DSN=&&TPLSPEC,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC1SP))
//COORD  DD  DSN=&&COORD,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK),
//  SPACE=(&TPLBLK,(&COORSP))
//COORD2  DD  DSN=*.COORD,VOL=REF=*.COORD,DISP=(SHR,PASS)
//CONSTR  DD  DSN=&&CONSTR,UNIT=WORK,DISP=(NEW,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN))
//TBSPECS  DD  DSN=&&TBSPECS,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(&TPLBLK,(&SPC2SP))
//PROFILE  DD  DSN=&PRODSN,DISP=SHR
//*
//*
//TPL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  COND=(0,LT,SYSCNTRL),
//  PARM='FILE=&TPLBLK,FILE2=&CBKBLK,FREE=&DATFREE,MAX=&CORE,&TPLOPT'
//* *****     THE FIRST STEP:  THE TPL TRANSLATION PHASE, THE   *******
//* *****     THE PCL TRANSLATION PHASE, THE POST-TRANSLATION   *******
//* *****     PHASE, AND THE CELL GENERATION PHASE.             *******
//STEPLIB  DD  DSN=&DBGDSN,DISP=SHR
//  DD  DDNAME=&DBMS.LIB
//  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//TOTALLIB  DD  DSN=&TOTDSN,DISP=SHR
//RAPIDLIB  DD  DSN=&RAPDSN,DISP=SHR
//NONELIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&TRNDSN,DISP=SHR
//  DD  DSN=&PCLDSN,DISP=SHR
//  DD  DSN=&POTDSN,DISP=SHR
//  DD  DSN=&DSPDSN,DISP=SHR
//  DD  DSN=&CGNDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT3  DD  DSN=&&CONSTR,DISP=(OLD,PASS),
//  DCB=(RECFM=&CONRECF,LRECL=&CONLREC,BLKSIZE=&CONBLK),
//  SPACE=(&CONBLK,(&CONSP,&CONIN),RLSE)
//FILE1  DD  DSN=&&TPLSPEC,DISP=(OLD,DELETE,KEEP),
//  SPACE=(&TPLBLK,(&SPC1SP),RLSE)
//FILE2  DD  DSN=&QUOT.&CBPREFX..CB.V&CBVER..&CBN.&QUOT,
//  DISP=SHR
//INPUT2  DD  DDNAME=DATA
//DATA  DD  DSN=&QUOT.&DATA.&QUOT,
//  LABEL=(&SEQ,&LAB),DISP=SHR
//SYSIN  DD  DDNAME=REQUEST
//REQUEST  DD  DUMMY
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS)
//FILE5  DD  DSN=&&TEMP,UNIT=WORK,SPACE=(&TPLBLK,(&TMPSP)),
//  DISP=(NEW,DELETE)
//FILE3  DD  DSN=&&COORD,DISP=(OLD,PASS),SPACE=(&TPLBLK,(&COORSP),RLSE)
//SYSPUNCH  DD  DSN=&&PCLSORT,UNIT=WORK,DISP=(NEW,PASS),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//INPUT4  DD  DUMMY
//INPUT3  DD  DSN=&PRODSN,DISP=SHR
//  DD  DDNAME=CHANGES
//CHANGES  DD  DUMMY
//OUTPUT6  DD  DSN=&&TPLSTR,UNIT=WORK,DISP=(NEW,PASS,DELETE),
//  SPACE=(TRK,(1,1),RLSE),DCB=(RECFM=FB,LRECL=133,BLKSIZE=3059)
//OUTPUT4  DD  DUMMY
//INPUT7  DD  DDNAME=INSWITCH
//INSWITCH  DD  DUMMY
//FILE6  DD  DSN=&&TBCLS1,DISP=(NEW,PASS,DELETE),
//  UNIT=&CL1UN,SPACE=(&TPLBLK,(&CL1SP))
//OUTPUT5  DD  DSN=&&SORTCTL,UNIT=WORK,SPACE=(TRK,(1,1)),
//  DISP=(NEW,PASS),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//FILE7  DD  DSN=&&JUNK,DISP=(NEW,DELETE),UNIT=WORK,SPACE=(TRK,(2,2))
//FILE8  DD  DSN=&DICPRE..&DICNAME..DICOBJ,DISP=SHR
//OUTPUT7  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//RAPSYSP  DD  SYSOUT=&LISTING
//*
//*
//PRESORT  EXEC  PGM=&SORTPGM,REGION=&TPLMAX,
//  COND=((0,LT,SYSCNTRL),(0,NE,TPL))
//* *****     THE SORT OF TABLE CELL INFORMATION GENERATED      *******
//* *****     DIRECTLY FROM THE DATA FILE.                      *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//MODLIB  DD  DSN=&MODLIB,DISP=SHR
//SYSIN  DD  DSN=&&SORTCTL,DISP=(OLD,PASS,KEEP)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&TBCLS1,DISP=(OLD,PASS,KEEP),
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTOUT  DD  DSN=&&TBCLS2,DISP=(NEW,PASS,DELETE),
//  UNIT=&CL2UN,SPACE=(&TPLBLK,(&CL2SP,&CL2IN),RLSE),
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTWK01  DD  DSN=&&PREWK1,DISP=(NEW,DELETE,DELETE),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&PREWK2,DISP=(NEW,DELETE,DELETE),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&PREWK3,DISP=(NEW,DELETE,DELETE),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&PREWK4,DISP=(NEW,DELETE,DELETE),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//*
//*
//SUBCELL  EXEC  PGM=&MONITOR,REGION=&TPLMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&CORE,&SUBOPT',
//  COND=((0,LT,SYSCNTRL),(4,LT,TPL),(0,LT,PRESORT))
//* *****     GENERATE CELL INFORMATION DERIVABLE FROM OTHER    *******
//* *****     CELLS IN THE CURRENT REQUEST.                     *******
//STEPLIB  DD  DSN=&MONLIB,DISP=(SHR,PASS)
//PROGRAM  DD  DSN=&SUBDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS)
//FILE6  DD  DSN=&&TBCLS1,DISP=(MOD,PASS),
//  SPACE=(&TPLBLK,(&CL1SP),RLSE)
//FILE7  DD  DSN=&&TBCLS2,UNIT=&CL1UN,DISP=(MOD,PASS),
//  SPACE=(&TPLBLK,(&CL2SP,&CL2IN),RLSE)
//OUTPUT5  DD  DSN=&&SORTCTL,UNIT=WORK,SPACE=(TRK,(1,1)),
//  DISP=(OLD,PASS),DCB=(RECFM=FB,LRECL=80,BLKSIZE=400)
//INPUT7  DD  DUMMY
//*
//*
//PCLSORT  EXEC  PGM=&SORTPGM,REGION=&PSRCORE,
//  COND=((0,LT,SYSCNTRL),(16,LE,TPL),(0,LT,PRESORT))
//* *****     THE PCL COORDINATE RECORD SORT STEP               *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//SYSIN  DD  DSN=&&PCLSORT,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTOUT  DD  DSN=&&COORD,DISP=(OLD,PASS),
//  DCB=(RECFM=&CORECFM,LRECL=&COLRECL,BLKSIZE=&COBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(NEW,PASS,KEEP),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK02  DD  DSN=&&WORK2,DISP=(NEW,PASS,KEEP),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK03  DD  DSN=&&WORK3,DISP=(NEW,PASS,KEEP),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//SORTWK04  DD  DSN=&&WORK4,DISP=(NEW,PASS,KEEP),
//  UNIT=&WKUN,SPACE=(&TPLBLK,(&WKSP),,CONTIG)
//*
//*
//SORT  EXEC  PGM=&SORTPGM,REGION=&TPLMAX,
//  COND=((0,LT,SYSCNTRL),(4,LT,TPL),(4,LT,SUBCELL),(0,LT,PRESORT),
//  (0,LT,PCLSORT))
//* *****     THE TABLE CELL SORT AND DATA REDUCTION STEP       *******
//SORTLIB  DD  DSN=SYS1.SORTLIB,DISP=SHR                             <<
//MODLIB  DD  DSN=&MODLIB,DISP=SHR
//SYSIN  DD  DSN=&&SORTCTL,DISP=(OLD,DELETE,DELETE)
//SYSOUT  DD  SYSOUT=&LISTING
//SORTMSGS  DD  SYSOUT=&LISTING
//SORTIN  DD  DSN=&&TBCLS1,DISP=(OLD,DELETE,DELETE),
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTOUT  DD  DSN=&&NEWCLS,DISP=(NEW,PASS,DELETE),
//  SPACE=(&TPLBLK,(&CL1SP,50),RLSE),UNIT=WORK,             =SORTIN,
//  DCB=(RECFM=FB,LRECL=&SRTLREC,BLKSIZE=&SRTBLK)
//SORTWK01  DD  DSN=&&WORK1,DISP=(OLD,DELETE,DELETE)
//SORTWK02  DD  DSN=&&WORK2,DISP=(OLD,DELETE,DELETE)
//SORTWK03  DD  DSN=&&WORK3,DISP=(OLD,DELETE,DELETE)
//SORTWK04  DD  DSN=&&WORK4,DISP=(OLD,DELETE,DELETE)
//*
//*
//LAYOUT  EXEC  PGM=&MONITOR,REGION=&LAYMAX,
//  PARM='FILE=&TPLBLK,FREE=&FREE,MAX=&LAYCORE,&LAYOPT',
//  COND=((0,LT,SYSCNTRL),(16,LE,TPL),(0,LT,PRESORT),(0,LT,SORT),
//  (0,LT,PCLSORT))
//* *****     FINISH PREPARING TABLE CELLS AND DO POSTCOMPUTES, *******
//* *****     THEN PRINT TABLES                                 *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&SISDSN,DISP=SHR
//  DD  DSN=&LAYDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//FILE2  DD  DSN=&&NEWCLS,DISP=(MOD,DELETE),UNIT=&CL1UN,
//  SPACE=(&TPLBLK,(&CL1SP,&CL2IN))
//FILE3  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//FILE4  DD  DSN=&&TBSPECS,DISP=(OLD,PASS)
//FILE6  DD  DSN=&&TBCELLS,DISP=(NEW,PASS,KEEP),UNIT=&CL3UN,
//  SPACE=(&TPLBLK,(&CL3SP),RLSE)
//FILE7  DD  DSN=&&TBCLS2,DISP=(MOD,DELETE),UNIT=&CL2UN,
//  SPACE=(&TPLBLK,(&CL2SP,&CL2IN),RLSE)
//OUTPUT7  DD  SYSOUT=&TABLES,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//OUTPUT4  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//SYSPUNCH  DD  SYSOUT=&LISTING,DCB=(RECFM=FA,LRECL=133,BLKSIZE=133)
//INPUT5  DD  DSN=&TSLDSN(&STYLE.CON),DISP=(SHR,KEEP)
//INPUT7  DD  DUMMY
//COORD  DD  DSN=&&COORD,DISP=(OLD,DELETE,DELETE)
//*        END OF PROCEDURE FOR GENERATING TABLES WITH TPL.
//*                DISKS ARE USED FOR THE MAJOR WORKFILES.
//*  PEND

TPLTYPE.text:
//TPLTYPE  PROC  STYLE='?',  STYLE OF TYPE STYLE BEING CREATED
//     TSLDSN='???', TYPE STYLE LIBRARY DSNAME (REQUIRED)
//     TSLDISP=NEW,  STATUS (NEW OR OLD) OF TYPE STYLE LIBRARY.
//     TSLCAT=CATLG, TO PERMIT CATALOGING OF TYPE STYLE LIBRARY
//     TSLSP=80,     NUMBER OF BLOCKS FOR TYPE STYLE LIBRARY
//     TSLDIR=1,     NUMBER OF DIRECTORY BLOCKS FOR TYPE STYLE LIBRARY
//     TSLIN=40,     SECONDARY ALLOCATION BLOCKS FOR TYPE STYLE LIBRARY
//     TSLRLSE=,     USED TO RELEASE UNUSED SPACE IN TYPE STYLE LIBRARY
//     TYPDSN='&&TYPEOUT',  DATASET NAME FOR TYPEOUT WORKFILE
//     TYPSP=80,     NUMBER OF BLOCKS FOR TYPEOUT WORKFILE
//     TYPIN=40,     SECONDARY ALLOCATION BLOCKS FOR TYPEOUT
//     TYPUNIT=WORK,  TYPE OF DEVICE FOR TYPEOUT WORKFILE
//     LISTING='A',  SYSOUT SPECIFICATION FOR ALL LISTINGS           <<
//     SETCORE=500K, REGION ACTUALLY USED FOR PROGRAM OF TYPE STEP
//     TYPMAX=2000K, MAXIMUM CORE USED DURING TYPE STEP
//     GWCORE=200K,  REGION SIZE FOR GETWIDTH STEP
//     CONTROL=IEFBR14,CONMAX=100K,
//     COPYPGM=IEBGENER,
//     CPYCORE=150K,
//     TSLBLK=2560,TSLLREC=256,TSLRECF=FB,
//     FREE=100,
//     GCDDSN='SYS2.TPL.V6.PROCLIB(TPLTYPCD)',
//     MONITOR=TPLMONTR,
//     MONLIB='SYS2.TPL.V6.LOADLIB',
//     PHCLIB='SYS2.TPL.V6.LOADLIB',
//     SETDSN='SYS2.TPL.V6.OBJLIB(SETOBJ)',
//     TPGDMBR=TYPEGRID,
//     TYPOPT=,
//     VDIDSN='SYS2.TPL.V6.PCL.VIDEO.DIRECT'
//*********************************************************************
//*                                                                   *
//*      T P L T Y P E  - -  A PROCEDURE FOR ESTABLISHING A TYPE      *
//*               STYLE SET WITHIN THE TYPE STYLE LIBRARY; THE TYPE   *
//*               STYLE SET DESCRIBES A SET OF TYPE FONT CHARAC-      *
//*               TERISTICS, WHICH ARE USED FOR PHOTOCOMPOSING TABLES *
//*               WITH THE TPL SYSTEM.                                *
//*                                                                   *
//*       THE FOLLOWING PARAMETERS MUST BE SUPPLIED:                  *
//*            STYLE=stylename,TSLDSN=tslibname                       *
//*                                                                   *
//*                                                                   *
//*       THE NAME GIVEN TO THE STYLE MUST BE NO MORE THAN            *
//*            FOUR (4) CHARACTERS LONG, AND BEGIN WITH A LETTER.     *
//*                                                                   *
//*       THE STYLE SPECIFICATION STATEMENTS MUST BE PLACED           *
//*           FOLLOWING A "//TYPE.STYLE  DD" CARD.                    *
//*                                                                   *
//*       THE CONTROL RECORD FOR THE LAYOUT PROGRAM MUST BE PLACED    *
//*           FOLLOWING A "//CONTROL.CARD  DD" CARD.                  *
//*                                                                   *
//*       TO CATALOG THE TYPE STYLE LIBRARY PRODUCED BY THIS          *
//*            PROCEDURE, ADD THE PARAMETER:  TSLCAT='CATLG'          *
//*                                                                   *
//*       TO ADD A NEW TYPE STYLE TO AN EXISTING TYPE STYLE LIBRARY,  *
//*            SPECIFY:  TSLDISP=OLD                                   *
//*                                                                   *
//*       TO RELEASE ANY UNUSED SPACE IN THE TYPE STYLE LIBRARY,      *
//*            ADD THE PARAMETER:  TSLRLSE=',RLSE'                    *
//*            (NOTE THAT THE COMMA IS REQUIRED.)                     *
//*                                                                   *
//*       THIS PROCEDURE EXECUTES VERSION 6 OF THE TPL SYSTEM.        *
//*                                                                   *
//*       IN CASE OF PROBLEMS, CONTACT THE  TPL SYSTEM TEAM.          *
//*            TELEPHONE:  357-5999                                   *
//*                                          LAST UPDATED ON 08/21/84 *
//*********************************************************************
//*
//*
//SYSCNTRL  EXEC  PGM=&CONTROL,REGION=&CONMAX
//* *****     ALLOCATE WORKING DATASETS                         *******
//TYPEOUT  DD  DSN=&TYPDSN,UNIT=&TYPUNIT,DISP=(NEW,PASS),
//  DCB=(RECFM=&TSLRECF,LRECL=&TSLLREC,BLKSIZE=&TSLBLK),
//  SPACE=(&TSLBLK,(&TYPSP,&TYPIN))
//TSLIB  DD  DSN=&TSLDSN,
//  DISP=(&TSLDISP,&TSLCAT,KEEP),
//  DCB=(RECFM=&TSLRECF,LRECL=&TSLLREC,BLKSIZE=&TSLBLK),
//  SPACE=(&TSLBLK,(&TSLSP,&TSLIN,&TSLDIR))
//*
//*
//TYPE  EXEC  PGM=&MONITOR,REGION=&TYPMAX,
//  PARM='FREE=&FREE,MAX=&SETCORE,&TYPOPT',
//  COND=((0,LT,SYSCNTRL))
//* *****     CREATE THE TYPE STYLE "TYPEOUT" WORKFILE          *******
//STEPLIB  DD  DSN=&MONLIB,DISP=SHR
//PROGRAM  DD  DSN=&SETDSN,DISP=SHR
//SYSPRINT  DD  SYSOUT=&LISTING
//OUTPUT3  DD  SYSOUT=&LISTING
//SYSPUNCH  DD  DSN=&TYPDSN,DISP=(OLD,PASS,KEEP),
//  DCB=(RECFM=&TSLRECF,LRECL=&TSLLREC,BLKSIZE=&TSLBLK),
//  SPACE=(&TSLBLK,(&TYPSP,&TYPIN),RLSE)
//SYSIN  DD  DDNAME=STYLE
//STYLE  DD  DUMMY
//*
//*
//GETWIDTH  EXEC  PGM=&TPGDMBR,REGION=&GWCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,TYPE))
//* *****     GET CHARACTER WIDTHS AND FINISH CREATION OF THE   *******
//* *****          TEMPORARY STYLE DATASET.                     *******
//STEPLIB  DD  DSN=&PHCLIB,DISP=SHR
//FT03F001  DD  SYSOUT=&LISTING
//FT06F001  DD  SYSOUT=&LISTING
//FT10F001  DD  DSN=&TYPDSN,DISP=(OLD,DELETE)
//FT11F001  DD  DSN=&&TMPSTYL,UNIT=WORK,DISP=(NEW,PASS,KEEP),
//  DCB=(RECFM=&TSLRECF,LRECL=&TSLLREC,BLKSIZE=&TSLBLK),
//  SPACE=(&TSLBLK,(&TSLSP,&TSLIN),RLSE)
//FT13F001  DD  DSN=&VDIDSN,DISP=SHR,
//  LABEL=(,,,IN)
//FT15F001  DD  SYSOUT=&LISTING
//*
//*
//COPYTYPE  EXEC  PGM=&COPYPGM,REGION=&CPYCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,TYPE),(0,LT,GETWIDTH))
//* *****     COPY THE TEMPORARY STYLE DATASET INTO THE LIBRARY *******
//* *****        AS MEMBER "TYPE" (WITH "STYLE" NAME APPENDED). *******
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DUMMY
//SYSUT1  DD  DSN=&TMPSTYL,DISP=(OLD,DELETE,DELETE)
//SYSUT2  DD  DSN=&TSLDSN(&STYLE.TYPE),
//  DISP=(OLD,PASS,KEEP),SPACE=(&TSLBLK,(&TSLSP,&TSLIN,&TSLDIR)),
//  DCB=(RECFM=&TSLRECF,LRECL=&TSLLREC,BLKSIZE=&TSLBLK)
//*
//*
//CONTROL  EXEC  PGM=&COPYPGM,REGION=&CPYCORE,
//  COND=((0,LT,SYSCNTRL),(0,LT,TYPE),(0,LT,GETWIDTH),(0,LT,COPYTYPE))
//* *****     COPY THE CONTROL "CARD" DATASET INTO THE FONTPDS  *******
//* *****        AS MEMBER "CON" (WITH "STYLE" NAME APPENDED).  *******
//SYSPRINT  DD  SYSOUT=&LISTING
//SYSIN  DD  DSN=&GCDDSN,DISP=SHR
//SYSUT1  DD  DDNAME=CARD
//CARD  DD  DUMMY
//SYSUT2  DD  DSN=&TSLDSN(&STYLE.CON),
//  DISP=(OLD,KEEP,KEEP),
//  SPACE=(&TSLBLK,(&TSLSP,&TSLIN,&TSLDIR)&TSLRLSE),
//  DCB=(RECFM=&TSLRECF,LRECL=&TSLLREC,BLKSIZE=&TSLBLK)
//*      END OF PROCEDURE FOR ESTABLISHING A TYPE FONT MATRIX FILE FOR
//*           USE IN PHOTOCOMPOSING TABLES.
//*  PEND
