//SYSPUNCH DD   DUMMY
//SYSLIN   DD   DSN=&&OBJSET,UNIT=SYSDA,SPACE=(80,(200,50)),
//             DISP=(MOD,PASS)
//SYSIN DD DSN=MSTITT.CBT000.FILE000.PDS(G207DCAN),DISP=SHR
