ABEND878.text:
//BC0THORG JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(ABEND878)                       *
//* MAP USER REGION BELOW 16M LINE FROM IPCS SUMMARY FORMAT AND     *
//*    VERBX VSMDATA 'NOGLOBAL' AS IN PDS.JCL(IPCSBTCH)             *
//*        FOR THE DUMP DATASET SYS1.DUMP12                         *
//* CHECK JOBCLASS FOR THE SYSTEM TO RUN ON                         *
//*******************************************************************
//*
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD   SYSOUT=*
//SYSIN    DD   *
           DELETE (BC0THOR.TST.IPCS)
//*
//IPCS     EXEC PGM=IKJEFT01,DYNAMNBR=20,REGION=0M
//IPCSDDIR DD   DSN=BC0THOR.TST.IPCSDDIR,DISP=OLD
//IPCSPARM DD   DSN=WRKBNCH.ISPF.IPCS.PARMLIB,DISP=SHR
//SYSPROC  DD   DSN=SYS1.SBLSCLI0,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//SYSTSPRT DD   DSN=BC0THOR.TST.IPCS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(CYL,(10,2),RLSE)
//SYSTSIN  DD   *
PROFILE MSGID
IPCS NOPARM
SETDEF DSN('SYS1.DUMP12') LIST
DROPDUMP
SETDEF DSN('SYS1.DUMP12') LIST
SETDEF NOCONFIRM
        STATUS
        SUMMARY FORMAT REGISTERS
        VERBX VSMDATA 'NOGLOBAL'
SETDEF CONFIRM
END
