CICSSCAN.text:
//BC0THORD JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(CICSSCAN)                       *
//* SEARCH CICS REPORT FROM ZACH WHITESEL FOR CICS PROGRAMS BY GROUP*
//* CICSSCAN --> CICSEXPN --> SORT --> PULLDUPS --> PDSDIRB --> SORT*
//* CICSJOIN                                                        *
//*******************************************************************
//DELETE   EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.DEL.ALLCICS
 DELETE BC0THOR.DEL.CICSUNIQ
 DELETE BC0THOR.DEL.CICSUNDU
 DELETE BC0THOR.DEL.CICSNON1
 DELETE BC0THOR.DEL.CICSDUPL
 DELETE BC0THOR.DEL.CICSDUP1
 DELETE BC0THOR.DEL.CICSDIRS
 DELETE BC0THOR.DEL.CICSPGDS
 DELETE BC0THOR.DEL.CICSRGNS
//*
//CICSSCAN EXEC  PGM=CICSSCAN
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//PRINT1   DD    SYSOUT=*
//INPUT1   DD    DSN=BC0THOR.PDS.DATA(CICSGRPS),DISP=SHR
//INPUT2   DD    DSN=BC0THOR.TST.CSDPRNT2,DISP=SHR
//OUTPUT1  DD    DSN=&&COPY,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(30,3),RLSE),
//         DSORG=PS,RECFM=FB,LRECL=80
//*
//CICSEXPN EXEC  PGM=CICSEXPN
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//INPUT1   DD    DSN=&&COPY,DISP=(OLD,PASS,DELETE)
//OUTPUT1  DD    DSN=&&COPY2,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(30,3),RLSE),
//         DSORG=PS,RECFM=FB,LRECL=80
//*
//SORT     EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD   SYSOUT=*
//SORTIN   DD   DSN=&&COPY2,DISP=(OLD,PASS,DELETE)
//SORTOUT  DD   DSN=BC0THOR.DEL.ALLCICS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK01 DD   UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD   UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD   UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD   UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD   UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD   UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD   SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,26,CH,A)
//*
//PULLDUPS EXEC  PGM=PULLDUPS,PARM='1,8' KEY IN POS 1, 8 BYTES LONG
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//SYSPRINT DD    SYSOUT=*
//SYSIN    DD    DISP=SHR,DSN=BC0THOR.DEL.ALLCICS
//UNIQUES  DD    DISP=(,CATLG),DSN=BC0THOR.DEL.CICSUNIQ,
//         UNIT=DISK,SPACE=(TRK,(10,3),RLSE),
//         RECFM=FB,LRECL=80
//UNIQDUPS DD    DISP=(,CATLG),DSN=BC0THOR.DEL.CICSUNDU,
