COBLRUN.text:
//BC0THORD JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=104M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*               BC0THOR.PDS.JCL(COBLRUN)                          *
//* THIS JOB EXECUTES THE LOAD MODULE CREATED BY THE COBLJCL JOB    *
//* WHEN IT IS MODIFIED TO PLACE THE LOAD MODULE IN THE             *
//* BC0THOR.LOAD.LOAD LOAD LIBRARY.                                 *
//*******************************************************************
//*
//RUNTEST  PROC RUNLIB=,RUNPROG=
//DELDEFIN EXEC PGM=IDCAMS
//SYSPRINT DD   SYSOUT=*
//SYSIN    DD   DSN=BC0THOR.PDS.DATA(RUNTEST1),DISP=SHR
//*
//REPRO    EXEC PGM=IDCAMS
//SYSPRINT DD   SYSOUT=*
//SYSUDUMP DD   SYSOUT=*
//VSAM01   DD   DSN=BC0THOR.TST.VSAM01,DISP=SHR
//VSAM02   DD   DSN=BC0THOR.TST.VSAM02,DISP=SHR
//SYSIN    DD   DSN=BC0THOR.PDS.DATA(RUNTEST2),DISP=SHR
//INFILE1  DD   DSN=BC0THOR.PDS.DATA(RUNTEST3),DISP=SHR
//INFILE2  DD   DSN=BC0THOR.PDS.DATA(RUNTEST4),DISP=SHR
//*
//TESTPROG EXEC PGM=&RUNPROG
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//*TEPLIB  DD   DSN=&&LOADLIB,DISP=(OLD,DELETE,DELETE)
//         DD   DSN=&RUNLIB,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//SYSOUT   DD   SYSOUT=*
//PRINT1   DD   SYSOUT=*
//VSAM01   DD   DSN=BC0THOR.TST.VSAM01,DISP=SHR
//VSAM02   DD   DSN=BC0THOR.TST.VSAM02,DISP=SHR
//INPUT1   DD   DSN=BC0THOR.PDS.DATA(RUNTEST5),DISP=SHR
//         PEND
//*
//RUNTEST  EXEC RUNTEST,RUNLIB=CEE.SCEERUN,RUNPROG=TESTPRG2

COBLSETY.text:
//BC0THORB JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//********************************************************************
//*                  BC0THOR.PDS.JCL(COBLSETY)                       *
//* MERGES THE LANGUAGE TRANSLATOR NAME FROM THE COBLTYPE PROGRAM'S  *
//* FILE INTO THE CSECTLST PROGRAM'S FILE. BOTH FILES ARE SORTED IN  *
//* MEMBER NAME - CSECT NAME ORDER.                                  *
//*                                                                  *
//* FILES USED BY THIS PROGRAM INCLUDE THE FOLLOWING:                *
//*     DDNAME INPUT1: OUTPUT FILE FROM CSECTLST PROGRAM             *
//*     DDNAME INPUT2: OUTPUT FILE FROM COBLTYPE PROGRAM             *
//*     DDNAME OUTPUT1: COBLSETY OUTPUT FILE                         *
//*                                                                  *
//*RUN ORDER: PDSDIRA -> CSECTLST -> COBLSETY -> COBLSUBR -> COBSUBEM*
//*                                                                  *
//********************************************************************
//*
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.TST.COBLSETY
//*
//SORT1    EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TST.CSECTS,DISP=SHR
//SORTOUT  DD DSN=&&CSECTOUT,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(300,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,8,CH,A,11,18,CH,A)
//*
//SORT2    EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TST.COBLTYPE,DISP=SHR
//SORTOUT  DD DSN=&&TYPEOUT,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(300,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,8,CH,A,10,17,CH,A)
//*
//COBLSET1 EXEC PGM=COBLSETY
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=*
//INPUT1   DD DSN=&&CSECTOUT,DISP=(OLD,PASS)
//INPUT2   DD DSN=&&TYPEOUT,DISP=(OLD,PASS)
//OUTPUT1  DD DSN=BC0THOR.TST.COBLSETY,DISP=(NEW,CATLG,CATLG),
//         UNIT=DISK,SPACE=(TRK,(300,150),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0

COBLSORT.text:
//BC0THORS JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(COBLSORT)                       *
//* SIMPLE COBOL PROGRAM USED TO DEMO THE COBOL INTERNAL SORT       *
//*******************************************************************
//DELETE   EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.DEL.SORTED
//*
//COBLSORT EXEC  PGM=COBLSORT
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//SYSOUT   DD    SYSOUT=*
//INPUT1   DD    DSN=NULLFILE
//*NPUT1   DD    DSN=BC0THOR.PDS.DATA(CFUNDESC),DISP=SHR
//SORTWK01 DD    UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK02 DD    UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK03 DD    UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK04 DD    UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK05 DD    UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK06 DD    UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//OUTPUT1  DD    DSN=BC0THOR.DEL.SORTED,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE),
//         DSORG=PS,RECFM=FB,LRECL=80

COBLSUBR.text:
//BC0THORC JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(COBLSUBR)                       *
//*                                                                 *
//*READS FILE OF LOAD LIBRARY CSECT DATA CREATED BY PROGRAM CSECTLST*
//*AND WRITES A FILE WHICH CAN BE USED TO PRINT THE SUBROUTINE INFO *
//*LISTING. OUTPUT FROM THIS PROGRAM IS ONE RECORD FOR EACH CSECT   *
//*WHOSE NAME DOES NOT BEGIN WITH "ILB". EACH RECORD CONTAINS THE   *
//*THE DSNAME, THE CSECT NAME, THE MEMBER NAME, AND TWO INDICATORS  *
//*THAT SHOW WHETHER THE MEMBER CONTAINS ANY "ILB" CSECTS AND       *
//*WHETHER THIS IS A MAIN ENTRY POINT OR A SUBROUTINE WITHIN THE    *
//*MODULE. THE OUTPUT FILE IS INTENDED TO BE SORTED IN CSECT ORDER  *
//*AND WILL THEN BE INPUT TO PROGRAM COBSUBEM.                      *
//*                                                                 *
//*RUN ORDER: PDSDIRA -> CSECTLST -> COBLSUBR -> COBSUBEM           *
//*                                                                 *
//*******************************************************************
//*
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.TST.COBLSUBR NONVSAM
//*
//COBLSUBB EXEC  PGM=COBLSUBR
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//OUTPUT1  DD    DSN=&&UNSORT,DISP=(,PASS),
//         UNIT=DISK,SPACE=(CYL,(20,5),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//INPUT1   DD    DSN=BC0THOR.TST.CSECTS,DISP=SHR
//*
//SORT     EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=&&UNSORT,DISP=(OLD,PASS)
//SORTOUT  DD DSN=BC0THOR.TST.COBLSUBR,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(CYL,(20,5),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,1)
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,1)
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,1)
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,1)
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,1)
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,1)
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,9,CH,A)

COBLTYPE.text:
//BC0THORD JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(COBLTYPE)                       *
//* READS A LOAD MODULE PDS AND WRITES A FILE OF RECORDS SHOWING THE*
//* MEMBER NAME, CSECT NAME, TRANSLATOR NAME, TRANSLATOR VERSION AND*
//* MOD LEVEL AND COMPILED DATE. OUTPUT RECORDS ARE 80 BYTES LONG   *
//*   DDNAME SYSLIB: RECFM=U. INPUT LOAD MODULE LIBRARY.            *
//*   DDNAME SYSOUT: : RECFM=FBA,LRECL=80 MEMBER/CSECT/ETC OUTPUT   *
//*******************************************************************
//*
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
   DELETE BC0THOR.DEL.COBLTYPE
//*******************************************************************
//* WRITE FILE OF CSECT AND LANGUAGE DATA FOR THE SYSLIB FILE       *
//*******************************************************************
//COBLTYP1 EXEC PGM=COBLTYPE,PARM='BC0THOR.LOAD.LOAD'
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=*
//SYSLIB   DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSOUT   DD DSN=&&COBLTYPE,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(300,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//*******************************************************************
//* SORT RECORDS IN LOAD LIBRARY DSNAME AND MEMBER ORDER            *
//*******************************************************************
//SORT     EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=&&COBLTYPE,DISP=(OLD,DELETE,KEEP)
//SORTOUT  DD DSN=BC0THOR.DEL.COBLTYPE,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE),VOL=SER=TECH02,
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,52,CH,A)

