//         UNIT=DISK,SPACE=(TRK,(60,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0

COBOL2.text:
//BC0THORF JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
