//         DCB=(RECFM=FB,LRECL=80,BLKSIZE=800)
//SYSIN    DD  DSN=BC0THOR.PDS.COBOL(RKM7040T),DISP=SHR
//SYSLIB   DD  DSN=EMVSP.OUTPUT.FILEDESC,DISP=SHR
//         DD  DSN=DB2PROD.COPYLIB,DISP=SHR
//         DD  DSN=EMVSQ.OUTPUT.FILEDESC,DISP=SHR
//         DD  DSN=DB2TEST.COPYLIB,DISP=SHR
//         DD  DSN=CB0RBAL.TSO.P935231.FILEDESC,DISP=SHR
//         DD  DSN=BC0THOR.PDS.COBOL,DISP=SHR
//SYSUT1   DD  SPACE=(800,(500,500),,,ROUND),UNIT=(DISK,6)
//SYSUT2   DD  SPACE=(800,(500,500),,,ROUND),UNIT=(DISK,6)
//SYSUT3   DD  SPACE=(800,(500,500),,,ROUND),UNIT=(DISK,6)
//SYSTERM  DD  SYSOUT=Y
//SYSUDUMP DD  SYSOUT=Y
//SYSPRINT DD  SYSOUT=Y
//**************************************************/
//**   VS COBOL II REL 3.0 XPEDITER COBOL COMPILE  */
//**************************************************/
//COB      EXEC PGM=ADSIR200,PARM=(NOADV,'NUMPROC(MIG)','TRUNC(BIN)',
//         APOST,RES,RENT,SSR,X,MAP,OFF,TERM,NOSEQ,VBREF,LIB),
//         REGION=2048K,COND=(4,LT,DB2PRE)
//STEPLIB  DD  DSN=SYS1.COB2COMP,DISP=SHR
//         DD  DSN=SPP.XPEDITER.PROD.LOADLIB,DISP=SHR
//SYSIN1   DD  UNIT=DISK,SPACE=(CYL,(10,5))
//SYSIN2   DD  UNIT=DISK,SPACE=(CYL,(10,5))
//SYSUT1   DD  UNIT=VIO,SPACE=(CYL,(10,5))
//SYSUT2   DD  UNIT=VIO,SPACE=(CYL,(10,5))
//SYSUT3   DD  UNIT=VIO,SPACE=(CYL,(10,5))
//SYSUT4   DD  UNIT=VIO,SPACE=(CYL,(10,5))
//SYSUT5   DD  UNIT=VIO,SPACE=(CYL,(10,5))
//SYSUT6   DD  UNIT=VIO,SPACE=(CYL,(10,5))
//SYSUT7   DD  UNIT=VIO,SPACE=(CYL,(10,5))
//SYSUDUMP DD SYSOUT=*
