//         DD  DSN=UCC10.IP3R20.LOADLIB,DISP=SHR
//SYSLMOD  DD  DSN=BC0THOR.LOAD.LOAD,DISP=(SHR,KEEP,KEEP)
//********************* BIND FOR DB2 ************************
//BIND    EXEC PGM=IKJEFT01,COND=(8,LT)
//SYSTSPRT DD  SYSOUT=Y
//SYSPRINT DD  SYSOUT=Y
//SYSUDUMP DD  SYSOUT=Y
//SYSTSIN  DD  *
     DSN SYSTEM(DB2
     BIND PLAN(RKM7040) -
     QUALIFIER(DB2TEST) -
     MEMBER(RKM7040) -
     LIBRARY('DB2TEST.DBRMLIB', -
                        )-
     ACTION(REPLACE) RETAIN VALIDATE(RUN) -
     ISOLATION(CS) -
     EXPLAIN(YES) -
     FLAG(I) -
     ACQUIRE(USE) RELEASE(COMMIT)
     END
//GRANTS EXEC PGM=IKJEFT1A,DYNAMNBR=100
//SYSTSPRT DD SYSOUT=*
//SYSPRINT DD SYSOUT=*
//SYSUDUMP DD SYSOUT=*
//SYSOUT   DD SYSOUT=*
//SYSTSIN  DD *
   DSN SYSTEM(DB2)
       RUN PROGRAM(DSNTIAD)
   END
//SYSIN  DD *
    GRANT BIND,EXECUTE ON PLAN RKM7040 TO PUBLIC

