CSECTLST.text:
//BC0THORC JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//********************************************************************
//*                  BC0THOR.PDS.JCL(CSECTLST)                       *
//* READS A CONTROL CARD INPUT DATASET. EACH 80-BYTE INPUT RECORD HAS*
//* A MEMBER NAME IN COLUMNS 1-8. FOR EACH CONTROL CARD MEMBER NAME: *
//*  .ISSUES A BLDL TO THE SYSLIB DATASET TO OBTAIN THE DIRECTORY ENT*
//*  .READS THE LOAD MODULE DATA RECORDS TO BUILD A SYM/RLD TABLES.  *
//*  .WRITE ONE OUTPUT RECORD FOR EACH CSECT GIVING MEMBER AND CSECT.*
//*   NAMES, LENGTH, LANGUAGE, AND * IF IT IS THE ENTRY POINT CSECT. *
//* THE PDSDIRA JOB CAN BE USED TO GET PDS DIRECTORY NAMES.          *
//*                                                                  *
//* FILES USED BY THIS PROGRAM INCLUDE THE FOLLOWING:                *
//*     DDNAME SYSIN:    RECFM=FB,LRECL=80 - INPUT CONTROL CARD FILE.*
//*     DDNAME SYSLIB:   RECFM=U. INPUT LOAD MODULE LIBRARY.         *
//*     DDNAME SYSOUT:   RECFM=FB,LRECL=80 - MODULE/CSECT RECORDS.   *
//*     DDNAME SYSPRINT: RECFM=FBA,LRECL=133 - MESSAGES              *
//*                                                                 *
//*RUN ORDER: PDSDIRA -> CSECTLST -> COBLSUBR -> COBSUBEM           *
//*                                                                 *
//********************************************************************
//*
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.TST.OUTPUT.CSECTS
 DELETE BC0THOR.TST.CICS.CSECTS
 DELETE BC0THOR.TST.IMS.CSECTS
 DELETE BC0THOR.TST.HCS.CSECTS
 DELETE BC0THOR.TST.ALL.CSECTS
//*******************************************************************
//* WRITE DIRECTORY FILE FOR EMVSP.OUTPUT.LOADLIB                   *
//*******************************************************************
//PDSDIR01 EXEC PGM=PDSDIR,PARM='EMVSP.OUTPUT.LOADLIB'
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//INPUT1   DD DISP=SHR,DSN=EMVSP.OUTPUT.LOADLIB
//OUTPUT1  DD DSN=&&DIRECT01,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE),
//         DCB=(RECFM=FB,LRECL=80,BLKSIZE=0)
//******************************************************************
//* WRITE FILE WITH ONE RECORD PER CSECT IN EMVSP.OUTPUT.LOADLIB   *
//******************************************************************
//CSECTLS1 EXEC PGM=CSECTLST
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=*
//ABNLDUMP DD SYSOUT=*
//SYSLIB   DD DSN=EMVSP.OUTPUT.LOADLIB,DISP=SHR
//SYSPRINT DD SYSOUT=*,BLKSIZE=0
//SYSOUT   DD DSN=BC0THOR.TST.OUTPUT.CSECTS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(300,150),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SYSIN    DD DSN=&&DIRECT01,DISP=(OLD,PASS)
//*******************************************************************
//* WRITE DIRECTORY FILE FOR EMVSP.CICS.LOADLIB                     *
//*******************************************************************
//PDSDIR02 EXEC PGM=PDSDIR,PARM='EMVSP.CICS.LOADLIB'
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//INPUT1   DD DISP=SHR,DSN=EMVSP.CICS.LOADLIB
//OUTPUT1  DD DSN=&&DIRECT02,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE),
//         DCB=(RECFM=FB,LRECL=80,BLKSIZE=0)
//******************************************************************
//* WRITE FILE WITH ONE RECORD PER CSECT IN EMVSP.CICS.LOADLIB     *
//******************************************************************
//CSECTLS2 EXEC PGM=CSECTLST
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=*
//ABNLDUMP DD SYSOUT=*
//SYSLIB   DD DSN=EMVSP.CICS.LOADLIB,DISP=SHR
//SYSPRINT DD SYSOUT=*,BLKSIZE=0
//SYSOUT   DD DSN=BC0THOR.TST.CICS.CSECTS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(300,150),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SYSIN    DD DSN=&&DIRECT02,DISP=(OLD,PASS)
//*******************************************************************
//* WRITE DIRECTORY FILE FOR EMVSP.IMSVS.PGMLIB                     *
//*******************************************************************
//PDSDIR03 EXEC PGM=PDSDIR,PARM='EMVSP.IMSVS.PGMLIB'
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//INPUT1   DD DISP=SHR,DSN=EMVSP.IMSVS.PGMLIB
//OUTPUT1  DD DSN=&&DIRECT03,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE),
//         DCB=(RECFM=FB,LRECL=80,BLKSIZE=0)
//******************************************************************
//* WRITE FILE WITH ONE RECORD PER CSECT IN EMVSP.IMSVS.PGMLIB     *
//******************************************************************
//CSECTLS3 EXEC PGM=CSECTLST
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=*
//ABNLDUMP DD SYSOUT=*
//SYSLIB   DD DSN=EMVSP.IMSVS.PGMLIB,DISP=SHR
//SYSPRINT DD SYSOUT=*,BLKSIZE=0
//SYSOUT   DD DSN=BC0THOR.TST.IMS.CSECTS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(300,150),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SYSIN    DD DSN=&&DIRECT03,DISP=(OLD,PASS)
//*******************************************************************
//* WRITE DIRECTORY FILE FOR EMVSP.HCS.LOADLIB                      *
//*******************************************************************
//PDSDIR04 EXEC PGM=PDSDIR,PARM='EMVSP.HCS.LOADLIB'
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//INPUT1   DD DISP=SHR,DSN=EMVSP.HCS.LOADLIB
//OUTPUT1  DD DSN=&&DIRECT04,DISP=(,PASS),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE),
//         DCB=(RECFM=FB,LRECL=80,BLKSIZE=0)
//******************************************************************
//* WRITE FILE WITH ONE RECORD PER CSECT IN EMVSP.HCS.LOADLIB      *
//******************************************************************
//CSECTLS4 EXEC PGM=CSECTLST
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=*
//ABNLDUMP DD SYSOUT=*
//SYSLIB   DD DSN=EMVSP.HCS.LOADLIB,DISP=SHR
//SYSPRINT DD SYSOUT=*,BLKSIZE=0
//SYSOUT   DD DSN=BC0THOR.TST.HCS.CSECTS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(300,150),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SYSIN    DD DSN=&&DIRECT04,DISP=(OLD,PASS)
//******************************************************************
//*    SORT RECORDS IN LOAD LIBRARY DSNAME AND MEMBER NAME ORDER   *
//******************************************************************
//SORT     EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TST.OUTPUT.CSECTS,DISP=OLD
//         DD DSN=BC0THOR.TST.CICS.CSECTS,DISP=OLD
//         DD DSN=BC0THOR.TST.IMS.CSECTS,DISP=OLD
//         DD DSN=BC0THOR.TST.HCS.CSECTS,DISP=SHR
//SORTOUT  DD DSN=BC0THOR.TST.ALL.CSECTS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(75,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(38,43,CH,A,1,8,CH,A)

CSECTMCH.text:
//BC0THORC JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
