//         DSORG=PO,RECFM=FB,LRECL=80

CVTEXPND.text:
//BC0THORI JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(CVTXPND)                        *
//*    HIGH-LEVEL ASSEMBLY TO EXPAND THE CVT FOR DOCUMENTATION      *
//*******************************************************************
//ASM      EXEC PGM=ASMA90,PARM='OBJECT,XREF(SHORT)'
//SYSLIB   DD DSN=SYS1.MACLIB,DISP=SHR
//SYSUT1   DD DSN=&&SYSUT1,UNIT=DISK,SPACE=(CYL,(10,5))
//SYSPRINT DD SYSOUT=*
//*YSPRINT DD DSN=BC0THOR.PDS.CNTLBLOX(CVT),DISP=SHR
//SYSPUNCH DD DUMMY,DCB=BLKSIZE=80
//SYSLIN   DD DSN=&&LOADSET,DISP=(,PASS),
//         UNIT=DISK,SPACE=(CYL,(5,1),RLSE),
//         DCB=(BLKSIZE=400)
//SYSIN    DD *
***********************************************************************
* EXPANSION OF THE CVT CONTROL BLOCK                                  *
* THE CVT MAPPING MACRO IS IN SYS1.MACLIB                             *
***********************************************************************
ANYNAME  CSECT
         TITLE 'CVT DEFINITION'
         CVT DSECT=YES,PREFIX=NO,LIST=YES,SYS=
ANYNAME  CSECT
         END

