//* MATCHES THE PRODUCTION LOAD MODULE FILE FROM EDGESTRP WITH THE  *
//* ENDEVOR ELEMENT FILE FROM ENDVDATA. BOTH FILES ARE SORTED       *
//* BEFORE USE. THREE OUTPUT FILES ARE WRITTEN: (1) THE MATCHED     *
//* FILE, (2) UNMATCHED REMAINING ELEMENTS FILE, AND (3) UNMATCHED  *
//* REMAINING LOAD MODULES FILE. THESE FILES WILL NEXT BE USED BY   *
//* THE ENDVELM2 PROGRAM TO CONTINUE THE MATCH PROCESS.             *
//*******************************************************************
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.TSTSORT.ELEMENTS
 DELETE BC0THOR.TSTSORT.LMODS
 DELETE BC0THOR.TST.UNMATCH.ELEMENTS
 DELETE BC0THOR.TST.UNMATCH.LDMODS
 DELETE BC0THOR.TST.MATCHED.ELEMENTS
//*
//SORT1    EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TST.EDGESTRP.INHOUSE,DISP=SHR
//SORTOUT  DD DSN=BC0THOR.TSTSORT.LMODS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,16,CH,A)
//*
//SORT2    EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TST.UNSORT.ELEMENTS,DISP=SHR
//SORTOUT  DD DSN=BC0THOR.TSTSORT.ELEMENTS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(15,5),RLSE)
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,8,CH,A)
/*
//ENDVELMT EXEC  PGM=ENDVELMT
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//         DD    DSN=CEE.SCEERUN,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//SYSPRINT DD    SYSOUT=*
//SYSOUT   DD    SYSOUT=*
//LMODS    DD    DSN=BC0THOR.TSTSORT.LMODS,DISP=SHR
//ELEMENTS DD    DSN=BC0THOR.TSTSORT.ELEMENTS,DISP=SHR
//MATCHED  DD    DSN=BC0THOR.TST.MATCHED.ELEMENTS,DISP=(,CATLG),
