//*********************************************************************
//*              BC0THOR.PDS.JCL(IBMREC)                              *
//* THIS JOB IS USED TO RECEIVE PTF'S SENT ELECTRONICALLY FROM IBMLINK*
//* NOTES:                                                            *
//* 0. FOR QUESTIONS, CALL PETE WOO                                   *
//* 1. RUN THIS JOB ON SYSA ONLY.                                     *
//* 2. SET IEUSERID AND IEPASSWORD AS NECESSARY.                      *
//* 3. SET THE LAST FOUR DIGITS OF THE CLASS(MPTFNNNN) TO BE THE LAST *
//*    4 DIGITS OF THE ORDER NUMBER FROM IBMLINK.                     *
//*********************************************************************
//*
//IEBASE   EXEC PGM=IEBASE
//*TEPLIB  DD DSN=NET.IBMIE.PROD.LOADLIB,DISP=SHR
//STEPLIB  DD DSN=NET.IE430.EXPLOAD,DISP=SHR
//SYSPRINT DD SYSOUT=*
//ERRORMSG DD DISP=SHR,DSN=NET.IE430.EXPMSGS(ERRORMSG)
//ERRORTXT DD DISP=SHR,DSN=NET.IE430.EXPMSGS(ERRORTXT)
//BASETRC  DD SYSOUT=*
//OUTMSG   DD SYSOUT=*
//OUTPRO   DD SYSOUT=*
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//ISCTRACE DD UNIT=VIO,SPACE=(CYL,(1,5)),DISP=(NEW,PASS,DELETE),
//            DCB=(LRECL=6000,BLKSIZE=12288,BUFNO=2,RECFM=VB)
//OUTFILE  DD SYSOUT=*
//INPRO    DD *
IDENTIFY IEACCOUNT(HNET) IEUSERID(BC0THOR) IEPASSWORD(DEC00)
 TIMEZONE(EDT) ;
//         DD DISP=SHR,DSN=NET.IBMIE.PROD.SNACOMM
//INMSG    DD *
RECEIVE FILEID(DD:OUTPUT) CLASS(MPTF0579) ;
//OUTPUT   DD DSN=BC0THOR.OR0577,DISP=(,CATLG,DELETE),
//         UNIT=DISK,SPACE=(CYL,(25,10),RLSE),
//         DCB=(RECFM=FB,LRECL=1024,BLKSIZE=7168)
//UNTERSE EXEC PGM=TRSUNPCK,
//             PARM='UNPACK'
//STEPLIB  DD DISP=SHR,DSN=SYS1.TECHLOAD
//SYSPRINT DD  SYSOUT=*
//INFILE   DD  DISP=SHR,
//         DSN=BC0THOR.OR0579
//OUTFILE  DD  DISP=(,CATLG,DELETE),
//         DSN=BC0THOR.TEST.UQ04823,
//         UNIT=DISK,
//         DCB=(LRECL=80,BLKSIZE=7200,RECFM=FB,DSORG=PS),
//         SPACE=(CYL,(10,10),RLSE)
//
//
//
//
SNACOMM  IELUNAME(IBM0RELY) USERLUNAME(A15IIN09) USERLUNAME(A15IIN08)
         USERLUNAME(A15IIN00) ;
/*
//         DD DISP=SHR,DSN=NET.TST.BC0THOR.EXPMSGS(SNACOMM)
//
//
CSSHNET    BC0THOR BC0THOR         W0500
CRF                     MPTF2045
CRFC DOUTPUT
CSE
//

