IDCMREN2.text:
//BC0THORF JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*********************************************************************
//*        BC0THOR.PDS.JCL(IDCMREN2)                                  *
//*  RENAME A VSAM DATASET, CHANGING THE HIGH-LEVEL DSNAME QUALIFIER  *
//*  SUCH THAT IT SHOULD BE CATALOGED IN A DIFFERENT CATALOG. THE     *
//*  ALTER FUNCTION MERELY RENAMES THE DATASET, BUT IT REMAINS        *
//*  CATALOGED IN THE ORIGINAL CATALOG. TO ACCOMPLISH THE RECATALOG,  *
//*  A REPRO MERGECAT FOLLOWS THE ALTER                               *
//*********************************************************************
//*********************************************************************
//*        LIST THE CATALOG BEFORE ANY ACTION IS TAKEN                *
//*********************************************************************
//LIST1    EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 LISTCAT ENTRIES(SPP.VSAMNAME) ALL
//*********************************************************************
//*        RENAME THE DATASET USING ALTER ... NEWNAME                 *
//*********************************************************************
//RENAME   EXEC PGM=IDCAMS
//SYSPRINT DD   SYSOUT=*
//SYSIN    DD   *
           ALTER  -
                  SPP.VSAMNAME -
                  NEWNAME(BC0THOR.VSAMNAME)
           ALTER  -
                  SPP.VSAMNAME.* -
                  NEWNAME(BC0THOR.VSAMNAME.*)
//*********************************************************************
//*        LIST THE CATALOG AFTER RENAME (STILL IN OLD CATALOG)       *
//*********************************************************************
//LIST2    EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 LISTCAT ENTRIES(BC0THOR.VSAMNAME) CATALOG(SYS1.CAT.SYSTEM) ALL
//*********************************************************************
//*        MOVE THE CATALOG ENTRY USING REPRO ... MERGECAT            *
//*********************************************************************
//REPROMC  EXEC PGM=IDCAMS,REGION=2048K
//SYSPRINT DD   SYSOUT=*
//SYSUDUMP DD   SYSOUT=*
//DD1      DD   UNIT=DISK,VOL=SER=MVS220,DISP=OLD
//NEWCAT   DD   DSN=SYS1.CAT.TSO,DISP=SHR
//OLDCAT   DD   DSN=SYS1.CAT.SYSTEM,DISP=SHR
//SYSIN    DD   *
 REPRO -
    MERGECAT -
    INFILE(OLDCAT) -
    OUTFILE(NEWCAT) -
    ENTRIES(BC0THOR.VSAMNAME) -
    FILE(DD1)
//*********************************************************************
//*        LIST THE CATALOG AFTER RENAME (NOW IN CORRECT CATALOG)     *
//*********************************************************************
//LIST3    EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 LISTCAT ENTRIES(BC0THOR.VSAMNAME) ALL

IDCMRERG.text:
//BC0THOR3 JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*********************************************************************
//*        BC0THOR.PDS.JCL(IDCMRERG)                                  *
//*  "REORG" A VSAM KSDS BY COPYING IT TO A SEQUENTIAL FILE FOR       *
//*  BACKUP, THEN RESTORING IT TO DISK AS A VSAM KSDS. THE IDCAMS     *
//*  REPRO FUNCTION IS USED FOR THE DATA MOVEMENTS.                   *
//*  DATASETS SPECIFIED IN THIS RUN ARE:                              *
//*  INPUT VSAM FILE TO BE CLONED: EADMIN.ENDEVOR.STAGE1              *
//*  OUTPUT VSAM CLONE IS: BC0THOR.TST.STAGE1                         *
//*********************************************************************
//*
//SCRATCH  EXEC PGM=IDCAMS,REGION=2048K
//SYSPRINT DD   SYSOUT=*
//SYSIN    DD   *
 DELETE BC0THOR.TST.STAGE1 CLUSTER
//*
//SEQSAVE  EXEC PGM=IDCAMS,REGION=2048K
//SYSPRINT DD   SYSOUT=*
//SYSUDUMP DD   SYSOUT=*
//VSAMFILE DD   DSN=EADMIN.ENDEVOR.STAGE1,DISP=SHR
//SEQFILE  DD   DSN=&&SEQFILE,DISP=(,PASS),
//         UNIT=DISK,SPACE=(CYL,(100,9),RLSE),
//         DSORG=PS,BLKSIZE=27920
//SYSIN    DD   *
 REPRO INFILE (VSAMFILE) OUTFILE (SEQFILE)
//*
//RESTORE  EXEC PGM=IDCAMS,REGION=2048K
//SYSPRINT DD   SYSOUT=*
//SYSUDUMP DD   SYSOUT=*
//SEQFILE  DD   DSN=&&SEQFILE,DISP=(OLD,PASS)
//VSAMFILE DD   DSN=BC0THOR.TST.STAGE1,DISP=(,CATLG),
//         UNIT=DISK,LIKE=EADMIN.ENDEVOR.STAGE1
//SYSIN    DD   *
 REPRO INFILE (SEQFILE) OUTFILE (VSAMFILE)

