//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(       )                        *
//*                                                                 *
//* MERGE DATA FROM TWO INPUT FILES BOTH SORTED ON THE SAME FIELD   *
//* PROGRAM MUST BE REBUILT FOR EACH RUN. PRESENT SPECS ARE:        *
//*  1) INPUT SORT KEYS IN COL 1-8, BOTH FILES.                     *
//*  2) DATA FROM INPUT2 FILE COLS 10-16 OR 18-24 IS MOVED TO COLUMN*
//*     10-16 OF THE INPUT1 RECORDS WHICH ARE THEN WRITTEN TO THE   *
//*     OUTPUT1 FILE.                                               *
//*  3) UNMATCHED INPUT1 RECORDS ARE WRITTEN TO THE OUTPUT1 FILE WIT*
//*     BLANKS IN COL 10-16. UNMATCHED INPUT2 RECORDS ARE DROPPED. A*
//*     MESSAGE IS WRITTEN TO THE PRINT1 FILE FOR ALL UNMATCHED RECO*
//*******************************************************************
//DELETE   EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.TST.CLISTS    NONVSAM
//*
//MERGE    EXEC  PGM=MERGE
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//PRINT1   DD    SYSOUT=*
//OUTPUT1  DD    DSN=BC0THOR.TST.CLISTS,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(5,3),RLSE),
//         RECFM=FB,LRECL=80
//INPUT1   DD    DSN=BC0THOR.PDS.DATA(CLSTSTAT),DISP=SHR
//INPUT2   DD    DSN=BC0THOR.PDS.DATA(CLSTPROD),DISP=SHR

MERGE1.text:
//BC0THORA JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(MERGE1)                         *
//*          *** MERGE1: MERGE COBLTYPE, ENDVMTCH FILES ***         *
//* MERGE DATA FROM THE COBLTYPE AND ENDVMTCH PROGRAM OUTPUT FILES  *
//* INTO A SINGLE FILE. ENDVMTCH FILE IS SORTED IN ENDEVOR ELEMENT  *
//* NAME ORDER, AND THE COBLTYPE FILE IS SORTED IN MEMBER NAME ORDER*
//*******************************************************************
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.TSTENDST NONVSAM
 DELETE BC0THOR.TSTTYPST NONVSAM
 DELETE BC0THOR.TSTMRG1A NONVSAM
 DELETE BC0THOR.TSTM1AST NONVSAM
 DELETE BC0THOR.TSTMERG1 NONVSAM
//*******************************************************************
//*   SORT THE ENDVMTCH OUTPUT FILE IN ENDEVOR ELEMENT ORDER        *
//*******************************************************************
//SORTEND  EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TSTMTCH,DISP=OLD
//*ORTIN   DD DSN=BC0THOR.TSTMTCH,DISP=(OLD,DELETE,KEEP)
//SORTOUT  DD DSN=BC0THOR.TSTENDST,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,8,CH,A)
//*******************************************************************
//*   SORT THE COBLTYPE OUTPUT FILE IN MEMBER NAME ORDER            *
//*******************************************************************
//SORTTYP  EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TSTCBLTY,DISP=OLD
//*ORTIN   DD DSN=BC0THOR.TSTCBLTY,DISP=(OLD,DELETE,KEEP)
//SORTOUT  DD DSN=BC0THOR.TSTTYPST,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(45,8,CH,A)
//*******************************************************************
//*   MERGE DATA FROM THE ENDVMTCH AND COBLTYPE FILES USING MEMBER  *
//*******************************************************************
//MERGE1A  EXEC PGM=MERGE1A
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//PRINT1   DD   SYSOUT=*,BLKSIZE=0
//ENDVMTCH DD   DSN=BC0THOR.TSTENDST,DISP=(OLD,PASS)
//COBLTYPE DD   DSN=BC0THOR.TSTTYPST,DISP=(OLD,DELETE,KEEP)
//OUTPUT1  DD   DSN=BC0THOR.TSTMRG1A,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//*******************************************************************
//*   SORT THE MERGE1A OUTPUT FILE IN CSECT NAME ORDER              *
//*******************************************************************
//SORTCSE  EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TSTMRG1A,DISP=(OLD,DELETE,KEEP)
//SORTOUT  DD DSN=BC0THOR.TSTM1AST,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=FB,LRECL=107,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(53,8,CH,A)
//*******************************************************************
//*   MERGE DATA FROM THE ENDVMTCH AND MERGE1A FILES USING CSECT    *
//*******************************************************************
//MERGE1B  EXEC PGM=MERGE1B
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//ENDVMTCH DD   DSN=BC0THOR.TSTENDST,DISP=(OLD,DELETE,KEEP)
//MERGE1A  DD   DSN=BC0THOR.TSTM1AST,DISP=(OLD,DELETE,KEEP)
//OUTPUT1  DD   DSN=BC0THOR.TSTMERG1,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0

MERGE2.text:
//BC0THORB JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(MERGE2)                         *
//*          *** MERGE2: MERGE JCLSCAN2 AND MERGE1 FILES ***        *
//* MERGE DATA FROM THE JCLSCAN2 AND MERGE1 PROGRAM OUTPUT FILES    *
//* INTO A SINGLE FILE. JCLSCAN2 FILE IS SORTED IN STEPLIB DSNAME   *
//* AND PROGRAM NAME ORDER AND THE MERGE1 OUTPUT FILE IS SORTED IN  *
//* LOAD LIBRARY DSNAME AND MEMBER NAME ORDER.                      *
//*******************************************************************
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.TSTMG1ST NONVSAM
 DELETE BC0THOR.TSTJCLST NONVSAM
 DELETE BC0THOR.TSTMERG2 NONVSAM
//*******************************************************************
//*   SORT THE JCLSCAN2 OUTPUT FILE IN STEPLIB, PROGRAM NAME ORDER  *
//*******************************************************************
//SORTJCL  EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TSTEXECS,DISP=SHR
//SORTOUT  DD DSN=BC0THOR.TSTJCLST,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=FB,LRECL=120,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,44,CH,A,46,8,CH,A)
//*******************************************************************
//*   SORT THE MERGE1 OUTPUT FILE IN LOAD LIBRARY DSNAME, MEMBER    *
//*   NAME ORDER                                                    *
//*******************************************************************
//SORTMG1  EXEC PGM=SORT33,PARM='CORE=MAX,MSG=AP'
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//SORTIN   DD DSN=BC0THOR.TSTMERG1,DISP=SHR
//SORTOUT  DD DSN=BC0THOR.TSTMG1ST,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=FB,LRECL=107,BLKSIZE=0
//SORTWK01 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK02 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK03 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK04 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK05 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SORTWK06 DD UNIT=DISK,SPACE=(CYL,(5,5))
//SYSOUT   DD SYSOUT=*
//SYSIN    DD *
 SORT FIELDS=(1,52,CH,A)
//*******************************************************************
//*   MERGE DATA FROM THE MERGE1 AND JCLSCAN2 FILES                 *
//*******************************************************************
//MERGE2   EXEC PGM=MERGE2
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//MERGE1   DD   DSN=BC0THOR.TSTMG1ST,DISP=(OLD,DELETE,KEEP)
//JCLSCAN2 DD   DSN=BC0THOR.TSTJCLST,DISP=(OLD,DELETE,KEEP)
//OUTPUT1  DD   DSN=BC0THOR.TSTMERG2,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(105,15),RLSE),
//         RECFM=VB,LRECL=6413,BLKSIZE=0

