MTCHMERG.text:
//BC0THORJ JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(MTCHMERG)                       *
//*******************************************************************
//* GENERIC MATCH/MERGE PROGRAM THAT MUST BE RESPECIALIZED FOR EACH *
//* USE. CURRENT SETUP:                                             *
//*  0. PRE-SORT INPUT1 AND INPUT2 ON COLUMNS 1-16                  *
//*  1. MATCH INPUT1 AND INPUT2 ON COLUMNS 1-16                     *
//*  2. IF NOMATCH AND INPUT1 IS LOW, WRITE INPUT1 RECORD TO IN1LOW.*
//*  3. IF NOMATCH AND INPUT2 IS LOW, WRITE INPUT2 RECORD TO IN2LOW.*
//*  4. IF MATCH WRITE INPUT1 RECORD TO MATCHED.                    *
//*  5. RECFM, LRECL, AND BLKSIZE ARE ALL DEFAULTED TO JCL          *
//*  6. FOR VARIABLE RECORDS, RDW WILL BE PART OF THE MATCH FIELD   *
//*******************************************************************
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.DEL.IN1LOW
 DELETE BC0THOR.DEL.IN2LOW
 DELETE BC0THOR.DEL.MATCHED
//*
//MTCHMERG EXEC  PGM=MTCHMERG
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//INPUT1   DD    DSN=BC0THOR.DEL.R150.DIR,DISP=SHR
//INPUT2   DD    DSN=BC0THOR.DEL.RNT0.DIR,DISP=SHR
//IN1LOW   DD    DSN=BC0THOR.DEL.IN1LOW,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(10,1),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//IN2LOW   DD    DSN=BC0THOR.DEL.IN2LOW,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(10,1),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0
//MATCHED  DD    DSN=BC0THOR.DEL.MATCHED,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(10,1),RLSE),
//         RECFM=FB,LRECL=80,BLKSIZE=0

MVCLCHEK.text:
//BC0THORC JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(CHECKIPO)                       *
//* 1. ASSEMBLE BC0THOR.PDS.ASM(MVSCALL)                            *
//* 2. LINKEDIT TO &&LOADLIB(MVSCALL)                               *
//* 3. DISASSEMBLE &&LOADLIB(MVSCALL) TO &&MVSCALLN                 *
//* 4. DISASSEMBLE BC0THOR.LOAD.LOAD(MVSCALLO) TO &&MVSCALLO        *
//* 5. SUPERCE COMPARE &&MVSCALLN TO &&MVSCALLO                     *
//*******************************************************************
//*********************************************
//* 1. ASSEMBLE BC0THOR.PDS.ASM(MVSCALL)      *
//*********************************************
//ASM      EXEC PGM=ASMA90,
//  PARM=(OBJECT,'XREF(FULL)',ESD,LIST,'FLAG(NORECORD)')
//SYSLIB   DD DSN=SYS1.MACLIB,DISP=SHR
//         DD DSN=SYS1.MODGEN,DISP=SHR
//         DD DSN=IMS.IMSM.MACLIB,DISP=SHR
//         DD DSN=IMS.IMSM.SVSOURCE,DISP=SHR
//         DD DSN=IMS.IMSM.DBSOURCE,DISP=SHR
//         DD DSN=IMS.IMSM.DFSISRC,DISP=SHR
//         DD DSN=IMS.IMSM.GENLIB,DISP=SHR
//         DD DSN=IMS.IMSM.GENLIBA,DISP=SHR
//         DD DSN=IMS.IMSM.GENLIBB,DISP=SHR
//SYSUT1   DD DSN=&&SYSUT1,UNIT=DISK,SPACE=(CYL,(10,5))
//SYSPRINT DD SYSOUT=*
//SYSUDUMP DD SYSOUT=*
//SYSPUNCH DD DUMMY,DCB=BLKSIZE=80
//SYSLIN   DD DSN=&&LOADSET,DISP=(,PASS),
//         UNIT=DISK,SPACE=(CYL,(5,1),RLSE),
//         BLKSIZE=400
//SYSIN    DD DSN=BC0THOR.PDS.ASM(MVSCALL),DISP=SHR
//*********************************************
//* 2. LINKEDIT BC0THOR.LOAD.LOAD(MVSCALL)    *
//*********************************************
// IF (ASM.RC LE 4) THEN
//LKED    EXEC PGM=IEWL,REGION=256K,COND=(8,LT),
//  PARM='AC=1,XREF,LET,REFR,LIST,CALL,MAP,SIZE=(256K,96K)'
//SYSPRINT DD SYSOUT=*
//SYSLIN   DD DSN=&&LOADSET,DISP=(SHR,DELETE,DELETE)
//SYSUT1   DD UNIT=DISK,SPACE=(CYL,(1,1))
//SYSLIB   DD DSN=SYS1.LINKLIB,DISP=SHR
//SYSLMOD  DD DSN=&&LOADLIB(MVSCALL),DISP=(NEW,PASS),
//         UNIT=DISK,SPACE=(TRK,(30,15,1),RLSE),
//         DSORG=PO,RECFM=U
//****************************************************
//* 3. DISASSEMBLE &&LOADLIB(MVSCALL) TO &&MVSCALLN *
//****************************************************
// IF (LKED.RC LE 4) THEN
//DISASM1  EXEC PGM=DISASM0,COND=(8,LT)
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//SYSPRINT DD   SYSOUT=*
//SYSLIB   DD   DSN=&&LOADLIB,DISP=(OLD,PASS)
//SYSPUNCH DD   DSN=&&MVSCALLN,DISP=(,PASS),
//         UNIT=SYSDA,SPACE=(TRK,(90,15),RLSE)
//SYSIN    DD   *
MVSCALL MVSCALL
//*****************************************************************
//* 4. DISASSEMBLE SYS1.SYSB.HNET.LINKLIB(MVSCALL) TO &&MVSCALLO *
//*****************************************************************
// IF (DISASM1.RC LE 4) THEN
//DISASM2  EXEC PGM=DISASM0,COND=(8,LT)
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//SYSPRINT DD   SYSOUT=*
//SYSLIB   DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSPUNCH DD   DSN=&&MVSCALLO,DISP=(,PASS),
//         UNIT=SYSDA,SPACE=(TRK,(90,15),RLSE)
//SYSIN    DD   *
MVSCALLO MVSCALL
//***********************************************
//* 5. SUPERCE COMPARE &&MVSCALLN TO &&MVSCALLO *
//***********************************************
// IF (DISASM2.RC LE 4) THEN
//SUPERC  EXEC PGM=ISRSUPC,PARM=(DELTAL,LINECMP,' SEQ','')
//NEWDD  DD DSN=&&MVSCALLN,DISP=(OLD,PASS)
//OLDDD  DD DSN=&&MVSCALLO,DISP=(OLD,PASS)
//OUTDD  DD SYSOUT=*
// ENDIF
// ENDIF
// ENDIF
// ENDIF

