//RECV      EXEC NDM2Y2K
//DMBATCH.SYSIN  DD  *
  SIGNON UNIT=DISK
  SUB PROC=RECVFY2K                   -
      NOTIFY=%USER                    -
      &PID=BC0THOR                    -
