      &FROMDCB=,                      -
      &FROMSER=,                      -
      &TODSN=CDC.INTT.OCR.C0LD1.UNLOAD.DSRLINE.RUN1 -
      &TODCB=,                        -
      &TODISP=\(NEW,CATLG,DELETE)\    -
      &TOUNIT=DISK                    -
      &TOSER=,                        -
      &TOSPACE=
  SIGNOFF
//

