PDSCPYMB.text:
//BC0THORJ JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//********************************************************************
//*                 BC0THOR.PDS.JCL(PDSCPYMB)                        *
//* READS MEMBERS OF A PDS AND WRITES THEM TO A PHYSICALLY SEQUENTIAL*
//* DATASET WITH A ./ ADD CARD PRECEDING EACH MEMBER'S RECORDS       *
//*                                                                  *
//* THE INPUT PDS IS INPUT1, AND THE OUTPUT FILE IS SYSOUT. THEY MUST*
//* HAVE THE SAME RECFM AND LRECL, AND LRECL MUST NOT EXCEED 1688.   *
//*                                                                  *
//* MEMBER NAMES ARE SPECIFIED IN THE SYSIN FILE, ONE PER RECORD,    *
//* LEFT JUSTIFIED IN COLUMNS 1-8.                                   *
//********************************************************************
//DELETE   EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.DEL.PDSDIR
 DELETE BC0THOR.DEL.PDSCPYMB
//*
//PDSDIR   EXEC PGM=PDSDIR
//STEPLIB  DD DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD SYSOUT=(*,,DUMP)
//INPUT1   DD DISP=SHR,DSN=BC0THOR.PDS.JCL
//OUTPUT1  DD DSN=BC0THOR.DEL.PDSDIR,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(15,5)),
//         RECFM=FB,LRECL=80
//*
//PDSCPYMB EXEC PGM=PDSCPYMB
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=(*,,DUMP)
//INPUT1   DD   DISP=SHR,DSN=BC0THOR.PDS.JCL
//SYSPRINT DD   SYSOUT=*
//SYSOUT   DD   DSN=BC0THOR.DEL.PDSCPYMB,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(30,3),RLSE),
//         RECFM=FB,LRECL=80
//SYSIN    DD   DSN=BC0THOR.DEL.PDSDIR,DISP=SHR

