//IEBGENER EXEC PGM=IEBGENER
//SYSPRINT DD   SYSOUT=*
//SYSIN    DD   DUMMY
//SYSUT1   DD   DISP=SHR,DSN=BC0THOR.HNET.ISPF.EOY.ENU.CBCPG010.PRINT
//SYSUT2   DD   SYSOUT=(,),OUTPUT=(*.DP50)

PRTCC.text:
//BC0THORD JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=104M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(PRTCC)                          *
//*  TESTING COBOL PRINTING USING WRITE WITH AND WITHOUT ADVANCING. *
//*******************************************************************
//SCRATCH  EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.DEL.ADVNCING
 DELETE BC0THOR.DEL.NOADVANC
//*
//*****************************************************************
//* CREATE TWO PRINT FILES USING THE SAME DATA, BUT USING THE     *
//* WRITE AFTER ADVANCING FOR ONE, AND SIMPLE WRITES FOR THE OTHER*
//*****************************************************************
//PRTCC    EXEC  PGM=PRTCC
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//INPUT1   DD    DSN=BC0THOR.PDS.JCL(PRTCC),DISP=SHR
//ADVNCING DD    DSN=BC0THOR.DEL.ADVNCING,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(5,1),RLSE),
//         DSORG=PS,RECFM=FBA,LRECL=133
//NOADVANC DD    DSN=BC0THOR.DEL.NOADVANC,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(5,1),RLSE),
//         DSORG=PS,RECFM=FBA,LRECL=133
//****************************************************************
//* COMPARE THE TWO PRINT FILES CREATED BY THE PRTCC PROGRAM     *
//* DATASETS SHOULD DIFFER ONLY ON THE LAST RECORD.              *
//****************************************************************
//SUPERC   EXEC PGM=ISRSUPC,PARM=(DELTAL,LINECMP,' NOSEQ','')
//NEWDD    DD DSN=BC0THOR.DEL.ADVNCING,DISP=SHR
//OLDDD    DD DSN=BC0THOR.DEL.NOADVANC,DISP=SHR
//OUTDD    DD SYSOUT=*

PRTCCLAS.text:
//BC0THORG JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*********************************************************************
//*                   BC0THOR.PDS.JCL(PRTCCLAS)                       *
//*                                                                   *
//* PRINT THE TABLE OF CONTENTS AND THE HANDBOOK FOR THE C CLASS      *
//*                                                                   *
//*      ============>  SET FOR COPIES=1     <===================     *
//*                                                                   *
//* FILES ARE UPLOADED FROM THE PC USING AN OS/2 WINDOW AND:          *
//*        CD C:\TC1\CCLASS                                           *
//*        SEND CONTENTS F: 'BC0THOR.CONTENTS ASCII CRLF BLKSIZE(255) *
//*        SEND HANDBOOK F: 'BC0THOR.HANDBOOK ASCII CRLF BLKSIZE(255) *
//*                                                                   *
//* PARM=NOBOLD IS USED AS ONLY ONE CHARACTER SET IS USED             *
//*                                                                   *
//* SEE COMMENTS IN THE SOURCE PROGRAM FOR MORE DETAIL                *
//*********************************************************************
//*
//PRTCCONT EXEC  PGM=PRTPCWRT,PARM=NOBOLD
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//ABNLDUMP DD    DUMMY
//INPUT1   DD    DSN=BC0THOR.CONTENTS,DISP=SHR
//PRINT1   DD  SYSOUT=(D,,DP50),
//             FCB=STD2, STD2=6LPI, STD3=8LPI
//             DCB=(OPTCD=J),
//             CHARS=(GT12,GT15),
//             RECFM=VBA,LRECL=255,BLKSIZE=6233,
//             COPIES=1
//*
//PRTCHDBK EXEC  PGM=PRTPCWRT,PARM=NOBOLD
//STEPLIB  DD    DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD    SYSOUT=*
//ABNLDUMP DD    DUMMY
//INPUT1   DD    DSN=BC0THOR.HANDBOOK,DISP=SHR
//PRINT1   DD  SYSOUT=(D,,DP50),
//             FCB=STD2, STD2=6LPI, STD3=8LPI
//             DCB=(OPTCD=J),
//             CHARS=(GT12,GT15),
//             RECFM=VBA,LRECL=255,BLKSIZE=6233,
//             COPIES=1

PRTCHARS.text:
//BC0THORH JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(PRTCHARS)                       *
//*                                                                 *
//* EXTRACTED FROM PROD.FORMS.DATA IN JAN 1991:                     *
//* FCB     LPI    FRM LEN   LIN PG                                 *
//* STD0     10     8.5"         75                                 *
//* STD1     10      11"        100                                 *
//* STD2      6      11"         60                                 *
//* STD3      8      11"         80                                 *
//* STD4      8     8.5"         60                                 *
//* STD5      6     8.5"         45                                 *
//* STD6      6       7"         36                                 *
//* STD7     10       7"         60 (DEFAULT)                       *
//* STD8      8       7"         48                                 *
//* STD9     12       7"         72                                 *
//*                                                                 *
//* FROM THE 3800 PROGRAMMER'S GUIDE                                *
//* GS10,12,15 GOTHIC                                               *
//* GF10,12,15 GOTHIC UPPERCASE                                     *
//* GU10,12,15 GOTHIC UNDERSCORED                                   *
//* CE10,12    COURIER EXTENDED                                     *
//* CR10       COURIER REGULAR                                      *
//* PB12       PRESTIGE ELITE BOLD                                  *
//* PI12       PRESTIGE ELITE ITALIC                                *
//* PR12       PRESTIGE ELITE REGULAR                               *
//* PR10       PRESTIGE PICA REGULAR                                *
//* LB12       LETTER GOTHIC BOLD                                   *
//* LR12       LETTER GOTHIC REGULAR                                *
//* GB10,12    GOTHIC BOLD                                          *
//* GI12       GOTHIC ITALIC                                        *
//* GT10,12,15 GOTHIC TEXT                                          *
//* SR12       SCRIPT REGULAR                                       *
//* TN, T11    PL/I TRAIN 10CPI (HAS BRACKETS, BRACES, UC, LC, ETC) *
//* FINAL 2 DIGITS ARE CHARACTERS-PER-INCH HORIZONTALLY             *
//*******************************************************************
//PRTHEX EXEC PGM=IEBGENER
//SYSPRINT DD SYSOUT=*
//SYSIN  DD DUMMY
//SYSUT2 DD SYSOUT=*,FCB=STD8,CHARS=GT12
//SYSUT1 DD DSN=BC0THOR.PDS.DATA(PRTCHARS),DISP=SHR
//***************************************************************
//* THIS STEP RESETS THE PRINTER TO THE DEFAULT CHARACTER SET TO*
//* PREVENT MAKING ENEMIES OF OPERATORS AND OTHER PROGRAMMERS.  *
//***************************************************************
//DEFLT  EXEC PGM=IEBGENER
//SYSPRINT DD SYSOUT=*
//SYSIN  DD DUMMY
//SYSUT2 DD SYSOUT=*,FCB=STD2,CHARS=T11
//*YSUT2 DD SYSOUT=*,FCB=STD7,CHARS=GT15
//SYSUT1 DD *

         RETURN TO:  FCB=STD7,CHARS=GT15

