//SCAN010  EXEC SCAN,DSNAME=GIBC.TESTPROC
//SCAN011  EXEC SCAN,DSNAME=IMS.IMSM.PROCLIB
//SCAN012  EXEC SCAN,DSNAME=IMS.IMST.PROCLIB
//SCAN013  EXEC SCAN,DSNAME=HCS.PROD.PROC
//SCAN014  EXEC SCAN,DSNAME=HCS.TEST.PROC
