SUBCCMPL.text:
//BC0THORH JOB (1152002-9800039,,TMVS01-UR-XXXX),'DTHORNTON',CLASS=V,
//   MSGCLASS=Y,NOTIFY=BC0THOR,REGION=4M
//JESOUT OUTPUT GROUPID=02A,DEFAULT=YES,JESDS=ALL,
//   PAGEDEF=GRYBIG,FORMDEF=GRYBIG
//*******************************************************************
//*                 BC0THOR.PDS.JCL(SUBCLOND)                       *
//* 0. DELETE ANY OLD RESULT FILES (DEL.NEW OR DEL.ORIG)            *
//* 1. COBOL II COMPILE OF BC0THOR.PDS.COBOL(SUBCLONW)              *
//* 2. LINKEDIT TO &&LOADLIB(SUBCLONW)                              *
//* 3. DISASSEMBLE &&LOADLIB(SUBCLONW) TO BC0THOR.DEL.NEW           *
//* 4. DISASSEMBLE BC0THOR.LOAD.LOAD(SUBORIG) TO BC0THOR.DEL.ORIG   *
//* 5. SUPERCE COMPARE BC0THOR.DEL.NEW TO BC0THOR.DEL.ORIG          *
//*******************************************************************
//****************************************************
//* 0. SCRATCH ANY OLD COPIES OF DEL.NEW OR DEL.ORIG *
//****************************************************
//DELETE   EXEC PGM=IDCAMS
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 DELETE BC0THOR.DEL.NEW
 DELETE BC0THOR.DEL.ORIG
//***************************************************
//* 1. COBOL II COMPILE OF SUBCLONW                 *
//***************************************************
//COB2   EXEC  PGM=IGYCRCTL,REGION=4096K,
//         PARM=('NUMPROC(MIG)',X,MAP,NOOFF,TERM,QUOTE)
//STEPLIB  DD  DSN=SYS1.COB2COMP,DISP=SHR
//SYSPRINT DD  SYSOUT=*
//SYSTERM  DD  SYSOUT=*
//SYSLIN   DD  DSN=&&LOADSET,UNIT=DISK,DISP=(MOD,PASS),
//             SPACE=(TRK,(3,3))
//SYSUT1   DD  UNIT=DISK,SPACE=(CYL,(1,1))
//SYSUT2   DD  UNIT=DISK,SPACE=(CYL,(1,1))
//SYSUT3   DD  UNIT=DISK,SPACE=(CYL,(1,1))
//SYSUT4   DD  UNIT=DISK,SPACE=(CYL,(1,1))
//SYSUT5   DD  UNIT=DISK,SPACE=(CYL,(1,1))
//SYSUT6   DD  UNIT=DISK,SPACE=(CYL,(1,1))
//SYSUT7   DD  UNIT=DISK,SPACE=(CYL,(1,1))
//SYSIN    DD  DSN=BC0THOR.PDS.COBOL(SUBCLONW),DISP=SHR
//*****************************************
//* 2. LINKEDIT TO &&LOADLIB(SUBCLONW)    *
//*****************************************
// IF (COB2.RC LE 4) THEN
//LKED     EXEC PGM=HEWL,REGION=256K,
//         PARM=(XREF,LIST,MAP,LET,CALL,'SIZE=(256K,96K)')
//SYSPRINT DD SYSOUT=*
//SYSLIN   DD DSN=&&LOADSET,DISP=(OLD,DELETE,DELETE)
//SYSUT1   DD DSN=&&SYSUT1,UNIT=DISK,SPACE=(CYL,(2,1))
//SYSLIB   DD DSN=CEE.SCEELKED,DISP=SHR
//         DD DSN=EMVSP.OUTPUT.LOADLIB,DISP=SHR
//         DD DSN=SYS1.IMSVS.RESLIB,DISP=SHR
//SYSLMOD  DD DSN=&&LOADLIB(SUBCLONW),DISP=(NEW,PASS),
//         UNIT=DISK,SPACE=(TRK,(30,15,1),RLSE),
//         DSORG=PO,RECFM=U
//*********************************************************
//* 3. DISASSEMBLE &&LOADLIB(SUBCLONW) TO BC0THOR.DEL.NEW *
//*********************************************************
// IF (LKED.RC LE 4) THEN
//DISASM1  EXEC PGM=DISASM0
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//SYSPRINT DD   SYSOUT=*
//SYSLIB   DD   DSN=&&LOADLIB,DISP=(OLD,PASS)
//SYSPUNCH DD   DSN=BC0THOR.DEL.NEW,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(90,15),RLSE)
//SYSIN    DD   *
SUBCLONW SUBCLONW
//******************************************************************
//* 4. DISASSEMBLE BC0THOR.LOAD.LOAD(SUBORIG)  TO BC0THOR.DEL.ORIG *
//******************************************************************
// IF (DISASM1.RC LE 4) THEN
//DISASM2  EXEC PGM=DISASM0
//STEPLIB  DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSUDUMP DD   SYSOUT=*
//SYSPRINT DD   SYSOUT=*
//SYSLIB   DD   DSN=BC0THOR.LOAD.LOAD,DISP=SHR
//SYSPUNCH DD   DSN=BC0THOR.DEL.ORIG,DISP=(,CATLG),
//         UNIT=DISK,SPACE=(TRK,(90,15),RLSE)
//SYSIN    DD   *
SUBORIG SUBCLONW
//**********************************************************
//* 5. SUPERCE COMPARE BC0THOR.DEL.NEW TO BC0THOR.DEL.ORIG *
//**********************************************************
// IF (DISASM2.RC LE 4) THEN
//SUPERC  EXEC PGM=ISRSUPC,PARM=(DELTAL,LINECMP,' SEQ','')
//NEWDD  DD DSN=BC0THOR.DEL.NEW,DISP=SHR
//OLDDD  DD DSN=BC0THOR.DEL.ORIG,DISP=SHR
//OUTDD  DD SYSOUT=*
// ENDIF
// ENDIF
// ENDIF
// ENDIF

