CFCUTIL.text:
//CFCUTIL  JOB (6948),
//   'DISAS.JCLLIB.CFCUTIL',
//    MSGCLASS=Q,
//    CLASS=A,
//    REGION=2M,
//    NOTIFY=&SYSUID
/*ROUTE  PRINT HOLD
//*   DISASTER.RECOVER.JCLLIB(CFCUTIL)
//*
//* PRINT  COMPUWARE CUSTOMER MODULE UTILITY FROM SKFXLOAD & SKFXCLIB.
//*
//* CICS - LIBRARY CREATED FOR CICS RELATED LOAD MODULES (SKFXCLIB).
//* LOAD - LIBRARY CREATED FOR NONAUTHORIZED LOAD MODULES (SKFXLOAD).
//*
//* * * * * * * * * * * * * * * N O T E * * * * * * * * * * * * * * * *
//*      THIS MEMBER PRODUCES PRINT OF THE CUSTCF IN FX LOAD LIBRARIES.
//*         >>>>  NOT TO BE USED TO UPDATE THE CUSTCF. <<<<<<
//* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
//*
//PRINT    PROC CICS='OEM1.KFX310.SKFXCLIB',
//             LOAD='OEM1.KFX310.SKFXLOAD'
//*
//*
//*
//PSTEP001 EXEC PGM=CFCUTIL
//*
//STEPLIB  DD   DISP=SHR,DSN=&LOAD
//*
//MAINTIN  DD   DISP=SHR,DSN=&LOAD
//SYSLMOD  DD   DUMMY
//*REPORT   DD   SYSOUT=*
//REPORT   DD   SYSOUT=A,DEST=U178
//SYSIN    DD   DUMMY
//SYSPRINT DD   SYSOUT=*
//SYSUDUMP DD   SYSOUT=*
//*
//PSTEP002 EXEC PGM=CFCUTIL
//*
//STEPLIB  DD   DISP=SHR,DSN=&LOAD
//*
//MAINTIN  DD   DISP=SHR,DSN=&CICS
//SYSLMOD  DD   DUMMY
//*REPORT   DD   SYSOUT=*
//REPORT   DD   SYSOUT=A,DEST=U178
//SYSIN    DD   DUMMY
//SYSPRINT DD   SYSOUT=*
//SYSUDUMP DD   SYSOUT=*
//*
//         PEND
//CFCUTIL  EXEC PRINT
//*
//
//*   DISASTER.RECOVER.JCLLIB(CFCUTIL)

