FACUTIL.text:
//FACUTIL  JOB (6948),
//   'DISAS.JCLLIB.FACUTIL',
//    MSGCLASS=Q,
//    CLASS=A,
//    REGION=2M,
//    NOTIFY=&SYSUID
/*ROUTE  PRINT HOLD
//*
//*   DISASTER.RECOVER.JCLLIB(FACUTIL)
//*--------------------------------------------------------------------
//* FACUTIL:  JCL TO EXECUTE THE FILE-AID CUSTOMER UTILITY PROGRAM
//*           FACUTIL.  SUBMITTING THIS JCL WITHOUT ANY KEYWORDS WILL
//*           PRODUCE A REPORT OF THE CONTENTS OF YOUR CUSTOMER PROFILE
//*           MODULE.  SEE THE INSTALLATION GUIDE FOR MORE INFORMATION.
//*
//* IMPORTANT!  WHEN MAKING A CHANGE TO YOUR CUSTOMER PROFILE MODULE,
//*             ALWAYS CALL COMPUWARE FIRST FOR A NEW PASSWORD.
//*
//*--------------------------------------------------------------------
//FACUTIL  PROC DEV=SYSDA,                         DISK WORK DEVICE
//           PRINT='*',                            SYSOUT PRINT CLASS
//           LOADDSN='OEM1.FILEAID.V8R0M2.LOAD'    FILE-AID LOAD LIB
//STEP1    EXEC PGM=FACUTIL
//STEPLIB    DD DSN=&LOADDSN,DISP=SHR
//MAINTIN    DD DSN=&LOADDSN,DISP=SHR
//SYSLMOD    DD DSN=&LOADDSN,DISP=SHR
//SYSLIN     DD UNIT=&DEV,SPACE=(CYL,(2,1))
//SYSUT1     DD UNIT=&DEV,SPACE=(CYL,(1,1))
//* REPORT     DD SYSOUT=&PRINT
//REPORT     DD SYSOUT=A,DEST=U178
//SYSPRINT   DD SYSOUT=&PRINT
//SYSUDUMP   DD SYSOUT=&PRINT
//SYSIN      DD DUMMY
//         PEND
//*
//FACUTIL  EXEC FACUTIL
/*
//
//*   DISASTER.RECOVER.JCLLIB(FACUTIL)

FAZAP.text:
//FAZAP  JOB (6948),'FILEAID ZAP',CLASS=A,MSGCLASS=Q
//*
//*   OEM1.FILEAID.V8R0M2.INSTALL(JCLUTILD)
//*--------------------------------------------------------------------
//* JCLUTILD:  JCL TO EXECUTE THE FILE-AID CUSTOMER UTILITY PROGRAM
//*            FACUTIL FOR DISASTER RECOVERY SITES.  CONTACT COMPUWARE
//*            FOR INSTRUCTIONS ON RUNNING THIS JOB.
//*            THIS SHOULD ALLOW FILE-AID TO RUN FOR 7 DAYS AT THE
//*            DISASTER SITE.
//*--------------------------------------------------------------------
//*  1)  ADD A JOB CARD
//*  2)  CHANGE PROC PARAMETERS
//*  3)  SUBMIT
//*--------------------------------------------------------------------
//JCLUTIL  PROC WRKDEV=SYSALLDA,                   DISK WORK DEVICE
//           PRINT='*',                            SYSOUT PRINT CLASS
//           LOADDSN='OEM1.FILEAID.V8R0M2.LOAD'    FILE-AID LOAD LIB
//STEP1    EXEC PGM=FACUTIL,PARM=DISASTER
//STEPLIB    DD DSN=&LOADDSN,DISP=SHR,UNIT=SYSALLDA,VOL=SER=SOFT02
//MAINTIN    DD DSN=&LOADDSN,DISP=SHR,UNIT=SYSALLDA,VOL=SER=SOFT02
//SYSLMOD    DD DSN=&LOADDSN,DISP=SHR,UNIT=SYSALLDA,VOL=SER=SOFT02
//SYSLIN     DD UNIT=&WRKDEV,SPACE=(CYL,(2,1))
//SYSUT1     DD UNIT=&WRKDEV,SPACE=(CYL,(1,1))
//REPORT     DD SYSOUT=&PRINT
//SYSPRINT   DD SYSOUT=&PRINT
//SYSUDUMP   DD SYSOUT=&PRINT
//SYSIN      DD DUMMY
//         PEND
//*
//JCLUTIL  EXEC JCLUTIL
//

