ZAPBKUP.text:
//ZAPBKUP   JOB (6948),'BACKUP JCLLIB ZAPS',
//       CLASS=A,MSGCLASS=Q
/*ROUTE  PRINT RMT49
//*
//*  ENSURE THAT THE OLD 'DISASTER.RECOVER.ZAPS' HAS BEEN DELETED
//*  BEFORE RUNNING THIS JOB.
//*  IEBCOPY 'DISASTER.RECOVER.JCLLIB' TO 'DISASTER.RECOVER.ZAPS', THEN
//*  BACKUP DISASTER.RECOVER.ZAPS TO SFT1.BKUP.DISASTER.RECOVER.ZAPS.
//*
//*
//ALLOC    EXEC    PGM=IEFBR14
//DD1  DD  DSN=DISASTER.RECOVER.ZAPS,DISP=(NEW,CATLG,DELETE),
//         DCB=DISASTER.RECOVER.JCLLIB,
//         UNIT=SYSDA,SPACE=(TRK,(30,,10))
//*
//COPY     EXEC    PGM=IEBCOPY,COND=(0,NE)
//SYSPRINT DD      SYSOUT=A
//INFILE1  DD      DSN=DISASTER.RECOVER.JCLLIB,DISP=SHR
//OTFILE1  DD      DSN=DISASTER.RECOVER.ZAPS,DISP=OLD
//SYSUT3   DD      UNIT=SYSDA,SPACE=(TRK,(40,2))
//SYSUT4   DD      UNIT=SYSDA,SPACE=(TRK,(40,2))
//SYSIN    DD      *
  COPY OUTDD=OTFILE1,INDD=((INFILE1,R))
//*
//BACKUP   EXEC PGM=FDRDSF,COND=(0,NE)
//SYSPRINT  DD  SYSOUT=A
//DISK1     DD  DSN=DISASTER.RECOVER.ZAPS,DISP=(OLD,DELETE)
//TAPE1     DD  DSN=SFT1.BKUP.DISASTER.RECOVER.ZAPS,
//             DISP=(NEW,CATLG,DELETE),UNIT=TAPE
//SYSIN  DD  *
 DUMP DSN=DISASTER.RECOVER.ZAPS
/*
//*

ZAPRSTR.text:
//ZAPRSTR   JOB (6948),'RESTORE JCLLIB ZAPS',
//       CLASS=A,MSGCLASS=Q
//*
//* THIS JCL CAN BE USED TO RESTORE A COPY OF 'DISASTER.RECOVER.ZAPS'
//* (WHICH SHOULD BE AN EXACT COPY OF 'DISASTER.RECOVER.JCLLIB')
//* FROM AN FDR BACKUP OF SAME.  THIS IS ONLY USEFUL FOR A DISASTER
//* RECOVERY TEST, AND IT IS ASSUMED THAT THE BACKUP TAPE IS THE MOST
//* RECENT BACKUP OF THE LIBRARY, WITH UPDATED ZAPS FOR THE SCHEDULED
//* TEST, AND IS HAND-CARRIED TO THE DISASTER TEST SITE.
//*
//RESTORE1 EXEC PGM=FDRDSF
//SYSPRINT  DD  SYSOUT=*
//DISK1     DD  DISP=OLD,UNIT=SYSALLDA,VOL=SER=SYSL01
//TAPE1     DD  DSN=SFT1.BKUP.DISASTER.RECOVER.ZAPS,
//             DISP=OLD,
//             VOL=SER=??????,
//             UNIT=3490,LABEL=EXPDT=98000
//SYSIN  DD  *
 RESTORE DSN=DISASTER.RECOVER.ZAPS
/*
