RESCUE01.text:
//RESCUE01 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//*  INITIALIZE THE DISK
//*  NOTE: DASD DEVICE MUST BE VARIED OFFLINE PRIOR TO SUBMITTING
//*          V UUUU,OFFLINE
//*        AND VARIED BACK ONLINE AFTER THIS JOB ENDS
//*          V UUUU,ONLINE
//*  CHANGES:
//*   1 - CHANGE UUUU TO VALID DASD DEVICE TO BE INITIALIZED
//*   2 - CHANGE VVVVVV TO THE CURRENT VOLSER OF THE DASD DEVICE TO
//*       BE INITIALIZED OR USE 'NOVERIFY'
//*--------------------------------------------------------------------
//INIT      EXEC PGM=ICKDSF
//SYSPRINT  DD   SYSOUT=*
//IPLTEXT  DD DISP=SHR,DSN=SYS1.SAMPLIB(IPLRECS)
//         DD DISP=SHR,DSN=SYS1.SAMPLIB(IEAIPL00)
//SYSIN     DD   *
    INIT UNIT(UUUU) IPLDD(IPLTEXT) VERIFY(VVVVVV) VOLID(RESCUE) -
       VTOC(1,0,60) INDEX(0,1,14)   NOCONTINUE  PURGE NOCHECK
/*

RESCUE02.text:
//RESCUE02 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//*  ALLOCATE SYSTEM FILES
//*--------------------------------------------------------------------
//ALLOCATE EXEC PGM=IEFBR14
//IBMPROF  DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(CYL,(1,1,10)),
//             DCB=(LRECL=80,BLKSIZE=9040,RECFM=FB),
//             DSN=RESCUE.ISPPROF
//PARMLIB  DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(CYL,(1,1,2)),
//             DCB=(LRECL=80,BLKSIZE=9040,RECFM=FB),
//             DSN=RESCUE.PARMLIB
//VTAMLST  DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(CYL,(1,1,2)),
//             DCB=(LRECL=80,BLKSIZE=9040,RECFM=FB),
//             DSN=RESCUE.VTAMLST
//HASPACE  DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             DCB=DSORG=PSU,SPACE=(CYL,20),
//             DSN=RESCUE.HASPACE
//LOGREC   DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             DCB=SYS1.PYR1.LOGREC,SPACE=(TRK,2),
//             DSN=RESCUE.LOGREC
//HASPCKPT DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(CYL,5),
//             DSN=RESCUE.HASPCKPT
//SDSFINDX DD  DSN=RESCUE.HASPINDX,
//             DISP=(NEW,KEEP),
//             VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(CYL,(5),,CONTIG),
//             DCB=(RECFM=F,LRECL=4096,BLKSIZE=4096)
//PROCLIB  DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(CYL,(1,1,2)),
//             DCB=(LRECL=80,BLKSIZE=9040,RECFM=FB),
//             DSN=RESCUE.PROCLIB
//UADS     DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(CYL,(1,1,2)),DCB=SYS1.UADS,
//             DSN=RESCUE.UADS
//BRODCAST DD  DISP=(NEW,KEEP),VOL=SER=RESCUE,UNIT=SYSDA,
//             SPACE=(TRK,(1,1)),DCB=SYS1.BRODCAST,
//             DSN=RESCUE.BRODCAST
//*--------------------------------------------------------------------
//*  RENAME SYSTEM FILES
//*--------------------------------------------------------------------
//RENAME    EXEC PGM=IEHPROGM
//DD1       DD   DISP=SHR,UNIT=SYSDA,VOL=SER=RESCUE
//SYSPRINT  DD   SYSOUT=*
//SYSIN     DD   *
 RENAME DSNAME=RESCUE.PARMLIB,VOL=SYSDA=RESCUE,NEWNAME=SYS1.PARMLIB
 RENAME DSNAME=RESCUE.VTAMLST,VOL=SYSDA=RESCUE,NEWNAME=SYS1.VTAMLST
 RENAME DSNAME=RESCUE.HASPACE,VOL=SYSDA=RESCUE,NEWNAME=SYS1.HASPACE
 RENAME DSNAME=RESCUE.LOGREC,VOL=SYSDA=RESCUE,NEWNAME=SYS1.LOGREC
 RENAME DSNAME=RESCUE.HASPCKPT,VOL=SYSDA=RESCUE,NEWNAME=SYS1.HASPCKPT
 RENAME DSNAME=RESCUE.PROCLIB,VOL=SYSDA=RESCUE,NEWNAME=SYS1.PROCLIB
 RENAME DSNAME=RESCUE.UADS,VOL=SYSDA=RESCUE,NEWNAME=SYS1.UADS
 RENAME DSNAME=RESCUE.BRODCAST,VOL=SYSDA=RESCUE,NEWNAME=SYS1.BRODCAST
 RENAME DSNAME=RESCUE.HASPINDX,VOL=SYSDA=RESCUE,NEWNAME=ISF.HASPINDX
 RENAME DSNAME=RESCUE.ISPPROF,VOL=SYSDA=RESCUE,NEWNAME=IBMUSER.ISPPROF
/*

RESCUE03.text:
//RESCUE03 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//*  DEFINE THE MASTERCATALOG FOR THE RESCUE SYSTEM
//*--------------------------------------------------------------------
//DEFCAT   EXEC PGM=IDCAMS
//CATVOL   DD  VOL=SER=RESCUE,UNIT=SYSDA,DISP=OLD
//SYSPRINT DD  SYSOUT=*
//SYSIN    DD  *
 EXPORT CATALOG.MASTER.RESCUE DISCONNECT
  SET MAXCC = 0
 /********************************************/
 /*    DEFINE ICF MASTER CATALOG             */
 /********************************************/
  DEFINE UCAT (ICFCAT -
  NAME(CATALOG.MASTER.RESCUE) -
  FILE(CATVOL) VOL(RESCUE) CYL(1 1) SHR(3 3))
/*

RESCUE05.text:
//RESCUE05 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//*  DEFINE PAGE DATA SETS
//*--------------------------------------------------------------------
//DEFPAGE  EXEC PGM=IDCAMS
//STEPCAT  DD  DISP=SHR,DSN=CATALOG.MASTER.RESCUE
//SYSPRINT DD  SYSOUT=*
//RESCUE   DD  VOL=SER=RESCUE,UNIT=SYSDA,DISP=OLD
//SYSIN    DD  *
  DEF  PGSPC  (NAME(PAGE.RESCUE.PLPA) -
               VOL(RESCUE) FILE(RESCUE) CYL(40) UNIQUE ) -
               CAT(CATALOG.MASTER.RESCUE)
  DEF  PGSPC  (NAME(PAGE.RESCUE.COMMON) -
               VOL(RESCUE) FILE(RESCUE) CYL(40) UNIQUE ) -
               CAT(CATALOG.MASTER.RESCUE)
  DEF  PGSPC  (NAME(PAGE.RESCUE.LOCAL) -
               VOL(RESCUE) FILE(RESCUE) CYL(40) UNIQUE ) -
               CAT(CATALOG.MASTER.RESCUE)
/*

RESCUE08.text:
//RESCUE08 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//*  MODIFY PROCLIB
//*--------------------------------------------------------------------
//MODPROC   EXEC PGM=IEBUPDTE,PARM=NEW
//SYSPRINT  DD   SYSOUT=*
//SYSUT2    DD   DISP=SHR,DSN=SYS1.PROCLIB,UNIT=SYSDA,VOL=SER=RESCUE
//SYSIN     DD   DATA,DLM=$$
./  ADD NAME=BPXAS
//IEFPROC   EXEC   PGM=IEFIIC,DPRTY=12,PARM=',,&GETWORK,BPXPRJRW'
./  ADD NAME=BPXOINIT
//BPXOINIT PROC
//BPXOINIT EXEC PGM=BPXPINPR,REGION=0K,TIME=NOLIMIT
./  ADD NAME=OMVS
//OMVS PROC
//OMVS EXEC     PGM=BPXINIT,REGION=0K,TIME=NOLIMIT
./  ADD NAME=TSO
//TSO     PROC MBR=TSOKEY00
//STEP1   EXEC PGM=IKTCAS00,TIME=1440
//PARMLIB  DD  DSN=SYS1.PARMLIB(&MBR),DISP=SHR,FREE=CLOSE
//PRINTOUT DD  SYSOUT=*,FREE=CLOSE
//*
./  ADD NAME=JES2
//JES2    PROC M=JES2PARM
//IEFPROC EXEC PGM=HASJES20,TIME=1440,DPRTY=(15,15)
//HASPLIST  DD DDNAME=IEFRDER
//HASPPARM  DD DSN=SYS1.PARMLIB(&M),DISP=SHR
//PROC00    DD DSN=SYS1.PROCLIB,DISP=SHR
./  ADD NAME=VTAM
//VTAM    PROC
//VTAM    EXEC PGM=ISTINM01,REGION=2048K,
//             DPRTY=(15,15),TIME=1440,PERFORM=8
//VTAMLST   DD DSN=SYS1.VTAMLST,DISP=SHR
//VTAMLIB   DD DSN=SYS1.VTAMLIB,DISP=SHR
//SISTCLIB  DD DSN=SYS1.SISTCLIB,DISP=SHR
//SYSABEND  DD SYSOUT=*,HOLD=YES
./  ADD NAME=IKJACCNT
//IKJACCNT PROC
//IKJACCT EXEC PGM=IKJEFT01,DYNAMNBR=50,REGION=6000K,TIME=1440,
//  PARM='ISPSTART PANEL(ISR@390) NEWAPPL(ISR)'
//STEPLIB  DD  DSN=ISP.SISPLOAD,DISP=SHR
//ISPLLIB  DD  DSN=ISP.SISPLOAD,DISP=SHR
//ISPPLIB  DD  DSN=ISP.SISPPENU,DISP=SHR
//         DD  DSN=ISF.SISFPLIB,DISP=SHR
//ISPSLIB  DD  DSN=ISP.SISPSENU,DISP=SHR
//         DD  DSN=ISP.SISPSLIB,DISP=SHR
//         DD  DSN=ISF.SISFSLIB,DISP=SHR
//ISPMLIB  DD  DSN=ISP.SISPMENU,DISP=SHR
//         DD  DSN=ISF.SISFMLIB,DISP=SHR
//ISPTLIB  DD  DSN=ISP.SISPTENU,DISP=SHR
//         DD  DSN=ISF.SISFTLIB,DISP=SHR
//SYSPROC  DD  DSN=ISP.SISPCLIB,DISP=SHR
//         DD  DSN=ISF.SISFEXEC,DISP=SHR
//HASPINDX DD  DISP=SHR,DSN=ISF.HASPINDX
//ISPPROF  DD  DISP=SHR,DSN=IBMUSER.ISPPROF
//ISPTABL  DD  DDNAME=ISPPROF
//SYSPRINT DD  TERM=TS,SYSOUT=*
//SYSTERM  DD  TERM=TS,SYSOUT=*
//SYSIN    DD  TERM=TS
$$
//*--------------------------------------------------------------------
//*  COPY ONLY NEEDED MEMBERS FROM PROCLIB
//*--------------------------------------------------------------------
//COPYPROC  EXEC PGM=IEBCOPY
//SYSUT3    DD  UNIT=SYSDA,SPACE=(CYL,(1,1))
//SYSUT4    DD  UNIT=SYSDA,SPACE=(CYL,(1,1))
//PROCIN    DD  DISP=SHR,DSN=SYS1.IBM.PROCLIB
//PROCOUT   DD  DISP=SHR,DSN=SYS1.PROCLIB,UNIT=SYSDA,VOL=SER=RESCUE
//SYSPRINT  DD  SYSOUT=*
//SYSIN     DD  *
    COPY I=PROCIN,O=PROCOUT
    S    M=LLA
    S    M=DUMPSRV
    S    M=IEESYSAS
    S    M=IEEVMPCR
    S    M=INIT

RESCUE10.text:
//RESCUE10 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//* REASSEMBLE ICHRDSNT FOR RACF
//*--------------------------------------------------------------------
//ASSRACF   EXEC PGM=ASMA90,PARM='OBJECT,NODECK,ALIGN'
//SYSPRINT  DD  SYSOUT=*
//SYSUDUMP  DD  SYSOUT=*
//SYSUT1    DD  UNIT=SYSDA,SPACE=(CYL,(4,3))
//SYSUT2    DD  UNIT=SYSDA,SPACE=(CYL,(4,3))
//SYSUT3    DD  UNIT=SYSDA,SPACE=(CYL,(4,3))
//SYSLIB    DD  DSN=SYS1.MACLIB,DISP=SHR
//SYSLIN    DD  DSN=&&OBJ,DISP=(,PASS),
//      UNIT=SYSDA,SPACE=(CYL,(1,1))
//SYSIN    DD  *
ICHRDSNT CSECT
         DC    AL1(1)             INDICATES ONE RACF DATA SET
         DC    CL44'SYS1.RACF.RESCUE' PRIMARY RACF DS NAME
         DC    CL44' '            BACKUP RACF DS NAME
         DC    AL1(255)           # RESIDENT INDEX AND DATA BLOCKS
         DC    X'81'               UPDATES DUPLICATED ON BACKUP DS, X
         END
//*--------------------------------------------------------------------
//* LINK-EDIT ICHRDSNT
//*--------------------------------------------------------------------
//LINKRACF EXEC PGM=IEWL,PARM='XREF,LIST'
//SYSPRINT DD  SYSOUT=*
//SYSUT1   DD  UNIT=SYSDA,SPACE=(CYL,(1,1))
//SYSLIN   DD  DSN=*.ASSRACF.SYSLIN,DISP=(OLD,DELETE)
//         DD  *
      NAME ICHRDSNT(R)
//SYSLIB   DD  DSN=SYS1.LINKLIB,DISP=SHR,
//      UNIT=SYSDA,VOL=SER=RESCUE
//SYSLMOD  DD  DSN=SYS1.LINKLIB,DISP=SHR,
//      UNIT=SYSDA,VOL=SER=RESCUE
//*--------------------------------------------------------------------
//*  CREATE RACF DATABASE
//*--------------------------------------------------------------------
//RACFDB   EXEC PGM=IRRMIN00,PARM='NEW'
//SYSPRINT DD  SYSOUT=*
//SYSTEMP  DD  DSN=SYS1.MODGEN(IRRTEMP1),DISP=SHR
//SYSRACF  DD  DSN=SYS1.RACF.RESCUE,DISP=(NEW,KEEP,DELETE),
//             UNIT=SYSDA,VOL=SER=RESCUE,
//             SPACE=(TRK,(30),,CONTIG),
//             DCB=(RECFM=F,BLKSIZE=4096,DSORG=PSU)
//*--------------------------------------------------------------------
//* CREATE ICHRIN03 TABLE FOR RACF
//*--------------------------------------------------------------------
//ASMRACF  EXEC PGM=ASMA90,PARM='OBJECT,NODECK,ALIGN'
//SYSPRINT DD  SYSOUT=*
//SYSUDUMP DD  SYSOUT=*
//SYSUT1   DD  UNIT=SYSDA,SPACE=(CYL,(4,3))
//SYSUT2   DD  UNIT=SYSDA,SPACE=(CYL,(4,3))
//SYSUT3   DD  UNIT=SYSDA,SPACE=(CYL,(4,3))
//SYSLIB   DD  DSN=SYS1.MACLIB,DISP=SHR
//SYSLIN   DD  DSN=&&OBJ,DISP=(,PASS),
//      UNIT=SYSDA,SPACE=(CYL,(1,1))
//SYSIN    DD  *
ICHRIN03 CSECT
         DC    X'8001'
         DC    CL8'*'
         DC    CL8'='
         DC    CL8'SYS1'
         DC    14X'00'
         END
//*--------------------------------------------------------------------
//* LINK ICHRIN03 TABLE FOR RACF
//*--------------------------------------------------------------------
//LINKRACF EXEC PGM=IEWL,PARM='XREF,LIST'
//SYSPRINT DD  SYSOUT=*
//SYSUT1   DD  UNIT=SYSDA,SPACE=(CYL,(1,1))
//SYSLIN   DD  DSN=*.ASMRACF.SYSLIN,DISP=(OLD,DELETE)
//         DD *
      NAME ICHRIN03(R)
//SYSLIB  DD DSN=SYS1.LPALIB,DISP=SHR,
//      UNIT=SYSDA,VOL=SER=RESCUE
//SYSLMOD DD DSN=SYS1.LPALIB,DISP=SHR,
//      UNIT=SYSDA,VOL=SER=RESCUE

RESCUE11.text:
//RESCUE11 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//*  CREATE TSO USERID IBMUSER
//*--------------------------------------------------------------------
//IBMUSER  EXEC PGM=IKJEFT01
//SYSTSIN  DD  *
 ACCOUNT
 A (IBMUSER S A IKJACCNT) SIZE(4000) JCL OPER NOMOUNT ACCT UNIT(SYSDA)
 L (IBMUSER)
 END
//SYSTSPRT DD  SYSOUT=*
//SYSUADS  DD  DISP=SHR,DSN=SYS1.UADS,UNIT=SYSDA,VOL=SER=RESCUE
//SYSLBC   DD  DISP=SHR,DSN=SYS1.BRODCAST,UNIT=SYSDA,VOL=SER=RESCUE
//*--------------------------------------------------------------------
//*  INITIALIZE LOGREC
//*--------------------------------------------------------------------
//LOGREC   EXEC PGM=IFCDIP00
//SERERDS  DD  DSN=SYS1.LOGREC,UNIT=SYSDA,DISP=SHR,VOL=SER=RESCUE
//FRAMES   DD  DDNAME=IEFRDER

RESCUE12.text:
//RESCUE12 JOB (XXXXX),'RESCUE BUILD',TIME=1440,CLASS=A,MSGCLASS=X,
//             MSGLEVEL=(1,1),NOTIFY=&SYSUID,REGION=0M
//*--------------------------------------------------------------------
//*   COPY PRODUCTION IODEF TO SYS1.IODF00
//*
//*   CHANGES:
//*     1 - CHANGE 'CURRENTIODF' TO CURRENT ACTIVE SYSX.IODFXX DATASET
//*--------------------------------------------------------------------
//DEFIODF  EXEC PGM=IDCAMS
//STEPCAT  DD DSN=CATALOG.MASTER.RESCUE,DISP=SHR
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
     DELETE SYS1.IODF00.CLUSTER CLUSTER PURGE
       /*            */
       SET MAXCC = 0
       SET LASTCC = 0
       /*            */
     DEFINE CLUSTER (NAME(SYS1.IODF00.CLUSTER) -
            LINEAR -
            CYL(20 0)       -
            VOLUME(RESCUE)) -
         DATA (NAME (SYS1.IODF00)) -
         CAT(CATALOG.MASTER.RESCUE)
/*
//*--------------------------------------------------------------------
//*   INITALIZE IODF FILE
//*--------------------------------------------------------------------
//INITIODF EXEC PGM=CBDMGHCP,PARM='INITIODF SIZE=3240,ACTLOG=NO'
//STEPCAT  DD DSN=CATALOG.MASTER.RESCUE,DISP=OLD
//HCDIODFT DD DSN=SYS1.IODF00,DISP=SHR
//HCDMLOG  DD SYSOUT=*,DCB=(RECFM=FBA,LRECL=133,BLKSIZE=6650)
/*
//*--------------------------------------------------------------------
//*   COPY IODF
//*--------------------------------------------------------------------
//COPYIODF EXEC PGM=CBDMGHCP,PARM='COPYIODF'
//STEPCAT  DD DSN=CATALOG.MASTER.RESCUE,DISP=OLD
//HCDIODFS DD DSN=CURRENTIODF,DISP=SHR
//HCDIODFT DD DSN=SYS1.IODF00,DISP=SHR
//HCDMLOG  DD SYSOUT=*,DCB=(RECFM=FBA,LRECL=133,BLKSIZE=6650)
/*
//*--------------------------------------------------------------------
//*   LISTCAT OF NEW IODF
//*--------------------------------------------------------------------
//LISTIODF EXEC PGM=IDCAMS
//STEPCAT  DD DSN=CATALOG.MASTER.RESCUE,DISP=SHR
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 LISTCAT ENT(SYS1.IODF00) ALL
/*

