GZIPFILE.text:
//GSIGZIP  JOB ,'SIEGEL',CLASS=A,MSGCLASS=X,REGION=0M,
//         MSGLEVEL=(1,1),NOTIFY=&SYSUID,TIME=1440
//*********************************************************************
//* GZIP FILE                                                         *
//*                                                                   *
//* change vvvvvv to VOLSER to dump                                   *
//* change ffffff to VOLSER of empty volume                           *
//*********************************************************************
//GZIP     EXEC PGM=GZIP,PARM='DD:INPUT  -f'
//STEPLIB  DD DSN=&SYSUID..GZIP.LOADLIB,DISP=SHR
//SYSOUT   DD SYSOUT=*
//SYSUDUMP DD SYSOUT=*
//INPUT    DD DISP=(OLD,DELETE,KEEP),DSN=&SYSUID..vvvvvv.XMI
//OUTPUT   DD DISP=(,CATLG,DELETE),DSN=&SYSUID..vvvvvv.XMI.GZ,
//            VOL=SER=ffffff,
//            UNIT=3390,
//            LIKE=&SYSUID..vvvvvv.XMI,SPACE=(TRK,(6000,6000),RLSE)

INITPACK.text:
//IBMUSERI JOB (,X588),GSF,
// NOTIFY=&SYSUID,
// CLASS=A,MSGCLASS=X,COND=(0,NE),REGION=2M
//*
//*   Create a new pack from Hercules, using the dasdinit program.
//*   Pack should be OFFLINE when this is run.
//*   Do an attach command from the Hercules console, and the pack
//*     will automatically come up offline.  Then this job can be
//*     run.  Then vary the pack online on MVS, and do the DFDSSRS
//*     job.
//*
//ICKDSF  EXEC PGM=ICKDSF
//SYSPRINT DD SYSOUT=*
//SYSIN    DD *
 INIT UNITADDRESS(nnnn) VTOC(0,1,74) NOVERIFY VOLID(vvvvvv)
/*
//

XMITFILE.text:
//GSIXMIT  JOB ,'SIEGEL',CLASS=A,MSGCLASS=X,REGION=0M,
//         MSGLEVEL=(1,1),NOTIFY=&SYSUID,TIME=1440
//*********************************************************************
//* XMIT FILE                                                         *
//*                                                                   *
//* change vvvvvv to VOLSER to dump                                   *
//* change ffffff to VOLSER of empty volume                           *
//*********************************************************************
//*
//XMIT     EXEC PGM=IKJEFT01
//SYSTSPRT   DD SYSOUT=*
//SYSTSIN    DD *
 ALLOC F(X) DA(vvvvvv.XMI) UNIT(3390) SPACE(6000 6000) TRACKS RELE +
                 VOL(ffffff)
 TRANSMIT GSI.N0 DATAS(vvvvvv.AWSTAPE) +
                 OUTDATA(vvvvvv.XMI)
