//* COPYBOOK LIBRARIES
//*YSLIB   DD DUMMY,BLKSIZE=80,DISP=SHR
//SYSLIB   DD DSN=ZY06.CHGWIZ,DISP=SHR
//*
//* OBJECT
//SYSLIN   DD DSN=&&SYSLIN,
//            DISP=(NEW,PASS,DELETE),
//            UNIT=SYSDA,SPACE=(CYL,(2,1,),RLSE),
//            DCB=(RECFM=FB,LRECL=80,BLKSIZE=0)
//* COMPILE LISTING
//SYSPRINT DD SYSOUT=*
//* COBOL SOURCE MEMBER
//SYSIN    DD DSN=ZY06.CHGWIZ(JXC310JX),
//            DISP=SHR
//*
//*****************************************************************
//*   LINK
//*****************************************************************
//STEP0010 EXEC PGM=HEWL,
//         COND=(4,LT),
//         PARM=('LIST,XREF,AMODE(31),RMODE(ANY),MAP')
