//            UNIT=&UNITT,SPACE=&HSPC,
//            DCB=(RECFM=FB,LRECL=&HLRECL,BLKSIZE=0)
//*
//SYSIN    DD DSN=&SRTPRM(JXSR301B),DISP=SHR
