//**********************************************************************
//STEP0010 EXEC PGM=IEFBR14
//DD1      DD DSN=&PREFW..IEHLIST.DUMP&PDSSEQ..&SUFW,  IEHLIST OUTPUT
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD2      DD DSN=&PREFW..JXCU011.RPT&PDSSEQ..&SUFW,   PDS MBR REPORT
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD3      DD DSN=&PREFW..XTRCT.PDS&PDSSEQ..&SUFW,     INDEX EXTRACT
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD4      DD DSN=&PREFW..LIST.PDS&PDSSEQ..&SUFW,      INDEX LIST
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//******************************************************************
//*    LIST THE PDS DIRECTORY
//*    IF THIS STEP ABENDS, BROWSE THE OUTPUT FILE IN SYSPRINT
//*       TO FIND THE REASON.  IF THE PDS LIBRARY HAS MOVED TO
//*       ANOTHER DISK PACK, THEN CHANGE VOLSER IN BOTH DDNAME1 AND
//*       THE INPUT CARD JXSDIH**. IF THE PDS IS UNAVAILABLE,
//*       THEN RERUN AGAIN AT ANOTHER TIME.
//******************************************************************
//STEP0020 EXEC PGM=IEHLIST
//*JX* LIST THE PDS DIRECTORY
//SYSPRINT DD DSN=&PREFW..IEHLIST.DUMP&PDSSEQ..&SUFW,
//             DISP=(NEW,CATLG,CATLG),
//             DCB=(RECFM=FBA,LRECL=121,BLKSIZE=15367),
//             UNIT=SYSDA,SPACE=(TRK,(500,250),RLSE)
//SYSOUT   DD  SYSOUT=&SOUT
//DDNAME1  DD  UNIT=3390,VOL=SER=&PDSVOL,DISP=SHR
//SYSIN    DD  DSN=&CARDLIB(JXSDIH&PDSSEQ),DISP=SHR
//*
//**********************************************************************
//*     CONVERT IEHLIST TO READABLE FORMAT
//*     IF STEP ABENDS, RESTART FROM STEP1005
//**********************************************************************
//STEP0030 EXEC PGM=&PGMU11,PARM='&DBGU11'
//*JX*  CONVERT IEHLIST TO READABLE FORMAT
//*STEPLIB  DD DSN=SYS2.DUMMY.LOADLIB,DISP=SHR
//         INCLUDE MEMBER=JXIS&ENV
//SYSUDUMP DD SYSOUT=&SDUMP
//ABNLTERM DD SYSOUT=&SDUMP
//SYSPRINT DD SYSOUT=&SOUT
//SYSOUT   DD SYSOUT=&SOUT
//*
//RDBUG    DD SYSOUT=&RPT
//*
//PDSDIRI  DD DSN=&PREFW..IEHLIST.DUMP&PDSSEQ..&SUFW,
//            DISP=SHR
//*
//PDSDIRO  DD DSN=&PREFW..JXCU011.RPT&PDSSEQ..&SUFW,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,(500,250),RLSE),
//            DCB=(RECFM=FBA,LRECL=133,BLKSIZE=0)
//*
//**********************************************************************
//*     CREATE DAILY EXTRACT OF PDS MEMBERS WITH ISPF STATS
//*     IF STEP ABENDS, RESTART FROM STEP2000
//**********************************************************************
//STEP0040 EXEC PGM=&PGM300P,PARM='&DBG300P'
//*JX*  CREATE DAILY EXTRACT OF JCL MEMBERS WITH ISPF STATS
//*STEPLIB  DD DSN=SYS2.DUMMY.LOADLIB,DISP=SHR
//         INCLUDE MEMBER=JXIS&ENV
//SYSUDUMP DD SYSOUT=&SDUMP
//ABNLTERM DD SYSOUT=&SDUMP
//SYSPRINT DD SYSOUT=&SOUT
//SYSOUT   DD SYSOUT=&SOUT
//*
//RDBUG    DD SYSOUT=&RPT
//*
//LIBNAME  DD DSN=&CARDLIB(JXSDLB&PDSSEQ),DISP=SHR
//PDSDIR   DD DSN=&PREFW..JXCU011.RPT&PDSSEQ..&SUFW,
//            DISP=SHR
//*
//SRCXTRT  DD DSN=&PREFW..XTRCT.PDS&PDSSEQ..&SUFW,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(500,250),RLSE),
//            DCB=(RECFM=FB,LRECL=&HSRECL,BLKSIZE=0)
//SRCLIST  DD DSN=&PREFW..LIST.PDS&PDSSEQ..&SUFW,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(500,250),RLSE),
//            DCB=(RECFM=FB,LRECL=20,BLKSIZE=0)
//**********************************************************************
//*   END OF PROC JXPR305A
//**********************************************************************

JXPR305D.text:
//**********************************************************************
//*   CHANGEWIZ       REL 1.0 VER 01
//*   (C) 2005 RENAISSANCE DATA SYSTEMS INC.
//*
//*  PROC JXPR305D - DELETE WORK FILES CREATED BY PROC JXPR305A
//*
//*   THIS PROC IS USED BY JOB JXD305 IN PROC JXPR305
//*   THIS PROC IS NOT RESTARTABLE
//*   RESTART IN STEP BEFORE THIS PROC IS EXECUTED
//*
//*  EXECUTE PROGRAMS
//*      IEFBR14         DELETE FILES
//*
//*  YOU MUST GIVE A VALUE TO SYMBOLIC  PDSSEQ
//*  WHEN EXECUTING THIS PROC.
//*
//*  INPUT FILES
//*      &PREFW..IEHLIST.DUMP&PDSSEQ..&SUFW   IEHLIST OUTPUT
//*      &PREFW..JXCU011.RPT&PDSSEQ..&SUFW    PDS MBR REPORT
//*      &PREFW..XTRCT.PDS&PDSSEQ..&SUFW      INDEX EXTRACT
//*      &PREFW..LIST.PDS&PDSSEQ..&SUFW       INDEX LIST
//*  OUTPUT FILES
//*      NONE
//*
//**********************************************************************
//JXPR305D PROC
//*
//**********************************************************************
//*     DELETE WORK FILES
//**********************************************************************
//STEP0010 EXEC PGM=IEFBR14
//DD1      DD DSN=&PREFW..IEHLIST.DUMP&PDSSEQ..&SUFW,  IEHLIST OUTPUT
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD2      DD DSN=&PREFW..JXCU011.RPT&PDSSEQ..&SUFW,   PDS MBR REPORT
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD3      DD DSN=&PREFW..XTRCT.PDS&PDSSEQ..&SUFW,     INDEX EXTRACT
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD4      DD DSN=&PREFW..LIST.PDS&PDSSEQ..&SUFW,      INDEX LIST
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//**********************************************************************
//*   END OF PROC JXPR305D
//**********************************************************************

JXPR310F.text:
//**********************************************************************
//*  PROC JXPR310F
//*  CREATE FIRST PDS CHANGE HISTORY
//*    BEFORE MERGING WITH PDS CHANGE HISTORY FILE
//*
//*    THIS JOB WILL PUT EDIT DATE IN PROMOTE DATE FOR ALL MEMBERS
//**********************************************************************
//*
//********************************************************************
//JXPR310F PROC  ENV='PROD',
//        PREFW='JX.CHGWRF',                  PREFIX FOR WORK FILES
//        SUFW='LIBTYPE',                     SUFFIX FOR WORK FILES
//        PGMU11=JXCU011,DBGU11='N 00000 00000',
//        PGM300P=JXC300PD,DBG300P='N 00000 00000',
//        SOUT='X',                           REPORT CLASS -MESSAGES
//        RPT='X',                            REPORT CLASS -REPORTS
//        SDUMP='D',                          REPORT CLASS -ABENDS
//        HSRECL='200',                       LRECL HISTORY FILE
//        UNITT=SYSDA,                        WORK FILE UNIT TYPE
//        CARDLIB='ZY06.CHGWIZ'               CARDLIB FOR PGM PARMS
//*
//**********************************************************************
//*   REFWIZ  JXREF   REL 5.0 VER 01
//*   (C) 2005 RENAISSANCE DATA SYSTEMS INC.
//*
//*  PROC JXPR310F - CREATE FIRST PDS CHANGE HISTORY FILE
//*
//*  EXECUTE PROGRAMS
//*      IEHLIST         CREATE PDS MEMBER LIST
//*      JXCU011         CONVERT IEHLIST TO A READABLE LIST
//*      JXC300PD        CREATE EXTRACT WITH ISPF STATS
//*
//*  INPUT FILES
//*      OVERRIDE DDNAME1 IN STEP0020 FOR VOLSER OF PDS LIBRARY
//*      OVERRIDE SYSIN   IN STEP0020 WITH DSN AND VOLSER OF PDS LIB
//*      OVERRIDE LIBNAME IN STEP0060 WITH LIBRARY TYPE
//*  OUTPUT FILES
//*      &PREFW..XTRCT.CHGCTRL.&SUFW        FIRST CHANGE HISTORY FILE
//*
//**********************************************************************
//*     DELETE IEHLIST FILE
//*     IF STEP ABENDS, RESTART FROM BEGINNING OF JOB
//**********************************************************************
//STEP0010 EXEC PGM=IEFBR14
//*JX*  DELETE IEHLIST FILE
//SYSPRINT DD DSN=&PREFW..IEHLIST.&SUFW,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//******************************************************************
//*    LIST THE JCL PDS DIRECTORY
//*    IF THIS STEP ABENDS, BROWSE THE OUTPUT FILE IN SYSPRINT
//*           DSN=&PREFW..IEHLIST.&SUFW,
//*       TO FIND THE REASON.  IF THE PDS LIBRARY HAS MOVED TO
//*       ANOTHER DISK PACK, THEN CHANGE VOLSER IN BOTH DDNAME1 AND
//*       SYSIN. IF THE PDS IS UNAVAILABLE,
//*       THEN RERUN AGAIN AT ANOTHER TIME.
//******************************************************************
//STEP0020 EXEC PGM=IEHLIST
//SYSPRINT DD DSN=&PREFW..IEHLIST.&SUFW,
//            DISP=(NEW,CATLG,CATLG),
//            DCB=(RECFM=FBA,LRECL=121,BLKSIZE=15367),
//            UNIT=SYSDA,SPACE=(TRK,(50,25),RLSE)
//SYSOUT   DD SYSOUT=*
//DDNAME1  DD UNIT=3390,VOL=SER=000000,DISP=SHR
//SYSIN    DD DSN=&CARDLIB(JXSDIH01),DISP=SHR
//**********************************************************************
//*     DELETE ISPF STATISTICS REPORT
//*     IF STEP ABENDS, RESTART FROM STEP0030
//**********************************************************************
//STEP0030 EXEC PGM=IEFBR14
//PDSDIRO  DD DSN=&PREFW..IEHLIST.RPT.&SUFW,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//**********************************************************************
//*     CONVERT IEHLIST TO READABLE FORMAT
//*     IF STEP ABENDS, RESTART FROM STEP0030
//**********************************************************************
//STEP0040 EXEC PGM=&PGMU11,PARM='&DBGU11'
//*JX*  CONVERT IEHLIST TO READABLE FORMAT
//*STEPLIB  DD DSN=SYS2.DUMMY.LOADLIB,DISP=SHR
//         INCLUDE MEMBER=JXIS&ENV
//SYSUDUMP DD SYSOUT=&SDUMP
//ABNLTERM DD SYSOUT=&SDUMP
//SYSPRINT DD SYSOUT=&SOUT
//SYSOUT   DD SYSOUT=&SOUT
//*
//RDBUG    DD SYSOUT=&RPT
//*
//PDSDIRI  DD DSN=&PREFW..IEHLIST.&SUFW,
//            DISP=SHR
//*
//PDSDIRO  DD DSN=&PREFW..IEHLIST.RPT.&SUFW,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,(250,25),RLSE),
//            DCB=(RECFM=FBA,LRECL=133,BLKSIZE=0)
//**********************************************************************
//*     DELETE JCLLIB EXTRACT
//*     IF RESTARTING, START AT STEP0050
//**********************************************************************
//STEP0050 EXEC PGM=IEFBR14
//*JX*  DELETE JCLLIB EXTRACT
//SRCXTRT  DD DSN=&PREFW..XTRCT.CHGCTRL.&SUFW,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//SRCLIST  DD DSN=&PREFW..PDSSTAT.LIST.&SUFW,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//**********************************************************************
//*     CREATE DAILY EXTRACT OF JCL MEMBERS WITH ISPF STATS
//*     IF RESTARTING, START AT STEP0050
//**********************************************************************
//STEP0060 EXEC PGM=&PGM300P,PARM='&DBG300P'
//*JX*  CREATE DAILY EXTRACT OF JCL MEMBERS WITH ISPF STATS
//*STEPLIB  DD DSN=SYS2.DUMMY.LOADLIB,DISP=SHR
//         INCLUDE MEMBER=JXIS&ENV
//SYSUDUMP DD SYSOUT=&SDUMP
//ABNLTERM DD SYSOUT=&SDUMP
//SYSPRINT DD SYSOUT=&SOUT
//SYSOUT   DD SYSOUT=&SOUT
//*
//RDBUG    DD SYSOUT=&RPT
//*
//LIBNAME  DD DSN=&CARDLIB(JXSDLB01),DISP=SHR
//PDSDIR   DD DSN=&PREFW..IEHLIST.RPT.&SUFW,
//            DISP=SHR
//*
//SRCXTRT  DD DSN=&PREFW..XTRCT.CHGCTRL.&SUFW,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(150,150),RLSE),
//            DCB=(RECFM=FB,LRECL=&HSRECL,BLKSIZE=0)
//SRCLIST  DD DSN=&PREFW..PDSSTAT.LIST.&SUFW,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(150,150),RLSE),
//            DCB=(RECFM=FB,LRECL=20,BLKSIZE=0)
//*
//**********************************************************************
//*     DELETE WORK FILES
//*     IF RESTARTING, START AT STEP0070
//**********************************************************************
//STEP0070 EXEC PGM=IEFBR14
//*JX*  DELETE WORK FILES
//*
//DD1      DD DSN=&PREFW..IEHLIST.&SUFW,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD2      DD DSN=&PREFW..IEHLIST.RPT.&SUFW,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//DD3      DD DSN=&PREFW..PDSSTAT.LIST.&SUFW,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//**********************************************************************
//*   END OF PROC JXPR310F
//**********************************************************************

JXPR505.text:
//**********************************************************************
//*  PROC JXPR505
//*
//*  CREATE INVENTORY REPORT  OF DEV1 AND UAT1 SCLM MEMBERS
//*
//*   THIS PROC USES THE SCLM ACCOUNT FILE FOR DEVELOPMENT AS INPUT
//********************************************************************
//JXPR505  PROC  PREFO='JX.WRK5',
//        SUFO='R5',                          SUFFIX FOR OUTPUT FILES
//        PREFR='JX.ALL',                     SUFFIX FOR OUTPUT FILES
//        ENV='PROD',
//        SCLMFL='SCLM.ACCOUNT.FILE',         THIS HAS DEV1/UAT1 INFO
//        PGM300=JXC300SC,DBG300='N 00000 00000',
//        PGM505=JXE505,
//        SOUT='X',                           REPORT CLASS -MESSAGES
//        RPT='X',                            REPORT CLASS -REPORTS
//        SDUMP='D',                          REPORT CLASS -ABENDS
//        UNITT=SYSDA,                        WORK FILE UNIT TYPE
//        UNITS=SYSDA,                        SORTWORK UNIT TYPE
//        VOLSERR='VSAM05',                   UNIT FOR REPORTS
//        CARDLIB='SYS2.PROD.CARDLIB',        CARDLIB FOR DATA
//        SRTPRM='SYS2.PROD.CARDLIB'          CARDLIB FOR SORTS
//*
//**********************************************************************
//*  DELETE COPY OF ACCOUNT FILE
//*  RESTART IN STEP0010
//**********************************************************************
//STEP0010 EXEC PGM=IEFBR14
//FILEOT01 DD DSN=&PREFO..SC.ACCOUNT.&SUFO,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//**********************************************************************
//*     COPY SCLM ACCOUNT FILE
//*     RESTART IN STEP0010
//**********************************************************************
//STEP0020 EXEC PGM=IDCAMS
//*JX*  COPY SCLM ACCOUNT FILE
//SYSOUT   DD SYSOUT=&SOUT
//SYSPRINT DD SYSOUT=&SOUT
//SYSUDUMP DD SYSOUT=&SDUMP
//*
//FILEIN01 DD DSN=&SCLMFL,
//            DISP=SHR
//FILEOT01 DD DSN=&PREFO..SC.ACCOUNT.&SUFO,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(2500,250),RLSE),
//            DCB=(RECFM=VB,LRECL=32004)
//*
//SYSIN    DD DSN=&SRTPRM(JXSR300A),DISP=SHR
//*
//**********************************************************************
//*     DELETE SORTED COPY OF SCLM ACCOUNT FILE
//*     RESTART IN STEP0030
//**********************************************************************
//STEP0030 EXEC PGM=IEFBR14
//SORTOUT  DD DSN=&PREFO..SC.ACCOUNT.SRTD.&SUFO,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//**********************************************************************
//*     SORT COPY OF SCLM ACCOUNT FILE
//*     RESTART IN STEP0030
//**********************************************************************
//STEP0040 EXEC PGM=SORT
//*JX*  SORT COPY OF SCLM ACCOUNT FILE
//SYSOUT   DD SYSOUT=&SOUT
//SYSPRINT DD SYSOUT=&SOUT
//SYSUDUMP DD SYSOUT=&SDUMP
//*
//SORTIN   DD DSN=&PREFO..SC.ACCOUNT.&SUFO,
//            DISP=SHR
//SORTOUT  DD DSN=&PREFO..SC.ACCOUNT.SRTD.&SUFO,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(2500,250),RLSE),
//            DCB=(RECFM=VB,LRECL=32004)
//SYSIN    DD DSN=&SRTPRM(JXSR300B),DISP=SHR
//*
//SORTWK01 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK02 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK03 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK04 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK05 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK06 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK07 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK08 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK09 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK10 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//*
//**********************************************************************
//*     DELETE  EXTRACT OF ALL ELEMENTS IN SCLM FILE
//*     IF RESTARTING, START AT STEP0050
//**********************************************************************
//STEP0050 EXEC PGM=IEFBR14
//SRCXTRT  DD DSN=&PREFO..SC.XTRCT.&SUFO,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//SRCLIST  DD DSN=&PREFO..SC.LIST.&SUFO,
//            DISP=(MOD,DELETE,DELETE),
//            UNIT=SYSDA,SPACE=(TRK,1)
//**********************************************************************
//*     CREATE  EXTRACT OF ALL ELEMENTS IN SCLM FILE
//*     IF RESTARTING, START AT STEP0050
//**********************************************************************
//STEP0060 EXEC PGM=&PGM300,PARM='&DBG300'
//*JX*  CREATE EXTRACT OF ELEMENTS IN SCLM ACCOUNT FILE
//STEPLIB  DD DSN=SYS2.DUMMY.LOADLIB,DISP=SHR
//         INCLUDE MEMBER=&ENV.STEP
//SYSUDUMP DD SYSOUT=&SDUMP
//ABNLTERM DD SYSOUT=&SDUMP
//SYSPRINT DD SYSOUT=&SOUT
//SYSOUT   DD SYSOUT=&SOUT
//*
//RDBUG    DD SYSOUT=&RPT
//*
//RUNTYPE  DD DSN=&CARDLIB(JXSD300D),DISP=SHR
//SCACCT   DD DSN=&PREFO..SC.ACCOUNT.SRTD.&SUFO,
//            DISP=SHR
//*
//SRCXTRT  DD DSN=&PREFO..SC.XTRCT.&SUFO,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(CYL,(50,25),RLSE),
//            DCB=(RECFM=FB,LRECL=200,BLKSIZE=0)
//SRCLIST  DD DSN=&PREFO..SC.LIST.&SUFO,
//            DISP=(NEW,CATLG,DELETE),
//            UNIT=&UNITT,SPACE=(CYL,(50,25),RLSE),
//            DCB=(RECFM=FB,LRECL=20,BLKSIZE=0)
//**********************************************************************
//*     SORT SCLM EXTRACT FILE
//*     IF RESTARTING, START AT STEP0070
//**********************************************************************
//STEP0070 EXEC PGM=SORT
//*JX*  SORT SCLM EXTRACT FILE
//SYSOUT   DD SYSOUT=&SOUT
//SYSPRINT DD SYSOUT=&SOUT
//SYSUDUMP DD SYSOUT=&SDUMP
//*
//SORTIN   DD DSN=&PREFO..SC.XTRCT.&SUFO,
//            DISP=SHR
//SORTOUT  DD DSN=&&SCLM,
//            DISP=(NEW,PASS,DELETE),
//            UNIT=&UNITT,SPACE=(CYL,(150,50),RLSE),
//            DCB=(RECFM=FB,LRECL=200)
//SYSIN    DD DSN=&SRTPRM(JXSRSMS3),DISP=SHR
//*
//SORTWK01 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK02 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK03 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK04 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK05 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK06 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK07 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK08 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK09 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//SORTWK10 DD UNIT=&UNITS,SPACE=(CYL,(50,25))
//*
//*
//**********************************************************************
//*     SORT CARDFILE OF MANAGER NAMES
//*     IF RESTARTING, START AT STEP0070
//**********************************************************************
//STEP0080 EXEC PGM=SORT
//*JX*  SORT CARDFILE OF MANAGER NAMES
//SYSPRINT DD SYSOUT=&SOUT
//SYSUDUMP DD SYSOUT=&SDUMP
//SYSOUT   DD SYSOUT=&SOUT
//*
//SORTIN   DD DSN=&CARDLIB(JXSD505A),
//            DISP=SHR
//SORTOUT  DD DSN=&&MGR,
//            DISP=(NEW,PASS,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(2500,250),RLSE),
//            DCB=(RECFM=FB,LRECL=80)
//SYSIN    DD DSN=&SRTPRM(JXSR505A),DISP=SHR
//*
//SORTWK01 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK02 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK03 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK04 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK05 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK06 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//*
//**********************************************************************
//*     SORT FILE OF PROGRAM PREFIXES
//*     IF RESTARTING, START AT STEP0070
//**********************************************************************
//STEP0090 EXEC PGM=SORT
//*JX*  SORT FILE OF PROGRAM PREFIXES
//SYSPRINT DD SYSOUT=&SOUT
//SYSUDUMP DD SYSOUT=&SDUMP
//SYSOUT   DD SYSOUT=&SOUT
//*
//SORTIN   DD DSN=&CARDLIB(JXSD505B),
//            DISP=SHR
//SORTOUT  DD DSN=&&PGM,
//            DISP=(NEW,PASS,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(2500,250),RLSE),
//            DCB=(RECFM=FB,LRECL=80)
//SYSIN    DD DSN=&SRTPRM(JXSR505B),DISP=SHR
//*
//SORTWK01 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK02 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK03 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK04 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK05 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//SORTWK06 DD UNIT=&UNITS,SPACE=(TRK,(50,25))
//*
//**********************************************************************
//*     CREATE REPORT OF ALL MEMBERS IN DEV1 AND UAT1
//*     HISTORY FILE IS SORTED SO THAT LATEST CHANGE CODE WILL BE FIRST
//*     IF RESTARTING, START AT STEP0070
//**********************************************************************
//STEP0100 EXEC PGM=&PGM505
//*JX*  CREATE REPORT OF ALL MEMBERS IN DEV1 AND UAT1
//STEPLIB  DD DSN=SYS2.DUMMY.LOADLIB,DISP=SHR
//         INCLUDE MEMBER=&ENV.STEP
//         INCLUDE MEMBER=SFSYSTEP
//*
//FJSYABE  DD SYSOUT=&SOUT
//SYSOUT   DD SYSOUT=&SOUT
//SYSPRINT DD SYSOUT=&SOUT
//SYSLIST  DD SYSOUT=&SOUT
//SYSUDUMP DD SYSOUT=&SDUMP
//SYSABOUT DD SYSOUT=&SDUMP
//TEMPWK1  DD UNIT=SYSDA,SPACE=(CYL,(50,25))
//TEMPWK2  DD UNIT=SYSDA,SPACE=(CYL,(50,25))
//SORTWK01 DD UNIT=SYSDA,SPACE=(CYL,(50,25))
//SORTWK02 DD UNIT=SYSDA,SPACE=(CYL,(50,25))
//*
//RPT01    DD DSN=&&RPT1,
//            DISP=(NEW,PASS,DELETE),
//            UNIT=&UNITT,SPACE=(TRK,(150,50),RLSE),
//            DCB=(RECFM=FBA,LRECL=133)
//*
//XRMSTR   DD DSN=&&SCLM,
//            DISP=(OLD,DELETE,DELETE)
//MGRTBL   DD DSN=&&MGR,
//            DISP=(OLD,DELETE,DELETE)
//PGMTBL   DD DSN=&&PGM,
//            DISP=(OLD,DELETE,DELETE)
//*
//**********************************************************************
//*     DELETE REPORT FILE
//*     IF RESTARTING, START AT STEP0070
//**********************************************************************
