ISPDTLC.text:
//ISPDTLC  JOB SYS,'BATCH ISPDTLC',NOTIFY=&SYSUID,
//         CLASS=I,MSGCLASS=6,REGION=256M
//*================  &&SYSUID..CBT.FILE960(ISPDTLC)   ================*
//*        INVOKE ISPF DTL COMPILER IN BATCH                          *
//*===================================================================*
//CONVERT  EXEC PGM=IKJEFT01,DYNAMNBR=50
//*
//*        ISPF LIBRARIES
//ISPMLIB  DD DISP=SHR,DSN=ISP.SISPMENU           ISPF MSGS
//ISPSLIB  DD DISP=SHR,DSN=ISP.SISPSENU           ISPF SKELS
//ISPPLIB  DD DISP=SHR,DSN=ISP.SISPPENU           ISPF PANELS
//ISPTLIB  DD DISP=SHR,DSN=ISP.SISPTENU           ISPF TABLES
//ISPTABL  DD UNIT=VIO,SPACE=(CYL,(1,1,5)),DCB=(LRECL=80,RECFM=FB)
//ISPPROF  DD UNIT=VIO,SPACE=(CYL,(1,1,5)),DCB=(LRECL=80,RECFM=FB)
//ISPLOG   DD SYSOUT=*,DCB=(LRECL=121,RECFM=FBA)
//ISPLIST  DD SYSOUT=*,DCB=(LRECL=121,RECFM=FBA)
//ISPCTL1  DD UNIT=VIO,SPACE=(CYL,(1,1)),DCB=(LRECL=80,RECFM=FB)
//*
//*   ISPDTLC INPUT (DTLGML) FILES CONCATINATION
//*         & OUTPUT (DTLMSG, DTLPAN, DTLTBL) FILES
//*     NOTE: THE FIRST (NOT QUOTED) FILES ARE PERSONAL FILES
//DTLPDDN  DD DATA,DLM=@@                         ISPDTLC PROFILE
DTLGML ISPF.ISPGLIB 'ISP.SISPGENU' 'ISP.SISPGMLI'
DTLMSG ISPF.ISPMLIB
DTLPAN ISPF.ISPPLIB
DTLTAB ISPF.ISPTLIB
@@
//DTLLOG   DD SYSOUT=*                            ISPDTLC LOG FILE
//*        TSO LIBRARIES
//SYSEXEC  DD DISP=SHR,DSN=ISP.SISPEXEC           ISPF REXX EXECS
//SYSPROC  DD DISP=SHR,DSN=ISP.SISPCLIB           ISPF CLISTS
//SYSTSIN  DD DATA,DLM=@@
ISPSTART CMD(%ISPDTLC USR1CMDS (DISK KEYLAPPL=USR1 CUASUPP NOCUAATTR +
 NOLISTING NOGRAPHIC REPLACE PROFDDN=DTLPDDN))
@@
//SYSTSPRT DD SYSOUT=*
//

