FTPG0001.text:
)Attr Default(%+_)
   + type(text  ) intens(low )
   _ type( input) intens(high) caps(on ) just(left )
   ~ type(text) intens(high) color(yellow)
   ! type(output) intens(low) caps(off) just(left )
)Body  width(80) expand(\\)
+ --\-\-- ~Generate FTP DSN JCL+ --\-\--
%Command ===>_ZCMD                                             %Scroll ===>_amt
%\ \ ~PF1+- HELP ~PF3+- END \ \
+  Enter one of the following under the ~"ACTION"+ column
%  B -~Build   FTP JCL         %C -~Create FTP DS Profile (do this first)
%  D -~Delete  FTP DS Profile  %E -~Edit   FTP DS Profile (select DSNs for FTP)
%  R -~Replace FTP DS Profile +(this will do a delete/create in one operation)
%ACTION PRODUCT NAME                        DSN PREFIX
)MODEL
  _a+  _pn                +Install~==>_dp                                      +
+                   Master Catalog~==>_syp                                     +
                       SMS Managed~==>_smp                                     +
+
)INIT
 .cursor = zcmd
 if (&amt = ' ')
   &amt = 'PAGE'
.help = FTPGH001
)REINIT
)PROC
VER (&A   LIST,B,C,D,E,R,MSG=FTPGM003)
if (.resp = ENTER)
 &resp = ENTER
if (.resp = END)
 &resp = END
&pfkey = .PFKEY
)END

FTPG0003.text:
)Attr Default(%+_)
   + type(text  ) intens(low )
   _ type( input) intens(high) caps(on ) just(left ) outline(box)
   ~ type(text) intens(high) color(yellow)
   ! type(output) intens(low) caps(off) just(left )
)Body  window(77,7) expand(\\)
+ --\-\-- ~Insert dataset for FTP+ --\-\--
%Command ===>_ZCMD
%\ \ ~PF1+- HELP ~PF3+- CANCEL \ \
%SOURCE DATASET NAME                           DS TYPE LRECL   RECFM BLKSIZE
%                                             CY-TR-BL PRIM    SEC   DIRBLKS
_dsn                                          +
+
)INIT
 .cursor = dsn
 if (&amt = ' ')
   &amt = 'PAGE'
.help = FTPGH003
)REINIT
)PROC
*REXX(*,,(FTPG0002))
VER (&dsn   DSNAME)
if (.resp = ENTER)
 &resp = ENTER
if (.resp = END)
 &resp = END
&pfkey = .PFKEY
)END

FTPG0006.text:
)Attr Default(%+_)
   + type(text  ) intens(low )
   _ type( input) intens(high) caps(on ) just(left )
   ~ type(text) intens(high) color(yellow)
   ! type(output) intens(low) caps(off) just(left )
)Body  window(77,7) expand(\\)
+ --\-\-- ~Generate FTP DSN JCL+ --\-\--
+Command %===>_ZCMD
+\ \ ~PF1+- HELP ~PF3+- END \ \
+ %ENTER VOLUME FOR SYS6 DATASETS ON SYP2
+ SYP2 does not have SYPPLX volume for allocations
+ Enter Volume %===>_sdvol +  (COMC volumes, SS2000, SS2001)   +
)INIT
 vget (sdvol) profile
 .cursor = zcmd
 if (&amt = ' ')
   &amt = 'PAGE'
)REINIT
)PROC
*REXX(*,,(FTPG0002))
VER (&sdvol NONBLANK)
vput (sdvol) profile
if (.resp = ENTER)
 &resp = ENTER
 vput (sdvol) profile
if (.resp = END)
 &resp = END
&pfkey = .PFKEY
)END
